import React, { useState } from 'react';
import { Eye, EyeOff, AlertCircle, LogIn, User, Phone } from 'lucide-react';
import './LoginPage.css'; // If you convert styles to CSS

const LoginPage = () => {
    const [phone, setPhone] = useState('0944233773');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const handleSubmit = async(e) => {
        e.preventDefault();
        setIsLoading(true);
        setError('');

        // Validate inputs
        if (!phone || !password) {
            setError('Please fill in all fields');
            setIsLoading(false);
            return;
        }

        if (phone.length < 10) {
            setError('Please enter a valid phone number');
            setIsLoading(false);
            return;
        }

        try {
            // This would be your actual API call
            const response = await simulateLoginRequest();

            if (!response.ok) {
                throw new Error(`Login failed: Route not found. POST ${response.url} returned ${response.status} (${response.statusText})`);
            }

            // Handle successful login
            console.log('Login successful');

        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    // Function to simulate the API request that's failing
    const simulateLoginRequest = () => {
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                // Simulate a 404 error response
                reject({
                    message: 'Request failed with status code 404',
                    name: 'AxiosError',
                    code: 'ERR_BAD_REQUEST',
                    config: {
                        url: 'https://localhost:8080/api/admin/login',
                        method: 'post'
                    },
                    response: {
                        status: 404,
                        statusText: 'Not Found',
                        data: 'Route not found'
                    }
                });
            }, 1500);
        });
    };

    return ( <
        div className = "login-container" >
        <
        div className = "login-box" >
        <
        div className = "login-header" >
        <
        div className = "login-logo" >
        <
        User size = { 32 }
        /> <
        /div> <
        h1 className = "login-title" > Agricultural Portal < /h1> <
        p className = "login-subtitle" > Login to access your dashboard < /p> <
        /div>

        <
        form onSubmit = { handleSubmit }
        className = "login-form" >
        <
        div className = "input-group" >
        <
        label htmlFor = "phone"
        className = "input-label" >
        <
        Phone size = { 16 }
        className = "label-icon" / >
        Phone Number <
        /label> <
        input id = "phone"
        type = "tel"
        value = { phone }
        onChange = {
            (e) => setPhone(e.target.value) }
        className = "form-input"
        placeholder = "Enter your phone number" /
        >
        <
        /div>

        <
        div className = "input-group" >
        <
        label htmlFor = "password"
        className = "input-label" >
        Password <
        /label> <
        div className = "password-container" >
        <
        input id = "password"
        type = { showPassword ? 'text' : 'password' }
        value = { password }
        onChange = {
            (e) => setPassword(e.target.value) }
        className = "form-input"
        placeholder = "Enter your password" /
        >
        <
        button type = "button"
        onClick = {
            () => setShowPassword(!showPassword) }
        className = "password-toggle" >
        { showPassword ? < EyeOff size = { 18 } /> : <Eye size={18} / > } <
        /button> <
        /div> <
        /div>

        <
        button type = "submit"
        disabled = { isLoading }
        className = { `login-button ${isLoading ? 'loading' : ''}` } >
        {
            isLoading ? ( <
                div className = "spinner" > < /div>
            ) : ( <
                >
                <
                LogIn size = { 18 }
                className = "button-icon" / >
                Login <
                />
            )
        } <
        /button>

        {
            error && ( <
                div className = "error-box" >
                <
                AlertCircle size = { 18 }
                className = "error-icon" / >
                <
                span > { error } < /span> <
                /div>
            )
        } <
        /form>

        <
        div className = "login-links" >
        <
        a href = "#"
        className = "login-link" > Forgot Password ? < /a> <
        a href = "#"
        className = "login-link" > Create Account < /a> <
        /div>

        <
        div className = "debug-info" >
        <
        h3 className = "debug-title" > Debug Information < /h3> <
        p > Attempted POST request to : < span className = "debug-url" > https: //localhost:8080/api/admin/login</span></p>
        <
        p > Server responded with: 404(Not Found) < /p> <
        p > Please check that the server is running and the endpoint is correct. < /p> <
        /div> <
        /div> <
        /div>
    );
};

export default LoginPage;
