import React from 'react';
import { adminRoles } from '../constants';

const Dashboard = ({ user, requests }) => {
    // Mock data for counts based on user role
    const totalCount = requests.length;
    const approvedCount = requests.filter(r => r.status === 'Approved').length;
    const pendingCount = requests.filter(r => r.status.includes('Pending')).length;

    return ( <
        div >
        <
        h1 className = "text-4xl font-bold text-gray-900 mb-6" > Dashboard < /h1> <
        p className = "text-gray-600 mb-8" > Welcome to your dashboard, { user.role }
        Admin. < /p>

        { /* Metrics Cards */ } <
        div className = "grid grid-cols-1 md:grid-cols-4 gap-6 mb-8" >
        <
        div className = "bg-white rounded-xl shadow-lg p-6 text-center" >
        <
        h3 className = "text-xl font-semibold text-gray-500" > Total Requests < /h3> <
        p className = "text-4xl font-bold text-gray-900 mt-2" > { totalCount } < /p> <
        /div> <
        div className = "bg-white rounded-xl shadow-lg p-6 text-center" >
        <
        h3 className = "text-xl font-semibold text-gray-500" > Approved < /h3> <
        p className = "text-4xl font-bold text-green-500 mt-2" > { approvedCount } < /p> <
        /div> <
        div className = "bg-white rounded-xl shadow-lg p-6 text-center" >
        <
        h3 className = "text-xl font-semibold text-gray-500" > Pending < /h3> <
        p className = "text-4xl font-bold text-yellow-500 mt-2" > { pendingCount } < /p> <
        /div> <
        div className = "bg-white rounded-xl shadow-lg p-6 text-center" >
        <
        h3 className = "text-xl font-semibold text-gray-500" > Rejected < /h3> <
        p className = "text-4xl font-bold text-red-500 mt-2" > { totalCount - approvedCount - pendingCount } < /p> <
        /div> <
        /div>

        { /* Requests Table Snippet */ } <
        div className = "bg-white rounded-xl shadow-lg p-6" >
        <
        h3 className = "text-2xl font-bold text-gray-900 mb-4" > Pending Requests < /h3> <
        RequestsTable requests = { requests.filter(r => r.status.includes('Pending')) }
        onViewRequest = {
            () => {} }
        /> <
        /div> <
        /div>
    );
};

export default Dashboard;
