import axios from 'axios';

// Base URL of your backend
const BASE_URL = 'http://localhost:5000'; // change to your backend URL if different

export const api = {
    // Login function for all users (admins and farmers)
    login: async(phoneNumber, password) => {
        const res = await axios.post(`${BASE_URL}/login`, { phoneNumber, password });
        return res.data; // should return user info + token
    },

    // Fetch all requests (filtered by user role)
    getRequests: async(token) => {
        const res = await axios.get(`${BASE_URL}/requests`, {
            headers: { Authorization: `Bearer ${token}` }
        });
        return res.data;
    },

    // Other API calls (add as needed)
};
