// components/AdminManagement.js
import React, { useState, useEffect } from 'react';
import './AdminManagement.css';

const AdminManagement = () => {
        const [admins, setAdmins] = useState([]);
        const [showForm, setShowForm] = useState(false);
        const [formData, setFormData] = useState({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: '',
            role: 'Region',
            region_name: '',
            zone_name: '',
            woreda_name: '',
            kebele_name: ''
        });
        const [error, setError] = useState('');

        useEffect(() => {
            fetchAdmins();
        }, []);

        const fetchAdmins = async() => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch('http://localhost:5000/api/admins/admins', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                const data = await response.json();
                if (response.ok) {
                    setAdmins(data);
                }
            } catch (error) {
                console.error('Error fetching admins:', error);
            }
        };

        const handleSubmit = async(e) => {
            e.preventDefault();
            setError('');

            if (formData.password !== formData.confirmPassword) {
                setError('Passwords do not match');
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const response = await fetch('http://localhost:5000/api/admins/register', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(formData)
                });

                const data = await response.json();

                if (response.ok) {
                    setShowForm(false);
                    setFormData({
                        fullName: '',
                        phoneNumber: '',
                        password: '',
                        confirmPassword: '',
                        role: 'Region',
                        region_name: '',
                        zone_name: '',
                        woreda_name: '',
                        kebele_name: ''
                    });
                    fetchAdmins();
                } else {
                    setError(data.message);
                }
            } catch (error) {
                setError('Network error. Please try again.');
            }
        };

        return ( <
            div className = "admin-management" >
            <
            div className = "section-header" >
            <
            h2 > Admin Management < /h2> <
            button onClick = {
                () => setShowForm(true) } > +Create Admin < /button> <
            /div>

            {
                showForm && ( <
                    div className = "modal-overlay" >
                    <
                    div className = "modal" >
                    <
                    h3 > Create New Admin < /h3> <
                    form onSubmit = { handleSubmit } >
                    <
                    input type = "text"
                    placeholder = "Full Name"
                    value = { formData.fullName }
                    onChange = {
                        (e) => setFormData({...formData, fullName: e.target.value }) }
                    required /
                    >
                    <
                    input type = "tel"
                    placeholder = "Phone Number"
                    value = { formData.phoneNumber }
                    onChange = {
                        (e) => setFormData({...formData, phoneNumber: e.target.value }) }
                    required /
                    >
                    <
                    input type = "password"
                    placeholder = "Password"
                    value = { formData.password }
                    onChange = {
                        (e) => setFormData({...formData, password: e.target.value }) }
                    required /
                    >
                    <
                    input type = "password"
                    placeholder = "Confirm Password"
                    value = { formData.confirmPassword }
                    onChange = {
                        (e) => setFormData({...formData, confirmPassword: e.target.value }) }
                    required /
                    >
                    <
                    select value = { formData.role }
                    onChange = {
                        (e) => setFormData({...formData, role: e.target.value }) } >
                    <
                    option value = "Region" > Region Admin < /option> <
                    option value = "Zone" > Zone Admin < /option> <
                    option value = "Woreda" > Woreda Admin < /option> <
                    option value = "Kebele" > Kebele Admin < /option> <
                    /select>

                    <
                    input type = "text"
                    placeholder = "Region Name"
                    value = { formData.region_name }
                    onChange = {
                        (e) => setFormData({...formData, region_name: e.target.value }) }
                    required /
                    >

                    {
                        formData.role !== 'Region' && ( <
                            input type = "text"
                            placeholder = "Zone Name"
                            value = { formData.zone_name }
                            onChange = {
                                (e) => setFormData({...formData, zone_name: e.target.value }) }
                            required /
                            >
                        )
                    }

                    {
                        formData.role === 'Woreda' || formData.role === 'Kebele' ? ( <
                            input type = "text"
                            placeholder = "Woreda Name"
                            value = { formData.woreda_name }
                            onChange = {
                                (e) => setFormData({...formData, woreda_name: e.target.value }) }
                            required /
                            >
                        ) : null
                    }

                    {
                        formData.role === 'Kebele' ? ( <
                            input type = "text"
                            placeholder = "Kebele Name"
                            value = { formData.kebele_name }
                            onChange = {
                                (e) => setFormData({...formData, kebele_name: e.target.value }) }
                            required /
                            >
                        ) : null
                    }

                    {
                        error && < div className = "error" > { error } < /div>}

                        <
                        div className = "modal-actions" >
                            <
                            button type = "submit" > Create < /button> <
                            button type = "button"
                        onClick = {
                                () => setShowForm(false) } > Cancel < /button> <
                            /div> <
                            /form> <
                            /div> <
                            /div>
                    )
                }

                <
                div className = "admins-list" >
                    <
                    h3 > Existing Admins < /h3> <
                    table >
                    <
                    thead >
                    <
                    tr >
                    <
                    th > Name < /th> <
                    th > Phone < /th> <
                    th > Role < /th> <
                    th > Region < /th> <
                    th > Zone < /th> <
                    th > Woreda < /th> <
                    th > Kebele < /th> <
                    /tr> <
                    /thead> <
                    tbody > {
                        admins.filter(admin => admin.type === 'admin').map((admin) => ( <
                            tr key = { admin.id } >
                            <
                            td > { admin.full_name } < /td> <
                            td > { admin.phone_number } < /td> <
                            td > { admin.role } < /td> <
                            td > { admin.region_name } < /td> <
                            td > { admin.zone_name } < /td> <
                            td > { admin.woreda_name } < /td> <
                            td > { admin.kebele_name } < /td> <
                            /tr>
                        ))
                    } <
                    /tbody> <
                    /table> <
                    /div> <
                    /div>
            );
        };

        export default AdminManagement;
