import React from 'react';
import { pageNames, adminRoles } from '../constants';
import { Home, ClipboardList, CheckSquare, Database, BarChart } from 'lucide-react';

const navItems = [
    { name: pageNames.DASHBOARD, icon: Home },
    { name: pageNames.REQUESTS, icon: ClipboardList },
    { name: pageNames.APPROVALS, icon: CheckSquare },
    { name: pageNames.MASTER_DATA, icon: Database },
    { name: pageNames.REPORTS, icon: BarChart },
];

const Sidebar = ({ userRole, currentPage, onNavigate, isSidebarOpen, toggleSidebar, isDarkMode }) => {
        const filteredNavItems = navItems.filter(item => {
            switch (userRole) {
                case adminRoles.FEDERAL:
                    return true;
                case adminRoles.REGIONAL:
                    return true;
                case adminRoles.ZONE:
                    return true;
                case adminRoles.WOREDA:
                    return true;
                case adminRoles.KEBELE:
                    return true;
                case adminRoles.FARMER:
                    return item.name === pageNames.DASHBOARD || item.name === pageNames.REQUESTS;
                default:
                    return false;
            }
        });

        return ( <
            aside className = { `bg-white dark:bg-gray-800 shadow-lg h-screen fixed top-0 left-0 transition-all duration-300 ${isSidebarOpen ? 'w-64' : 'w-20'}` } >
            <
            div className = { `flex items-center justify-between p-6 ${!isSidebarOpen && 'justify-center'}` } > {
                isSidebarOpen && ( <
                    h1 className = "text-xl font-bold text-gray-800 dark:text-gray-100" > Admin Panel < /h1>
                )
            } <
            /div> <
            nav className = "mt-8" > {
                filteredNavItems.map(item => ( <
                        button key = { item.name }
                        onClick = {
                            () => onNavigate(item.name) }
                        className = { `flex items-center w-full text-left px-6 py-3 transition-colors duration-200 ${
              currentPage === item.name
                ? 'bg-indigo-600 text-white font-semibold'
                : 'text-gray-600 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'
            }` } >
                        <
                        item.icon className = { `h-6 w-6 ${isSidebarOpen ? 'mr-3' : 'mx-auto'}` }
                        /> {
                            isSidebarOpen && < span > { item.name } < /span>} <
                                /button>
                        ))
                } <
                /nav> <
                /aside>
            );
        };

        export default Sidebar;
