/* global __app_id, __firebase_config, __initial_auth_token */

import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, onSnapshot } from 'firebase/firestore';

const App = () => {
    // We'll use state to hold the Firebase instances and the current user's ID
    const [db, setDb] = useState(null);
    const [auth, setAuth] = useState(null);
    const [userId, setUserId] = useState(null);
    const [currentRole, setCurrentRole] = useState('Kebele Admin');

    // --- Firestore Setup ---
    useEffect(() => {
        // These variables are provided by the canvas environment
        const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
        const firebaseConfig = JSON.parse(typeof __firebase_config !== 'undefined' ? __firebase_config : '{}');
        const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

        if (Object.keys(firebaseConfig).length === 0) {
            console.error("Firebase config is missing. Cannot initialize Firebase.");
            return;
        }

        try {
            const app = initializeApp(firebaseConfig);
            const firestore = getFirestore(app);
            const firebaseAuth = getAuth(app);

            setDb(firestore);
            setAuth(firebaseAuth);

            // Listen for auth state changes
            onAuthStateChanged(firebaseAuth, async(user) => {
                if (user) {
                    // If a user is signed in, set their UID.
                    setUserId(user.uid);
                    console.log("User is signed in with UID:", user.uid);
                } else {
                    // No user is signed in, so we sign in anonymously or with a custom token.
                    console.log("No user signed in. Attempting to sign in...");
                    if (initialAuthToken) {
                        await signInWithCustomToken(firebaseAuth, initialAuthToken);
                    } else {
                        await signInAnonymously(firebaseAuth);
                    }
                }
            });
        } catch (error) {
            console.error("Error initializing Firebase:", error);
        }
    }, []);

    // --- Role-Based UI Logic (from previous code) ---
    const roles = {
        'Kebele Admin': {
            title: 'Kebele Administrator Panel',
            permissions: ['View Requests', 'Approve Requests', 'Manage Data', 'Create Farmer Accounts'],
            info: 'Viewing and managing product requests for your kebele. You are the first level of approval.',
        },
        'Woreda Admin': {
            title: 'Woreda Administrator Panel',
            permissions: ['View Requests', 'Approve Requests', 'Manage Data', 'Create Kebele Reps', 'Manage Woreda Products'],
            info: 'Handling requests from all kebeles within your woreda. You also manage products available from NGOs in your woreda.',
        },
        'Zone Admin': {
            title: 'Zone Administrator Panel',
            permissions: ['View Requests', 'Approve Requests', 'Manage Data', 'Create Woreda Admins', 'Manage Zone Products'],
            info: 'Overseeing requests from all woredas in your zone. You manage products available from NGOs in the zone.',
        },
        'Region Admin': {
            title: 'Region Administrator Panel',
            permissions: ['View Requests', 'Approve Requests', 'Manage Data', 'Create Zone Admins', 'Manage Region Products'],
            info: 'Managing all requests from the zones in your region. You also manage products for the entire region.',
        },
        'Federal Admin': {
            title: 'Federal Administrator Panel',
            permissions: ['View All Requests', 'Approve All', 'Manage All Data', 'Manage All Products', 'Create Regional Admins'],
            info: 'Complete oversight of the entire system. You can view and manage requests and products at all levels.',
        },
    };

    const handleRoleChange = (event) => {
        setCurrentRole(event.target.value);
    };

    return ( <
        div className = "min-h-screen bg-gray-100 p-8 font-sans antialiased flex items-center justify-center" >
        <
        div className = "bg-white shadow-xl rounded-2xl p-8 max-w-4xl w-full" > { /* Header and Role Selector */ } <
        div className = "flex flex-col md:flex-row items-center justify-between border-b pb-4 mb-6" >
        <
        h1 className = "text-3xl font-bold text-gray-800 mb-4 md:mb-0" >
        Product Request Management System <
        /h1> <
        div className = "flex items-center space-x-2" >
        <
        label htmlFor = "role-select"
        className = "text-gray-600 font-medium" > Select Role: < /label> <
        select id = "role-select"
        value = { currentRole }
        onChange = { handleRoleChange }
        className = "px-4 py-2 border rounded-full shadow-sm text-gray-700 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all cursor-pointer" >
        {
            Object.keys(roles).map((role) => ( <
                option key = { role }
                value = { role } > { role } < /option>
            ))
        } <
        /select> <
        /div> <
        /div>

        { /* Dynamic content based on the selected role */ } <
        div className = "p-6 bg-blue-50 rounded-xl border border-blue-200" >
        <
        h2 className = "text-2xl font-semibold text-blue-800 mb-2" > { roles[currentRole].title } < /h2> <
        p className = "text-gray-700 mb-4" > { roles[currentRole].info } < /p>

        <
        h3 className = "text-lg font-semibold text-blue-700 mb-2" > Permissions: < /h3> <
        ul className = "list-disc list-inside space-y-1 text-gray-600" > {
            roles[currentRole].permissions.map((permission, index) => ( <
                li key = { index }
                className = "flex items-center space-x-2" >
                <
                svg xmlns = "http://www.w3.org/2000/svg"
                width = "16"
                height = "16"
                viewBox = "0 0 24 24"
                fill = "none"
                stroke = "currentColor"
                strokeWidth = "2"
                strokeLinecap = "round"
                strokeLinejoin = "round"
                className = "text-green-500" > < path d = "M22 11.08V12a10 10 0 1 1-5.93-8.6" > < /path><path d="M22 4L12 14.01l-3-3"></path > < /svg> <
                span > { permission } < /span> <
                /li>
            ))
        } <
        /ul> <
        /div> <
        /div> <
        /div>
    );
};

export default App;
