import axios from 'axios';

const API_URL = 'http://localhost:5000'; // adjust to your backend URL

export const api = {
    login: async({ phone, password }) => {
        const response = await axios.post(`${API_URL}/auth/login`, { phone, password });
        return response.data; // expects { id, name, role, token } from backend
    },

    getRequests: async() => {
        const response = await axios.get(`${API_URL}/requests`);
        return response.data;
    },

    getProducts: async() => {
        const response = await axios.get(`${API_URL}/products`);
        return response.data;
    },

    // other endpoints like createRequest, approveRequest, etc.
};
