import React from 'react';
import { LayoutDashboard, Database, ClipboardList } from 'lucide-react';

/**
 * Sidebar Component
 * * This component renders the main navigation sidebar of the application.
 * It displays links to different sections (Dashboard, Master Data, Reports)
 * and highlights the currently active page.
 * * @param {object} props - The component's props.
 * @param {string} props.currentPage - The name of the currently active page.
 * @param {function} props.onNavigate - The function to call when a navigation link is clicked.
 */
const Sidebar = ({ currentPage, onNavigate }) => {
    // Define the navigation items with their page name, display title, and icon component
    const navItems = [
        { page: 'dashboard', title: 'Dashboard', icon: LayoutDashboard },
        { page: 'master-data', title: 'Master Data', icon: Database },
        { page: 'reports', title: 'Reports', icon: ClipboardList },
    ];

    return ( <
        aside className = "bg-gray-800 text-white w-64 min-h-screen p-6 shadow-xl transform transition-transform duration-300 ease-in-out md:translate-x-0" > { /* Sidebar Header */ } <
        div className = "text-xl font-bold mb-8" >
        Navigation <
        /div>

        { /* Navigation Links */ } <
        nav >
        <
        ul className = "space-y-2" > {
            navItems.map((item) => {
                const Icon = item.icon;
                const isActive = currentPage === item.page;
                const activeClass = isActive ? 'bg-gray-700' : 'hover:bg-gray-700';

                return ( <
                    li key = { item.page } >
                    <
                    button onClick = {
                        () => onNavigate(item.page) }
                    className = { `flex items-center w-full px-4 py-3 rounded-lg transition duration-200 ${activeClass}` } >
                    <
                    Icon className = "mr-3"
                    size = { 20 }
                    /> <
                    span > { item.title } < /span> <
                    /button> <
                    /li>
                );
            })
        } <
        /ul> <
        /nav> <
        /aside>
    );
};

export default Sidebar;
