import React, { useState } from 'react';
import api from './api';

// Dashboards
import FederalDashboard from './dashboards/FederalDashboard';
import RegionDashboard from './dashboards/RegionDashboard';
import ZoneDashboard from './dashboards/ZoneDashboard';
import WoredaDashboard from './dashboards/WoredaDashboard';
import KebeleDashboard from './dashboards/KebeleDashboard';
import FarmerDashboard from './dashboards/FarmerDashboard';

import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';

function App() {
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');
    const [currentUser, setCurrentUser] = useState(null);
    const [currentPage, setCurrentPage] = useState('login');
    const [requests] = useState([]);
    const [products] = useState([]);

    // Example function to filter requests per user role
    const getFilteredRequests = (user, allRequests) => {
        if (!user) return [];
        if (user.role === 'Farmer') return allRequests.filter(r => r.userId === user.id);
        return allRequests;
    };

    const handleLogin = async(e) => {
        e.preventDefault();
        try {
            const userType = 'admin'; // or 'farmer', you can add a toggle
            const endpoint = userType === 'admin' ? '/admin/login' : '/farmers/login';

            const response = await api.post(endpoint, {
                phoneNumber,
                password,
            });

            setCurrentUser(response.data.user);
            setCurrentPage('dashboard');
            alert('Login successful');
        } catch (err) {
            console.error(err);
            alert('Login failed: ' + (err.response ? err.response.data.message : err.message));
        }
    };

    const renderDashboard = () => {
        if (!currentUser) return null;

        switch (currentUser.role) {
            case 'Federal':
                return <FederalDashboard requests = { getFilteredRequests(currentUser, requests) }
                currentUser = { currentUser }
                />;
            case 'Region':
                return <RegionDashboard requests = { getFilteredRequests(currentUser, requests) }
                currentUser = { currentUser }
                />;
            case 'Zone':
                return <ZoneDashboard requests = { getFilteredRequests(currentUser, requests) }
                currentUser = { currentUser }
                />;
            case 'Woreda':
                return <WoredaDashboard requests = { getFilteredRequests(currentUser, requests) }
                currentUser = { currentUser }
                />;
            case 'Kebele':
                return <KebeleDashboard requests = { getFilteredRequests(currentUser, requests) }
                currentUser = { currentUser }
                />;
            case 'Farmer':
                return <FarmerDashboard requests = { getFilteredRequests(currentUser, requests) }
                currentUser = { currentUser }
                products = { products }
                />;
            default:
                return <div > No dashboard available < /div>;
        }
    };

    const LoginForm = ({ handleLogin, phoneNumber, setPhoneNumber, password, setPassword }) => ( <
        form onSubmit = { handleLogin }
        className = "bg-white p-8 rounded shadow-md w-96" >
        <
        h2 className = "text-2xl font-bold mb-4 text-center" > Login < /h2>

        <
        label className = "block mb-2 font-medium" > Phone Number < /label> <
        input type = "text"
        placeholder = "Enter phone number"
        value = { phoneNumber }
        onChange = {
            (e) => setPhoneNumber(e.target.value) }
        className = "w-full p-2 border border-gray-300 rounded mb-4"
        required /
        >

        <
        label className = "block mb-2 font-medium" > Password < /label> <
        input type = "password"
        placeholder = "Enter password"
        value = { password }
        onChange = {
            (e) => setPassword(e.target.value) }
        className = "w-full p-2 border border-gray-300 rounded mb-4"
        required /
        >

        <
        button type = "submit"
        className = "w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600 transition" >
        Login <
        /button> <
        /form>
    );

    return ( <
        Router >
        <
        div className = "min-h-screen flex items-center justify-center bg-gray-100" >
        <
        Routes >
        <
        Route path = "/login"
        element = {!currentUser ? ( <
                LoginForm handleLogin = { handleLogin }
                phoneNumber = { phoneNumber }
                setPhoneNumber = { setPhoneNumber }
                password = { password }
                setPassword = { setPassword }
                />
            ) : < Navigate to = "/dashboard" / >
        }
        /> <
        Route path = "/dashboard"
        element = {
            currentUser ? renderDashboard() : < Navigate to = "/login" / >
        }
        /> <
        Route path = "/"
        element = { < Navigate to = "/login" / > }
        /> <
        /Routes> <
        /div> <
        /Router>
    );
}

export default App;
