import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithEmailAndPassword, onAuthStateChanged, signInWithCustomToken } from 'firebase/auth';
import { getFirestore, doc, setDoc, getDoc } from 'firebase/firestore';

// Define global variables provided by the environment, with fallbacks for local development
const appId = typeof window !== 'undefined' && typeof window.__app_id !== 'undefined' ? window.__app_id : 'default-app-id';
const firebaseConfig = typeof window !== 'undefined' && typeof window.__firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
const initialAuthToken = typeof window !== 'undefined' && typeof window.__initial_auth_token !== 'undefined' ? window.__initial_auth_token : undefined;

// --- Login Component ---
const Login = ({ onLogin }) => {
        const [phoneNumber, setPhoneNumber] = useState('');
        const [password, setPassword] = useState('');
        const [error, setError] = useState('');
        const [loading, setLoading] = useState(false);

        // Handle the login form submission.
        const handleLogin = async(e) => {
            e.preventDefault();
            setLoading(true);
            setError('');

            // Simple validation for password length
            if (password.length <= 8) {
                setError('Password must be more than 8 characters long.');
                setLoading(false);
                return;
            }

            try {
                // We use signInWithEmailAndPassword but treat the phone number as the email identifier.
                // This allows us to use Firebase Auth with phone numbers without changing the user's
                // mental model for login.
                const userCredential = await signInWithEmailAndPassword(getAuth(), phoneNumber + "@example.com", password);
                onLogin(userCredential.user);
            } catch (err) {
                console.error("Login error:", err);
                setError('Invalid phone number or password. Please try again.');
            } finally {
                setLoading(false);
            }
        };

        return ( <
            div className = "flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900" >
            <
            div className = "w-full max-w-md p-8 space-y-6 bg-white dark:bg-gray-800 rounded-2xl shadow-xl border border-gray-200 dark:border-gray-700" >
            <
            h2 className = "text-3xl font-bold text-center text-gray-900 dark:text-white" > Log In < /h2> <
            p className = "text-center text-sm text-gray-500 dark:text-gray-400" >
            Enter your credentials to access the system. <
            /p> <
            form className = "space-y-6"
            onSubmit = { handleLogin } >
            <
            div >
            <
            label htmlFor = "phoneNumber"
            className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
            Phone Number <
            /label> <
            input id = "phoneNumber"
            type = "tel"
            required value = { phoneNumber }
            onChange = {
                (e) => setPhoneNumber(e.target.value) }
            className = "mt-1 w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 dark:bg-gray-700 dark:text-white"
            placeholder = "e.g., 944233773" /
            >
            <
            /div> <
            div >
            <
            label htmlFor = "password"
            className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
            Password <
            /label> <
            input id = "password"
            type = "password"
            required value = { password }
            onChange = {
                (e) => setPassword(e.target.value) }
            className = "mt-1 w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 dark:bg-gray-700 dark:text-white"
            placeholder = "Enter your password ( > 8 characters)" /
            >
            <
            /div> {
                error && < div className = "text-red-500 text-sm text-center" > { error } < /div>} <
                    div >
                    <
                    button
                type = "submit"
                disabled = { loading }
                className = "w-full flex justify-center py-3 px-4 border border-transparent rounded-lg shadow-sm text-sm font-semibold text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-150 ease-in-out disabled:opacity-50" >
                    { loading ? 'Signing In...' : 'Sign In' } <
                    /button> <
                    /div> <
                    /form> <
                    /div> <
                    /div>
            );
        };


        // --- Admin Creation Component ---
        const CreateUserForm = ({ db, auth, parentRole, locationId, parentUserId }) => {
            const [fullName, setFullName] = useState('');
            const [phoneNumber, setPhoneNumber] = useState('');
            const [password, setPassword] = useState('');
            const [assignedLocation, setAssignedLocation] = useState('');
            const [message, setMessage] = useState('');
            const [loading, setLoading] = useState(false);

            // Determine the role and location type of the user to be created
            const childRoleMap = {
                'Federal Admin': 'Regional Admin',
                'Regional Admin': 'Zone Admin',
                'Zone Admin': 'Woreda Admin',
                'Woreda Admin': 'Kebele Admin',
                'Kebele Admin': 'Farmer',
            };
            const roleToCreate = childRoleMap[parentRole];

            // Handle form submission to create the new user
            const handleCreateUser = async(e) => {
                e.preventDefault();
                setLoading(true);
                setMessage('');

                // Simple password validation check
                if (password.length <= 8) {
                    setMessage('Error: Password must be more than 8 characters long.');
                    setLoading(false);
                    return;
                }

                try {
                    // Use phone number as a unique email alias for Firebase Auth
                    const emailAlias = phoneNumber + "@example.com";
                    // This will create a new user in Firebase Auth.
                    const userCredential = await createUserWithEmailAndPassword(auth, emailAlias, password);
                    const newUserId = userCredential.user.uid;

                    // Store user's profile and role in Firestore in the admins collection.
                    const userDocRef = doc(db, `artifacts/${appId}/public/data/admins`, newUserId);
                    await setDoc(userDocRef, {
                        user_id: newUserId,
                        full_name: fullName,
                        phone_number: phoneNumber,
                        role: roleToCreate,
                        location_id: assignedLocation,
                        created_by_user_id: parentUserId
                    });

                    setMessage(`Success! A new ${roleToCreate} account for '${fullName}' has been created with ID ${newUserId} for location '${assignedLocation}'.`);
                    // Clear form fields
                    setFullName('');
                    setPhoneNumber('');
                    setPassword('');
                    setAssignedLocation('');
                } catch (error) {
                    console.error("Error creating user:", error);
                    let errorMessage = "An unknown error occurred. Please try again.";
                    if (error.code === 'auth/email-already-in-use') {
                        errorMessage = 'This phone number is already in use.';
                    } else if (error.code === 'auth/weak-password') {
                        errorMessage = 'The password is too weak. Please use a stronger password.';
                    }
                    setMessage(`Error: ${errorMessage}`);
                } finally {
                    setLoading(false);
                }
            };

            return ( <
                div className = "p-8 bg-gray-50 dark:bg-gray-800 flex-1" >
                <
                div className = "max-w-2xl mx-auto bg-white dark:bg-gray-700 p-8 rounded-2xl shadow-lg" >
                <
                h2 className = "text-3xl font-bold mb-6 text-center text-gray-800 dark:text-gray-100" >
                Create { roleToCreate } <
                /h2> <
                p className = "mb-6 text-sm text-center text-gray-600 dark:text-gray-300" >
                As a { parentRole }, you can create an account
                for the next level down. <
                /p>

                <
                form onSubmit = { handleCreateUser }
                className = "space-y-6" >
                <
                div >
                <
                label htmlFor = "fullName"
                className = "block text-sm font-medium text-gray-700 dark:text-gray-200" >
                Full Name <
                /label> <
                input type = "text"
                id = "fullName"
                value = { fullName }
                onChange = {
                    (e) => setFullName(e.target.value) }
                className = "mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 dark:bg-gray-600 dark:border-gray-500 dark:text-white p-3"
                required /
                >
                <
                /div> <
                div >
                <
                label htmlFor = "phoneNumber"
                className = "block text-sm font-medium text-gray-700 dark:text-gray-200" >
                Phone Number <
                /label> <
                input type = "tel"
                id = "phoneNumber"
                value = { phoneNumber }
                onChange = {
                    (e) => setPhoneNumber(e.target.value) }
                className = "mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 dark:bg-gray-600 dark:border-gray-500 dark:text-white p-3"
                required /
                >
                <
                /div> <
                div >
                <
                label htmlFor = "password"
                className = "block text-sm font-medium text-gray-700 dark:text-gray-200" >
                Password <
                /label> <
                input type = "password"
                id = "password"
                value = { password }
                onChange = {
                    (e) => setPassword(e.target.value) }
                className = "mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 dark:bg-gray-600 dark:border-gray-500 dark:text-white p-3"
                required /
                >
                <
                /div> <
                div >
                <
                label htmlFor = "location"
                className = "block text-sm font-medium text-gray-700 dark:text-gray-200" >
                Assign Location <
                /label> <
                input type = "text"
                id = "location"
                value = { assignedLocation }
                onChange = {
                    (e) => setAssignedLocation(e.target.value) }
                className = "mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 dark:bg-gray-600 dark:border-gray-500 dark:text-white p-3"
                placeholder = { `Enter the new user's ${roleToCreate.split(' ')[0]} location` }
                required /
                >
                <
                /div>

                <
                button type = "submit"
                disabled = { loading || !db || !auth }
                className = "w-full flex justify-center py-3 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 transition duration-150 ease-in-out" >
                { loading ? 'Creating...' : `Create ${roleToCreate}` } <
                /button> <
                /form>

                {
                    message && ( <
                        div className = { `mt-6 text-center text-sm font-medium p-4 rounded-md ${
            message.startsWith('Error') ? 'bg-red-100 text-red-700 dark:bg-red-900 dark:text-red-300' : 'bg-green-100 text-green-700 dark:bg-green-900 dark:text-green-300'
          }` } > { message } <
                        /div>
                    )
                } <
                /div> <
                /div>
            );
        };


        // --- Dashboard and Content Views ---
        const Dashboard = ({ userRole, userLocation }) => ( <
            div className = "p-8 flex-1" >
            <
            div className = "max-w-4xl mx-auto" >
            <
            h2 className = "text-3xl font-bold mb-4" > { userRole }
            Dashboard < /h2> <
            p className = "text-lg text-gray-700 dark:text-gray-300 mb-6" >
            Welcome, { userRole }.You have permissions
            for location: ** { userLocation } ** . <
            /p>

            <
            div className = "mt-8 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" >
            <
            div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg" >
            <
            h3 className = "text-xl font-semibold mb-2" > Total Requests < /h3> <
            p className = "text-4xl font-bold text-gray-900 dark:text-white" > 1, 250 < /p> <
            /div> <
            div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg" >
            <
            h3 className = "text-xl font-semibold mb-2" > Pending Approvals < /h3> <
            p className = "text-4xl font-bold text-yellow-500" > 25 < /p> <
            /div> <
            div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg" >
            <
            h3 className = "text-xl font-semibold mb-2" > Approved Requests < /h3> <
            p className = "text-4xl font-bold text-green-500" > 1, 012 < /p> <
            /div> <
            /div> <
            /div> <
            /div>
        );

        const ProductRequests = ({ userRole }) => ( <
            div className = "p-8" >
            <
            h2 className = "text-2xl font-bold mb-4" > Product Requests < /h2> <
            p > This is where { userRole }
            would view and manage product requests based on their assigned location. < /p> <
            /div>
        );

        const Approvals = ({ userRole }) => ( <
            div className = "p-8" >
            <
            h2 className = "text-2xl font-bold mb-4" > Approvals < /h2> <
            p > This is where { userRole }
            would manage pending approvals
            for their assigned hierarchy. < /p> <
            /div>
        );

        const UserManagement = ({ db, auth, userRole, userId }) => ( <
            div className = "p-8 bg-gray-50 dark:bg-gray-800 flex-1" >
            <
            div className = "max-w-4xl mx-auto" >
            <
            h2 className = "text-3xl font-bold mb-6 text-gray-800 dark:text-gray-100" > User Management < /h2> <
            CreateUserForm db = { db }
            auth = { auth }
            parentRole = { userRole }
            parentUserId = { userId }
            /> <
            /div> <
            /div>
        );


        // --- Main App Component ---
        const App = () => {
            const [db, setDb] = useState(null);
            const [auth, setAuth] = useState(null);
            const [userId, setUserId] = useState(null);
            const [userRole, setUserRole] = useState(null);
            const [userLocation, setUserLocation] = useState(null);
            const [isLoggedIn, setIsLoggedIn] = useState(false);
            const [currentView, setCurrentView] = useState('dashboard');
            const [loading, setLoading] = useState(true);

            // --- Firebase Initialization and Auth Listener ---
            useEffect(() => {
                const initFirebase = async() => {
                    try {
                        const app = initializeApp(firebaseConfig);
                        const firestoreDb = getFirestore(app);
                        const firebaseAuth = getAuth(app);
                        setDb(firestoreDb);
                        setAuth(firebaseAuth);

                        // Set up the authentication state listener
                        onAuthStateChanged(firebaseAuth, async(user) => {
                            if (user) {
                                setUserId(user.uid);
                                await fetchAndSetUserRole(firestoreDb, user.uid);
                                setIsLoggedIn(true);
                            } else {
                                // Attempt to sign in with the custom token if available
                                if (initialAuthToken) {
                                    await signInWithCustomToken(firebaseAuth, initialAuthToken);
                                } else {
                                    // No user and no token, so a login is required.
                                    setIsLoggedIn(false);
                                    setUserRole(null);
                                }
                            }
                            setLoading(false);
                        });

                    } catch (error) {
                        console.error("Firebase initialization or auth error:", error);
                        setLoading(false);
                    }
                };
                initFirebase();
            }, []);

            // Fetch and set the user's role and location from Firestore
            const fetchAndSetUserRole = async(firestoreDb, uid) => {
                try {
                    const userDocRef = doc(firestoreDb, `artifacts/${appId}/public/data/admins`, uid);
                    const docSnap = await getDoc(userDocRef);
                    if (docSnap.exists()) {
                        const userData = docSnap.data();
                        setUserRole(userData.role);
                        setUserLocation(userData.location_id);
                    } else {
                        setUserRole('Unknown');
                        setUserLocation('N/A');
                    }
                } catch (error) {
                    console.error("Error fetching user role:", error);
                }
            };

            const handleLoginSuccess = async(user) => {
                if (user) {
                    await fetchAndSetUserRole(db, user.uid);
                    setIsLoggedIn(true);
                    setCurrentView('dashboard');
                }
            };

            const renderContent = () => {
                if (loading) {
                    return <div className = "p-8 text-center text-gray-500" > Loading... < /div>;
                }

                if (!isLoggedIn) {
                    return <Login onLogin = { handleLoginSuccess }
                    />;
                }

                switch (currentView) {
                    case 'dashboard':
                        return <Dashboard userRole = { userRole }
                        userLocation = { userLocation }
                        />;
                    case 'productRequests':
                        return <ProductRequests userRole = { userRole }
                        />;
                    case 'approvals':
                        return <Approvals userRole = { userRole }
                        />;
                    case 'userManagement':
                        return <UserManagement db = { db }
                        auth = { auth }
                        userRole = { userRole }
                        userId = { userId }
                        />;
                    default:
                        return <Dashboard userRole = { userRole }
                        userLocation = { userLocation }
                        />;
                }
            };

            const renderSidebar = () => {
                if (!isLoggedIn) return null;

                const navItems = [
                    { name: 'Dashboard', view: 'dashboard' },
                    { name: 'Product Requests', view: 'productRequests' },
                    { name: 'Approvals', view: 'approvals' },
                ];

                const canManageUsers = ['Federal Admin', 'Regional Admin', 'Zone Admin', 'Woreda Admin', 'Kebele Admin'].includes(userRole);
                if (canManageUsers) {
                    navItems.push({ name: 'User Management', view: 'userManagement' });
                }

                return ( <
                    aside className = "w-64 bg-white dark:bg-gray-800 shadow-lg p-6 flex flex-col justify-between" >
                    <
                    nav className = "space-y-2" >
                    <
                    div className = "p-4 text-center" >
                    <
                    h1 className = "text-xl font-bold" > Admin Panel < /h1> <
                    p className = "text-sm text-gray-500 dark:text-gray-400" > { userRole } < /p> <
                    p className = "text-xs text-gray-400 dark:text-gray-500" > ID: { userId } < /p> <
                    /div> {
                        navItems.map(item => ( <
                            button key = { item.view }
                            onClick = {
                                () => setCurrentView(item.view) }
                            className = { `w-full text-left p-3 rounded-xl transition-all duration-200 ${currentView === item.view ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-600 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'}` } >
                            { item.name } <
                            /button>
                        ))
                    } <
                    /nav> <
                    /aside>
                );
            };

            return ( <
                div className = "min-h-screen flex flex-col bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white" > {
                    isLoggedIn ? ( <
                        div className = "flex flex-1" > { renderSidebar() } <
                        main className = "flex-1 p-8 overflow-y-auto" > { renderContent() } <
                        /main> <
                        /div>
                    ) : (
                        renderContent()
                    )
                } <
                /div>
            );
        };

        export default App;
