import React, { useState, useEffect } from 'react';
import {
    Users,
    Package,
    FileText,
    Plus,
    Search,
    Edit,
    Trash2,
    Eye,
    CheckCircle,
    XCircle,
    Clock,
    LogOut,
    UserPlus,
    PackagePlus,
    BarChart3,
    MapPin,
    Phone,
    Calendar,
    DollarSign,
    Wheat,
    Building
} from 'lucide-react';

const RegionDashboard = ({ admin, onLogout }) => {
    const [activeTab, setActiveTab] = useState('dashboard');
    const [loading, setLoading] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [filterStatus, setFilterStatus] = useState('all');

    // State for different data
    const [dashboardStats, setDashboardStats] = useState({
        zoneAdmins: 0,
        farmers: 0,
        products: 0,
        pendingRequests: 0
    });

    const [zoneAdmins, setZoneAdmins] = useState([]);
    const [farmers, setFarmers] = useState([]);
    const [products, setProducts] = useState([]);
    const [requests, setRequests] = useState([]);

    // Modal states
    const [showAddZoneAdmin, setShowAddZoneAdmin] = useState(false);
    const [showAddProduct, setShowAddProduct] = useState(false);
    const [showEditProduct, setShowEditProduct] = useState(false);
    const [showRequestDetails, setShowRequestDetails] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [selectedRequest, setSelectedRequest] = useState(null);

    // Form states
    const [zoneAdminForm, setZoneAdminForm] = useState({
        full_name: '',
        phone_number: '',
        password: '',
        zone_name: ''
    });

    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        sub_category: '',
        price: '',
        amount: '',
        unit: '',
        description: '',
        manufacturer: '',
        expiry_date: ''
    });

    // Fetch dashboard data
    const fetchDashboardData = async() => {
        setLoading(true);
        try {
            // Fetch dashboard statistics
            const statsResponse = await fetch(`/api/region/dashboard-stats?region=${admin.region_name}`, {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            });
            if (statsResponse.ok) {
                const stats = await statsResponse.json();
                setDashboardStats(stats);
            }

            // Fetch zone admins
            const zoneAdminsResponse = await fetch(`/api/region/zone-admins?region=${admin.region_name}`, {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            });
            if (zoneAdminsResponse.ok) {
                const zoneAdminsData = await zoneAdminsResponse.json();
                setZoneAdmins(zoneAdminsData);
            }

            // Fetch farmers
            const farmersResponse = await fetch(`/api/region/farmers?region=${admin.region_name}`, {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            });
            if (farmersResponse.ok) {
                const farmersData = await farmersResponse.json();
                setFarmers(farmersData);
            }

            // Fetch products
            const productsResponse = await fetch('/api/products', {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            });
            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
            }

            // Fetch requests
            const requestsResponse = await fetch(`/api/region/requests?region=${admin.region_name}`, {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            });
            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                setRequests(requestsData);
            }
        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchDashboardData();
    }, [admin.region_name]);

    // Handle zone admin creation
    const handleCreateZoneAdmin = async(e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const response = await fetch('/api/region/create-zone-admin', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({
                    ...zoneAdminForm,
                    region_name: admin.region_name
                })
            });

            if (response.ok) {
                alert('Zone admin created successfully!');
                setShowAddZoneAdmin(false);
                setZoneAdminForm({
                    full_name: '',
                    phone_number: '',
                    password: '',
                    zone_name: ''
                });
                fetchDashboardData();
            } else {
                const error = await response.json();
                alert(`Error: ${error.message}`);
            }
        } catch (error) {
            console.error('Error creating zone admin:', error);
            alert('Error creating zone admin');
        } finally {
            setLoading(false);
        }
    };

    // Handle product creation
    const handleCreateProduct = async(e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const response = await fetch('/api/products', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify(productForm)
            });

            if (response.ok) {
                alert('Product created successfully!');
                setShowAddProduct(false);
                setProductForm({
                    name: '',
                    category: '',
                    sub_category: '',
                    price: '',
                    amount: '',
                    unit: '',
                    description: '',
                    manufacturer: '',
                    expiry_date: ''
                });
                fetchDashboardData();
            } else {
                const error = await response.json();
                alert(`Error: ${error.message}`);
            }
        } catch (error) {
            console.error('Error creating product:', error);
            alert('Error creating product');
        } finally {
            setLoading(false);
        }
    };

    // Handle product update
    const handleUpdateProduct = async(e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const response = await fetch(`/api/products/${selectedProduct.id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify(productForm)
            });

            if (response.ok) {
                alert('Product updated successfully!');
                setShowEditProduct(false);
                setSelectedProduct(null);
                fetchDashboardData();
            } else {
                const error = await response.json();
                alert(`Error: ${error.message}`);
            }
        } catch (error) {
            console.error('Error updating product:', error);
            alert('Error updating product');
        } finally {
            setLoading(false);
        }
    };

    // Handle product deletion
    const handleDeleteProduct = async(productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) return;

        setLoading(true);
        try {
            const response = await fetch(`/api/products/${productId}`, {
                method: 'DELETE',
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            });

            if (response.ok) {
                alert('Product deleted successfully!');
                fetchDashboardData();
            } else {
                const error = await response.json();
                alert(`Error: ${error.message}`);
            }
        } catch (error) {
            console.error('Error deleting product:', error);
            alert('Error deleting product');
        } finally {
            setLoading(false);
        }
    };

    // Handle request approval/rejection
    const handleRequestAction = async(requestId, action, feedback = '') => {
        setLoading(true);
        try {
            const response = await fetch(`/api/region/requests/${requestId}/${action}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({ feedback })
            });

            if (response.ok) {
                alert(`Request ${action}d successfully!`);
                setShowRequestDetails(false);
                fetchDashboardData();
            } else {
                const error = await response.json();
                alert(`Error: ${error.message}`);
            }
        } catch (error) {
            console.error(`Error ${action}ing request:`, error);
            alert(`Error ${action}ing request`);
        } finally {
            setLoading(false);
        }
    };

    // Filter functions
    const filteredZoneAdmins = zoneAdmins.filter(admin =>
        (admin.full_name && admin.full_name.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (admin.phone_number && admin.phone_number.includes(searchTerm)) ||
        (admin.zone_name && admin.zone_name.toLowerCase().includes(searchTerm.toLowerCase()))
    );

    const filteredFarmers = farmers.filter(farmer =>
        (farmer.full_name && farmer.full_name.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (farmer.phone_number && farmer.phone_number.includes(searchTerm)) ||
        (farmer.zone_name && farmer.zone_name.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (farmer.woreda_name && farmer.woreda_name.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (farmer.kebele_name && farmer.kebele_name.toLowerCase().includes(searchTerm.toLowerCase()))
    );

    const filteredProducts = products.filter(product =>
        (product.name && product.name.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (product.category && product.category.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (product.sub_category && product.sub_category.toLowerCase().includes(searchTerm.toLowerCase()))
    );

    const filteredRequests = requests.filter(request => {
        const matchesSearch = (request.farmer_name && request.farmer_name.toLowerCase().includes(searchTerm.toLowerCase())) ||
            (request.product_name && request.product_name.toLowerCase().includes(searchTerm.toLowerCase()));
        const matchesFilter = filterStatus === 'all' || request.region_status === filterStatus;
        return matchesSearch && matchesFilter;
    });

    // Render functions
    const renderDashboard = () => ( <
        div className = "space-y-6" > { /* Stats Cards */ } <
        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6" >
        <
        div className = "bg-white p-6 rounded-lg shadow-md border-l-4 border-blue-500" >
        <
        div className = "flex items-center justify-between" >
        <
        div >
        <
        p className = "text-sm font-medium text-gray-600" > Zone Admins < /p> <
        p className = "text-2xl font-bold text-gray-900" > { dashboardStats.zoneAdmins } < /p> <
        /div> <
        Users className = "h-8 w-8 text-blue-500" / >
        <
        /div> <
        /div>

        <
        div className = "bg-white p-6 rounded-lg shadow-md border-l-4 border-green-500" >
        <
        div className = "flex items-center justify-between" >
        <
        div >
        <
        p className = "text-sm font-medium text-gray-600" > Farmers < /p> <
        p className = "text-2xl font-bold text-gray-900" > { dashboardStats.farmers } < /p> <
        /div> <
        Wheat className = "h-8 w-8 text-green-500" / >
        <
        /div> <
        /div>

        <
        div className = "bg-white p-6 rounded-lg shadow-md border-l-4 border-purple-500" >
        <
        div className = "flex items-center justify-between" >
        <
        div >
        <
        p className = "text-sm font-medium text-gray-600" > Products < /p> <
        p className = "text-2xl font-bold text-gray-900" > { dashboardStats.products } < /p> <
        /div> <
        Package className = "h-8 w-8 text-purple-500" / >
        <
        /div> <
        /div>

        <
        div className = "bg-white p-6 rounded-lg shadow-md border-l-4 border-orange-500" >
        <
        div className = "flex items-center justify-between" >
        <
        div >
        <
        p className = "text-sm font-medium text-gray-600" > Pending Requests < /p> <
        p className = "text-2xl font-bold text-gray-900" > { dashboardStats.pendingRequests } < /p> <
        /div> <
        Clock className = "h-8 w-8 text-orange-500" / >
        <
        /div> <
        /div> <
        /div>

        { /* Quick Actions */ } <
        div className = "bg-white p-6 rounded-lg shadow-md" >
        <
        h3 className = "text-lg font-semibold text-gray-900 mb-4" > Quick Actions < /h3> <
        div className = "grid grid-cols-1 md:grid-cols-3 gap-4" >
        <
        button onClick = {
            () => setShowAddZoneAdmin(true) }
        className = "flex items-center justify-center p-4 border-2 border-dashed border-gray-300 rounded-lg hover:border-blue-500 hover:bg-blue-50 transition-colors" >
        <
        UserPlus className = "h-6 w-6 text-gray-400 mr-2" / >
        <
        span className = "text-gray-600" > Add Zone Admin < /span> <
        /button>

        <
        button onClick = {
            () => setShowAddProduct(true) }
        className = "flex items-center justify-center p-4 border-2 border-dashed border-gray-300 rounded-lg hover:border-green-500 hover:bg-green-50 transition-colors" >
        <
        PackagePlus className = "h-6 w-6 text-gray-400 mr-2" / >
        <
        span className = "text-gray-600" > Add Product < /span> <
        /button>

        <
        button onClick = {
            () => setActiveTab('requests') }
        className = "flex items-center justify-center p-4 border-2 border-dashed border-gray-300 rounded-lg hover:border-orange-500 hover:bg-orange-50 transition-colors" >
        <
        FileText className = "h-6 w-6 text-gray-400 mr-2" / >
        <
        span className = "text-gray-600" > Review Requests < /span> <
        /button> <
        /div> <
        /div>

        { /* Recent Activity */ } <
        div className = "bg-white p-6 rounded-lg shadow-md" >
        <
        h3 className = "text-lg font-semibold text-gray-900 mb-4" > Recent Activity < /h3> <
        div className = "space-y-3" > {
            requests.slice(0, 5).map((request, index) => ( <
                div key = { index }
                className = "flex items-center justify-between p-3 bg-gray-50 rounded-lg" >
                <
                div className = "flex items-center" >
                <
                FileText className = "h-5 w-5 text-gray-400 mr-3" / >
                <
                div >
                <
                p className = "text-sm font-medium text-gray-900" > { request.farmer_name }
                requested { request.product_name } <
                /p> <
                p className = "text-xs text-gray-500" > { request.created_at && new Date(request.created_at).toLocaleDateString() } <
                /p> <
                /div> <
                /div> <
                span className = { `px-2 py-1 text-xs font-medium rounded-full ${
                                request.region_status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                                request.region_status === 'Approved' ? 'bg-green-100 text-green-800' :
                                'bg-red-100 text-red-800'
                            }` } > { request.region_status } <
                /span> <
                /div>
            ))
        } <
        /div> <
        /div> <
        /div>
    );

    const renderZoneAdmins = () => ( <
        div className = "space-y-6" >
        <
        div className = "flex justify-between items-center" >
        <
        h2 className = "text-2xl font-bold text-gray-900" > Zone Admins < /h2> <
        button onClick = {
            () => setShowAddZoneAdmin(true) }
        className = "bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 flex items-center" >
        <
        Plus className = "h-4 w-4 mr-2" / >
        Add Zone Admin <
        /button> <
        /div>

        <
        div className = "bg-white p-4 rounded-lg shadow-md" >
        <
        div className = "flex items-center mb-4" >
        <
        Search className = "h-5 w-5 text-gray-400 mr-2" / >
        <
        input type = "text"
        placeholder = "Search zone admins..."
        value = { searchTerm }
        onChange = {
            (e) => setSearchTerm(e.target.value) }
        className = "flex-1 border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500" /
        >
        <
        /div>

        <
        div className = "overflow-x-auto" >
        <
        table className = "min-w-full divide-y divide-gray-200" >
        <
        thead className = "bg-gray-50" >
        <
        tr >
        <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Name < /th> <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Phone < /th> <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Zone < /th> <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Created < /th> <
        /tr> <
        /thead> <
        tbody className = "bg-white divide-y divide-gray-200" > {
            filteredZoneAdmins.map((zoneAdmin) => ( <
                tr key = { zoneAdmin.id }
                className = "hover:bg-gray-50" >
                <
                td className = "px-6 py-4 whitespace-nowrap" >
                <
                div className = "flex items-center" >
                <
                Users className = "h-5 w-5 text-gray-400 mr-3" / >
                <
                div >
                <
                div className = "text-sm font-medium text-gray-900" > { zoneAdmin.full_name } < /div> <
                /div> <
                /div> <
                /td> <
                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-900" > { zoneAdmin.phone_number } < /td> <
                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-900" > { zoneAdmin.zone_name } < /td> <
                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { zoneAdmin.created_at && new Date(zoneAdmin.created_at).toLocaleDateString() } <
                /td> <
                /tr>
            ))
        } <
        /tbody> <
        /table> <
        /div> <
        /div> <
        /div>
    );

    const renderFarmers = () => ( <
        div className = "space-y-6" >
        <
        div className = "flex justify-between items-center" >
        <
        h2 className = "text-2xl font-bold text-gray-900" > Farmers in { admin.region_name } < /h2> <
        /div>

        <
        div className = "bg-white p-4 rounded-lg shadow-md" >
        <
        div className = "flex items-center mb-4" >
        <
        Search className = "h-5 w-5 text-gray-400 mr-2" / >
        <
        input type = "text"
        placeholder = "Search farmers..."
        value = { searchTerm }
        onChange = {
            (e) => setSearchTerm(e.target.value) }
        className = "flex-1 border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500" /
        >
        <
        /div>

        <
        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" > {
            filteredFarmers.map((farmer) => ( <
                div key = { farmer.id }
                className = "bg-gray-50 p-4 rounded-lg border" >
                <
                div className = "flex items-center mb-3" >
                <
                Wheat className = "h-6 w-6 text-green-500 mr-2" / >
                <
                h3 className = "text-lg font-semibold text-gray-900" > { farmer.full_name } < /h3> <
                /div>

                <
                div className = "space-y-2 text-sm text-gray-600" >
                <
                div className = "flex items-center" >
                <
                Phone className = "h-4 w-4 mr-2" / >
                <
                span > { farmer.phone_number } < /span> <
                /div>

                <
                div className = "flex items-center" >
                <
                MapPin className = "h-4 w-4 mr-2" / >
                <
                span > { farmer.kebele_name }, { farmer.woreda_name }, { farmer.zone_name } < /span> <
                /div>

                {
                    farmer.land_size_hectares && ( <
                        div className = "flex items-center" >
                        <
                        BarChart3 className = "h-4 w-4 mr-2" / >
                        <
                        span > { farmer.land_size_hectares }
                        hectares < /span> <
                        /div>
                    )
                }

                {
                    farmer.primary_crops && ( <
                        div className = "flex items-center" >
                        <
                        Wheat className = "h-4 w-4 mr-2" / >
                        <
                        span > { farmer.primary_crops } < /span> <
                        /div>
                    )
                }

                <
                div className = "flex items-center" >
                <
                Calendar className = "h-4 w-4 mr-2" / >
                <
                span > Joined { farmer.created_at && new Date(farmer.created_at).toLocaleDateString() } < /span> <
                /div> <
                /div> <
                /div>
            ))
        } <
        /div> <
        /div> <
        /div>
    );

    const renderProducts = () => ( <
        div className = "space-y-6" >
        <
        div className = "flex justify-between items-center" >
        <
        h2 className = "text-2xl font-bold text-gray-900" > Products < /h2> <
        button onClick = {
            () => setShowAddProduct(true) }
        className = "bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 flex items-center" >
        <
        Plus className = "h-4 w-4 mr-2" / >
        Add Product <
        /button> <
        /div>

        <
        div className = "bg-white p-4 rounded-lg shadow-md" >
        <
        div className = "flex items-center mb-4" >
        <
        Search className = "h-5 w-5 text-gray-400 mr-2" / >
        <
        input type = "text"
        placeholder = "Search products..."
        value = { searchTerm }
        onChange = {
            (e) => setSearchTerm(e.target.value) }
        className = "flex-1 border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500" /
        >
        <
        /div>

        <
        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" > {
            filteredProducts.map((product) => ( <
                div key = { product.id }
                className = "bg-gray-50 p-4 rounded-lg border" >
                <
                div className = "flex items-center justify-between mb-3" >
                <
                div className = "flex items-center" >
                <
                Package className = "h-6 w-6 text-purple-500 mr-2" / >
                <
                h3 className = "text-lg font-semibold text-gray-900" > { product.name } < /h3> <
                /div> <
                div className = "flex space-x-2" >
                <
                button onClick = {
                    () => {
                        setSelectedProduct(product);
                        setProductForm({
                            name: product.name || '',
                            category: product.category || '',
                            sub_category: product.sub_category || '',
                            price: product.price || '',
                            amount: product.amount || '',
                            unit: product.unit || '',
                            description: product.description || '',
                            manufacturer: product.manufacturer || '',
                            expiry_date: product.expiry_date || ''
                        });
                        setShowEditProduct(true);
                    }
                }
                className = "text-blue-600 hover:text-blue-800" >
                <
                Edit className = "h-4 w-4" / >
                <
                /button> <
                button onClick = {
                    () => handleDeleteProduct(product.id) }
                className = "text-red-600 hover:text-red-800" >
                <
                Trash2 className = "h-4 w-4" / >
                <
                /button> <
                /div> <
                /div>

                <
                div className = "space-y-2 text-sm text-gray-600" >
                <
                div className = "flex justify-between" >
                <
                span > Category: < /span> <
                span className = "font-medium" > { product.category } < /span> <
                /div>

                {
                    product.sub_category && ( <
                        div className = "flex justify-between" >
                        <
                        span > Sub - category: < /span> <
                        span className = "font-medium" > { product.sub_category } < /span> <
                        /div>
                    )
                }

                <
                div className = "flex justify-between" >
                <
                span > Price: < /span> <
                span className = "font-medium text-green-600" > { product.price && `$${product.price}` } <
                /span> <
                /div>

                <
                div className = "flex justify-between" >
                <
                span > Amount: < /span> <
                span className = "font-medium" > { product.amount } { product.unit } < /span> <
                /div>

                {
                    product.manufacturer && ( <
                        div className = "flex justify-between" >
                        <
                        span > Manufacturer: < /span> <
                        span className = "font-medium" > { product.manufacturer } < /span> <
                        /div>
                    )
                }

                {
                    product.expiry_date && ( <
                        div className = "flex justify-between" >
                        <
                        span > Expires: < /span> <
                        span className = "font-medium" > { new Date(product.expiry_date).toLocaleDateString() } < /span> <
                        /div>
                    )
                }

                {
