                div >
                <
                label className = "block text-sm font-medium text-gray-700 mb-1" > Description < /label> <
                textarea rows = "3"
                value = { productForm.description }
                onChange = {
                    (e) => setProductForm({...productForm, description: e.target.value }) }
                className = "w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500" >
                < /textarea> <
                /div> <
                div className = "flex justify-end space-x-3 pt-4" >
                <
                button type = "button"
                onClick = {
                    () => {
                        setShowEditProduct(false);
                        setSelectedProduct(null);
                    }
                }
                className = "px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50" >
                Cancel <
                /button> <
                button type = "submit"
                disabled = { loading }
                className = "px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50" >
                { loading ? 'Updating...' : 'Update Product' } <
                /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        }

        { /* Request Details Modal */ } {
            showRequestDetails && selectedRequest && ( <
                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" >
                <
                div className = "bg-white p-6 rounded-lg shadow-xl max-w-2xl w-full mx-4 max-h-screen overflow-y-auto" >
                <
                h3 className = "text-lg font-semibold text-gray-900 mb-4" > Request Details < /h3>

                <
                div className = "space-y-4" >
                <
                div className = "grid grid-cols-1 md:grid-cols-2 gap-4" >
                <
                div >
                <
                label className = "block text-sm font-medium text-gray-700" > Farmer < /label> <
                p className = "text-sm text-gray-900" > { selectedRequest.farmer_name } < /p> <
                /div> <
                div >
                <
                label className = "block text-sm font-medium text-gray-700" > Product < /label> <
                p className = "text-sm text-gray-900" > { selectedRequest.product_name } < /p> <
                /div> <
                div >
                <
                label className = "block text-sm font-medium text-gray-700" > Quantity < /label> <
                p className = "text-sm text-gray-900" > { selectedRequest.quantity } < /p> <
                /div> <
                div >
                <
                label className = "block text-sm font-medium text-gray-700" > Price < /label> <
                p className = "text-sm text-gray-900" > { selectedRequest.product_price && `$${selectedRequest.product_price}` } <
                /p> <
                /div> <
                div >
                <
                label className = "block text-sm font-medium text-gray-700" > Location < /label> <
                p className = "text-sm text-gray-900" > { selectedRequest.kebele_name }, { selectedRequest.woreda_name }, { selectedRequest.zone_name } <
                /p> <
                /div> <
                div >
                <
                label className = "block text-sm font-medium text-gray-700" > Request Date < /label> <
                p className = "text-sm text-gray-900" > { selectedRequest.created_at && new Date(selectedRequest.created_at).toLocaleDateString() } <
                /p> <
                /div> <
                /div>

                {
                    selectedRequest.note && ( <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700" > Note < /label> <
                        p className = "text-sm text-gray-900" > { selectedRequest.note } < /p> <
                        /div>
                    )
                }

                <
                div >
                <
                label className = "block text-sm font-medium text-gray-700 mb-2" > Approval Chain Status < /label> <
                div className = "space-y-2" >
                <
                div className = "flex items-center justify-between p-2 bg-gray-50 rounded" >
                <
                span className = "text-sm font-medium" > Kebele < /span> <
                span className = { `px-2 py-1 text-xs font-medium rounded ${
                                            selectedRequest.kebele_status === 'Approved' ? 'bg-green-100 text-green-800' :
                                            selectedRequest.kebele_status === 'Rejected' ? 'bg-red-100 text-red-800' :
                                            'bg-yellow-100 text-yellow-800'
                                        }` } > { selectedRequest.kebele_status } <
                /span> <
                /div> <
                div className = "flex items-center justify-between p-2 bg-gray-50 rounded" >
                <
                span className = "text-sm font-medium" > Woreda < /span> <
                span className = { `px-2 py-1 text-xs font-medium rounded ${
                                            selectedRequest.woreda_status === 'Approved' ? 'bg-green-100 text-green-800' :
                                            selectedRequest.woreda_status === 'Rejected' ? 'bg-red-100 text-red-800' :
                                            'bg-yellow-100 text-yellow-800'
                                        }` } > { selectedRequest.woreda_status } <
                /span> <
                /div> <
                div className = "flex items-center justify-between p-2 bg-gray-50 rounded" >
                <
                span className = "text-sm font-medium" > Zone < /span> <
                span className = { `px-2 py-1 text-xs font-medium rounded ${
                                            selectedRequest.zone_status === 'Approved' ? 'bg-green-100 text-green-800' :
                                            selectedRequest.zone_status === 'Rejected' ? 'bg-red-100 text-red-800' :
                                            'bg-yellow-100 text-yellow-800'
                                        }` } > { selectedRequest.zone_status } <
                /span> <
                /div> <
                div className = "flex items-center justify-between p-2 bg-blue-50 rounded border-2 border-blue-200" >
                <
                span className = "text-sm font-medium" > Region(You) < /span> <
                span className = { `px-2 py-1 text-xs font-medium rounded ${
                                            selectedRequest.region_status === 'Approved' ? 'bg-green-100 text-green-800' :
                                            selectedRequest.region_status === 'Rejected' ? 'bg-red-100 text-red-800' :
                                            'bg-yellow-100 text-yellow-800'
                                        }` } > { selectedRequest.region_status } <
                /span> <
                /div> <
                div className = "flex items-center justify-between p-2 bg-gray-50 rounded" >
                <
                span className = "text-sm font-medium" > Federal < /span> <
                span className = { `px-2 py-1 text-xs font-medium rounded ${
                                            selectedRequest.federal_status === 'Approved' ? 'bg-green-100 text-green-800' :
                                            selectedRequest.federal_status === 'Rejected' ? 'bg-red-100 text-red-800' :
                                            'bg-yellow-100 text-yellow-800'
                                        }` } > { selectedRequest.federal_status } <
                /span> <
                /div> <
                /div> <
                /div>

                { /* Feedback sections */ } {
                    selectedRequest.kebele_feedback && ( <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700" > Kebele Feedback < /label> <
                        p className = "text-sm text-gray-900" > { selectedRequest.kebele_feedback } < /p> <
                        /div>
                    )
                } {
                    selectedRequest.woreda_feedback && ( <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700" > Woreda Feedback < /label> <
                        p className = "text-sm text-gray-900" > { selectedRequest.woreda_feedback } < /p> <
                        /div>
                    )
                } {
                    selectedRequest.zone_feedback && ( <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700" > Zone Feedback < /label> <
                        p className = "text-sm text-gray-900" > { selectedRequest.zone_feedback } < /p> <
                        /div>
                    )
                } {
                    selectedRequest.region_feedback && ( <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700" > Region Feedback < /label> <
                        p className = "text-sm text-gray-900" > { selectedRequest.region_feedback } < /p> <
                        /div>
                    )
                } {
                    selectedRequest.federal_feedback && ( <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700" > Federal Feedback < /label> <
                        p className = "text-sm text-gray-900" > { selectedRequest.federal_feedback } < /p> <
                        /div>
                    )
                } <
                /div>

                <
                div className = "flex justify-end space-x-3 pt-6" >
                <
                button onClick = {
                    () => setShowRequestDetails(false) }
                className = "px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50" >
                Close <
                /button> {
                    selectedRequest.region_status === 'Pending' && ( <
                        >
                        <
                        button onClick = {
                            () => handleRequestAction(selectedRequest.id, 'approve') }
                        className = "px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 flex items-center" >
                        <
                        CheckCircle className = "h-4 w-4 mr-2" / >
                        Approve <
                        /button> <
                        button onClick = {
                            () => {
                                const feedback = prompt('Enter rejection reason:');
                                if (feedback) {
                                    handleRequestAction(selectedRequest.id, 'reject', feedback);
                                }
                            }
                        }
                        className = "px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 flex items-center" >
                        <
                        XCircle className = "h-4 w-4 mr-2" / >
                        Reject <
                        /button> <
                        />
                    )
                } <
                /div> <
                /div> <
                /div>
            )
        } <
        /div>
    );
};

export default RegionDashboard;
