import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import './WoredaDashboard.css';

const WoredaDashboard = () => {
        const [activeTab, setActiveTab] = useState('overview');
        const [user, setUser] = useState(null);
        const navigate = useNavigate();

        // Admin/Farmer Management State
        const [admins, setAdmins] = useState([]);
        const [farmers, setFarmers] = useState([]);
        const [showAdminModal, setShowAdminModal] = useState(false);
        const [editingAdmin, setEditingAdmin] = useState(null);
        const [adminForm, setAdminForm] = useState({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: '',
            role: 'Kebele',
            kebele_name: ''
        });

        // Product Management State
        const [products, setProducts] = useState([]);
        const [myProducts, setMyProducts] = useState([]);
        const [otherProducts, setOtherProducts] = useState([]);
        const [showProductModal, setShowProductModal] = useState(false);
        const [editingProduct, setEditingProduct] = useState(null);
        const [productForm, setProductForm] = useState({
            name: '',
            description: '',
            category: '',
            amount: ''
        });

        // Request Management State
        const [requests, setRequests] = useState([]);
        const [filteredRequests, setFilteredRequests] = useState([]);
        const [selectedRequests, setSelectedRequests] = useState([]);
        const [showRequestModal, setShowRequestModal] = useState(false);
        const [selectedRequest, setSelectedRequest] = useState(null);
        const [requestStatus, setRequestStatus] = useState('');
        const [decisionReason, setDecisionReason] = useState('');
        const [bulkAction, setBulkAction] = useState('');
        const [statusFilter, setStatusFilter] = useState('all');
        const [kebeleFilter, setKebeleFilter] = useState('all');

        // Stats State
        const [stats, setStats] = useState({
            totalRequests: 0,
            pendingRequests: 0,
            approvedRequests: 0,
            rejectedRequests: 0,
            acceptedRequests: 0,
            totalAdmins: 0,
            totalFarmers: 0,
            totalProducts: 0
        });

        // Kebeles in this Woreda
        const [kebeles, setKebeles] = useState([]);

        useEffect(() => {
            // Check authentication
            const token = localStorage.getItem('token');
            const userData = JSON.parse(localStorage.getItem('user') || 'null');
            const userType = localStorage.getItem('userType');

            if (!token || userType !== 'Woreda' || !userData) {
                navigate('/login');
                return;
            }

            setUser(userData);
            fetchDashboardData();
        }, [navigate]);

        useEffect(() => {
            if (requests.length > 0) {
                filterRequests();
            }
        }, [statusFilter, kebeleFilter, requests]);

        const filterRequests = useCallback(() => {
            let filtered = [...requests];

            // Filter by status
            if (statusFilter !== 'all') {
                filtered = filtered.filter(req => req.status === statusFilter);
            }

            // Filter by kebele
            if (kebeleFilter !== 'all') {
                filtered = filtered.filter(req => req.kebele_name === kebeleFilter);
            }

            setFilteredRequests(filtered);
        }, [statusFilter, kebeleFilter, requests]);

        const fetchDashboardData = async() => {
            try {
                const token = localStorage.getItem('token');

                // Fetch admins and farmers in scope
                const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (adminsResponse.ok) {
                    const data = await adminsResponse.json();
                    const adminsData = data.filter(item => item.type === 'admin' && item.role === 'Kebele');
                    const farmersData = data.filter(item => item.type === 'farmer');

                    setAdmins(adminsData);
                    setFarmers(farmersData);
                    setStats(prev => ({
                        ...prev,
                        totalAdmins: adminsData.length,
                        totalFarmers: farmersData.length
                    }));

                    // Extract unique kebeles for filtering
                    const uniqueKebeles = [...new Set(farmersData.map(f => f.kebele_name))];
                    setKebeles(uniqueKebeles);
                }

                // Fetch all products
                const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (productsResponse.ok) {
                    const productsData = await productsResponse.json();
                    setProducts(productsData);
                    setStats(prev => ({...prev, totalProducts: productsData.length }));
                }

                // Fetch my products
                const myProductsResponse = await fetch('http://localhost:5000/api/admins/myproducts', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (myProductsResponse.ok) {
                    const myProductsData = await myProductsResponse.json();
                    setMyProducts(myProductsData);
                }

                // Fetch other admins' products
                const otherProductsResponse = await fetch('http://localhost:5000/api/admins/otherproducts', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (otherProductsResponse.ok) {
                    const otherProductsData = await otherProductsResponse.json();
                    setOtherProducts(otherProductsData);
                }

                // Fetch requests with detailed status
                const requestsResponse = await fetch('http://localhost:5000/api/admins/requests/status', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (requestsResponse.ok) {
                    const requestsData = await requestsResponse.json();
                    setRequests(requestsData);
                    filterRequests();

                    // Calculate stats from requests data
                    const totalRequests = requestsData.length;
                    const pendingRequests = requestsData.filter(req => req.status === 'Pending').length;
                    const approvedRequests = requestsData.filter(req => req.status === 'Approved').length;
                    const acceptedRequests = requestsData.filter(req => req.status === 'Accepted').length;
                    const rejectedRequests = requestsData.filter(req => req.status === 'Rejected').length;

                    setStats(prev => ({
                        ...prev,
                        totalRequests,
                        pendingRequests,
                        approvedRequests,
                        acceptedRequests,
                        rejectedRequests
                    }));
                }

            } catch (error) {
                console.error('Error fetching dashboard data:', error);
            }
        };

        const handleLogout = () => {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            localStorage.removeItem('userType');
            navigate('/login');
        };

        // Admin Management Functions
        const handleAdminSubmit = async(e) => {
            e.preventDefault();
            try {
                const token = localStorage.getItem('token');

                if (adminForm.password !== adminForm.confirmPassword) {
                    alert('Passwords do not match');
                    return;
                }

                const url = editingAdmin ?
                    `http://localhost:5000/api/admins/edit/${editingAdmin.id}` :
                    'http://localhost:5000/api/admins/register';

                const method = editingAdmin ? 'PUT' : 'POST';

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        fullName: adminForm.fullName,
                        phoneNumber: adminForm.phoneNumber,
                        password: adminForm.password,
                        role: adminForm.role,
                        region_name: user.region_name,
                        zone_name: user.zone_name,
                        woreda_name: user.woreda_name,
                        kebele_name: adminForm.kebele_name
                    })
                });

                if (response.ok) {
                    setShowAdminModal(false);
                    setEditingAdmin(null);
                    setAdminForm({
                        fullName: '',
                        phoneNumber: '',
                        password: '',
                        confirmPassword: '',
                        role: 'Kebele',
                        kebele_name: ''
                    });
                    fetchDashboardData();
                    alert(editingAdmin ? 'Admin updated successfully' : 'Admin registered successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error processing admin');
                }
            } catch (error) {
                console.error('Error processing admin:', error);
                alert('Error processing admin');
            }
        };

        const handleEditAdmin = (admin) => {
            setEditingAdmin(admin);
            setAdminForm({
                fullName: admin.full_name,
                phoneNumber: admin.phone_number,
                password: '',
                confirmPassword: '',
                role: admin.role,
                kebele_name: admin.kebele_name
            });
            setShowAdminModal(true);
        };

        const handleDeleteAdmin = async(adminId) => {
            if (window.confirm('Are you sure you want to delete this admin?')) {
                try {
                    const token = localStorage.getItem('token');
                    const response = await fetch(`http://localhost:5000/api/admins/delete/${adminId}`, {
                        method: 'DELETE',
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });

                    if (response.ok) {
                        fetchDashboardData();
                        alert('Admin deleted successfully');
                    } else {
                        const errorData = await response.json();
                        alert(errorData.message || 'Error deleting admin');
                    }
                } catch (error) {
                    console.error('Error deleting admin:', error);
                    alert('Error deleting admin');
                }
            }
        };

        // Product Management Functions
        const handleProductSubmit = async(e) => {
            e.preventDefault();
            try {
                const token = localStorage.getItem('token');

                const url = editingProduct ?
                    `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                    'http://localhost:5000/api/admins/addproduct';

                const method = editingProduct ? 'PUT' : 'POST';

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        name: productForm.name,
                        description: productForm.description,
                        category: productForm.category,
                        amount: productForm.amount
                    })
                });

                if (response.ok) {
                    setShowProductModal(false);
                    setEditingProduct(null);
                    setProductForm({ name: '', description: '', category: '', amount: '' });
                    fetchDashboardData();
                    alert(editingProduct ? 'Product updated successfully' : 'Product added successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error processing product');
                }
            } catch (error) {
                console.error('Error processing product:', error);
                alert('Error processing product');
            }
        };

        const handleEditProduct = (product) => {
            setEditingProduct(product);
            setProductForm({
                name: product.name,
                description: product.description,
                category: product.category,
                amount: product.amount
            });
            setShowProductModal(true);
        };

        const handleDeleteProduct = async(productId) => {
            if (window.confirm('Are you sure you want to delete this product?')) {
                try {
                    const token = localStorage.getItem('token');
                    const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                        method: 'DELETE',
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });

                    if (response.ok) {
                        fetchDashboardData();
                        alert('Product deleted successfully');
                    } else {
                        const errorData = await response.json();
                        alert(errorData.message || 'Error deleting product');
                    }
                } catch (error) {
                    console.error('Error deleting product:', error);
                    alert('Error deleting product');
                }
            }
        };

        // Request Management Functions
        const updateRequestStatus = async(requestId, status, reason = '') => {
            try {
                const token = localStorage.getItem('token');

                const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        status: status,
                        feedback: reason
                    })
                });

                const responseData = await response.json();

                if (response.ok) {
                    setShowRequestModal(false);
                    setSelectedRequest(null);
                    setRequestStatus('');
                    setDecisionReason('');
                    fetchDashboardData();
                    alert(`Request ${status} successfully`);
                } else {
                    alert(responseData.message || 'Error updating request');
                }
            } catch (error) {
                console.error('Error updating request:', error);
                alert('Error updating request');
            }
        };

        const handleStatusUpdate = (request, status) => {
            setSelectedRequest(request);
            setRequestStatus(status);
            setShowRequestModal(true);
        };

        const confirmStatusUpdate = () => {
            if (selectedRequest && requestStatus) {
                updateRequestStatus(selectedRequest.id, requestStatus, decisionReason);
            }
        };

        const toggleRequestSelection = (requestId) => {
            if (selectedRequests.includes(requestId)) {
                setSelectedRequests(selectedRequests.filter(id => id !== requestId));
            } else {
                setSelectedRequests([...selectedRequests, requestId]);
            }
        };

        const selectAllRequests = () => {
            if (selectedRequests.length === filteredRequests.filter(r => canWoredaAdminAct(r)).length) {
                setSelectedRequests([]);
            } else {
                setSelectedRequests(filteredRequests.filter(r => canWoredaAdminAct(r)).map(r => r.id));
            }
        };

        const handleDeleteRequest = async(requestId) => {
            if (window.confirm('Are you sure you want to delete this request?')) {
                try {
                    const token = localStorage.getItem('token');
                    const response = await fetch(`http://localhost:5000/api/admins/deleterequest/${requestId}`, {
                        method: 'DELETE',
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });

                    if (response.ok) {
                        fetchDashboardData();
                        alert('Request deleted successfully');
                    } else {
                        const errorData = await response.json();
                        alert(errorData.message || 'Error deleting request');
                    }
                } catch (error) {
                    console.error('Error deleting request:', error);
                    alert('Error deleting request');
                }
            }
        };

        const handleBulkUpdate = async() => {
            if (!bulkAction || selectedRequests.length === 0) {
                alert('Please select an action and at least one request');
                return;
            }

            try {
                const token = localStorage.getItem('token');

                for (const requestId of selectedRequests) {
                    await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                        method: 'PUT',
                        headers: {
                            'Content-Type': 'application/json',
                            'Authorization': `Bearer ${token}`
                        },
                        body: JSON.stringify({
                            status: bulkAction === 'approve' ? 'Approved' : bulkAction === 'accept' ? 'Accepted' : 'Rejected'
                        })
                    });
                }

                setBulkAction('');
                setSelectedRequests([]);
                fetchDashboardData();
                alert('Bulk update completed successfully');
            } catch (error) {
                console.error('Error performing bulk update:', error);
                alert('Error performing bulk update');
            }
        };

        const getStatusBadge = (status) => {
            const statusClasses = {
                'Pending': 'status-pending',
                'Approved': 'status-approved',
                'Accepted': 'status-accepted',
                'Rejected': 'status-rejected'
            };

            return ( <
                span className = { `status-badge ${statusClasses[status] || ''}` } > { status } < /span>
            );
        };

        const getLevelStatus = (level, request) => {
            const status = request[`${level}_status`];
            const admin = request[`${level}_admin_name`];
            const feedback = request[`${level}_feedback`];
            const date = request[`${level}_approved_at`];

            const formatLevel = level.charAt(0).toUpperCase() + level.slice(1);

            return ( <
                div className = "level-status" >
                <
                h4 > { formatLevel }
                Level < /h4> <
                p > < strong > Status: < /strong> {status || 'Pending'}</p > { admin && < p > < strong > Admin: < /strong> {admin}</p > } { feedback && < p > < strong > Feedback: < /strong> {feedback}</p > } { date && < p > < strong > Date: < /strong> {new Date(date).toLocaleString()}</p > } <
                /div>
            );
        };

        const viewRequestDetails = async(requestId) => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    const requestDetails = await response.json();
                    setSelectedRequest(requestDetails);
                    setShowRequestModal(true);
                } else {
                    alert('Error fetching request details');
                }
            } catch (error) {
                console.error('Error fetching request details:', error);
                alert('Error fetching request details');
            }
        };

        // Check if Woreda admin can act on this request
        const canWoredaAdminAct = (request) => {
            // Woreda admin can only act on requests that have been approved by Kebele admin
            return request.kebele_status === 'Approved' && request.woreda_status === 'Pending';
        };

        // Check if admin can delete this request (only rejected ones)
        const canDeleteRequest = (request) => {
            return request.status === 'Rejected' && request.woreda_status === 'Rejected';
        };

        if (!user) {
            return <div className = "loading" > Loading... < /div>;
        }

        return ( <
            div className = "woreda-dashboard" > { /* Sidebar */ } <
            div className = "sidebar" >
            <
            h2 > Woreda Dashboard < /h2> <
            nav >
            <
            button className = { activeTab === 'overview' ? 'active' : '' }
            onClick = {
                () => setActiveTab('overview') } > Overview <
            /button> <
            button className = { activeTab === 'admins' ? 'active' : '' }
            onClick = {
                () => setActiveTab('admins') } > Kebele Admins <
            /button> <
            button className = { activeTab === 'farmers' ? 'active' : '' }
            onClick = {
                () => setActiveTab('farmers') } > Farmers <
            /button> <
            button className = { activeTab === 'products' ? 'active' : '' }
            onClick = {
                () => setActiveTab('products') } > Products <
            /button> <
            button className = { activeTab === 'requests' ? 'active' : '' }
            onClick = {
                () => setActiveTab('requests') } > Requests <
            /button> <
            /nav> <
            /div>

            { /* Main Content */ } <
            div className = "main-content" >
            <
            header >
            <
            h1 > Welcome, { user.fullName }(Woreda Admin) < /h1> <
            p > Region: { user.region_name } | Zone: { user.zone_name } | Woreda: { user.woreda_name } < /p> <
            button onClick = { handleLogout }
            className = "logout-btn" >
            Logout <
            /button> <
            /header>

            <
            main > { /* Overview Tab */ } {
                activeTab === 'overview' && ( <
                    div className = "overview-tab" >
                    <
                    h2 > System Overview < /h2> <
                    div className = "stats-grid" >
                    <
                    div className = "stat-card" >
                    <
                    h3 > { stats.totalRequests } < /h3> <
                    p > Total Requests < /p> <
                    /div> <
                    div className = "stat-card" >
                    <
                    h3 > { stats.pendingRequests } < /h3> <
                    p > Pending Requests < /p> <
                    /div> <
                    div className = "stat-card" >
                    <
                    h3 > { stats.approvedRequests } < /h3> <
                    p > Approved Requests < /p> <
                    /div> <
                    div className = "stat-card" >
                    <
                    h3 > { stats.acceptedRequests } < /h3> <
                    p > Accepted Requests < /p> <
                    /div> <
                    div className = "stat-card" >
                    <
                    h3 > { stats.rejectedRequests } < /h3> <
                    p > Rejected Requests < /p> <
                    /div> <
                    div className = "stat-card" >
                    <
                    h3 > { stats.totalAdmins } < /h3> <
                    p > Kebele Admins < /p> <
                    /div> <
                    div className = "stat-card" >
                    <
                    h3 > { stats.totalFarmers } < /h3> <
                    p > Farmers < /p> <
                    /div> <
                    div className = "stat-card" >
                    <
                    h3 > { stats.totalProducts } < /h3> <
                    p > Products < /p> <
                    /div> <
                    /div>

                    <
                    div className = "quick-actions" >
                    <
                    h2 > Quick Actions < /h2> <
                    div className = "actions-grid" >
                    <
                    button onClick = {
                        () => setActiveTab('requests') }
                    className = "action-btn" > Manage Requests <
                    /button> <
                    button onClick = {
                        () => setActiveTab('admins') }
                    className = "action-btn" > Manage Kebele Admins <
                    /button> <
                    button onClick = {
                        () => setActiveTab('farmers') }
                    className = "action-btn" > View Farmers <
                    /button> <
                    button onClick = {
                        () => setActiveTab('products') }
                    className = "action-btn" > Manage Products <
                    /button> <
                    button onClick = {
                        () => setShowAdminModal(true) }
                    className = "action-btn" > Register Kebele Admin <
                    /button> <
                    button onClick = {
                        () => setShowProductModal(true) }
                    className = "action-btn" > Add Product <
                    /button> <
                    /div> <
                    /div> <
                    /div>
                )
            }

            { /* Kebele Admins Tab */ } {
                activeTab === 'admins' && ( <
                    div className = "admins-tab" >
                    <
                    div className = "tab-header" >
                    <
                    h2 > Kebele Admin Management < /h2> <
                    p > Managing Kebele admins in your woreda: { user.woreda_name } < /p> <
                    button onClick = {
                        () => setShowAdminModal(true) }
                    className = "add-btn" > Register New Kebele Admin <
                    /button> <
                    /div>

                    <
                    div className = "admins-list" > {
                        admins.map((admin) => ( <
                            div key = { admin.id }
                            className = "admin-card" >
                            <
                            div className = "admin-info" >
                            <
                            h3 > { admin.full_name } < /h3> <
                            p > Phone: { admin.phone_number } < /p> <
                            p > Kebele: { admin.kebele_name } < /p> <
                            p > Registered: { new Date(admin.created_at).toLocaleDateString() } < /p> <
                            /div> <
                            div className = "admin-actions" >
                            <
                            button onClick = {
                                () => handleEditAdmin(admin) }
                            className = "edit-btn" > Edit <
                            /button> <
                            button onClick = {
                                () => handleDeleteAdmin(admin.id) }
                            className = "delete-btn" > Delete <
                            /button> <
                            /div> <
                            /div>
                        ))
                    } {
                        admins.length === 0 && ( <
                            p > No Kebele admins found in your woreda. < /p>
                        )
                    } <
                    /div> <
                    /div>
                )
            }

            { /* Farmers Tab */ } {
                activeTab === 'farmers' && ( <
                    div className = "farmers-tab" >
                    <
                    div className = "tab-header" >
                    <
                    h2 > Farmer Management < /h2> <
                    p > Viewing farmers in your woreda: { user.woreda_name } < /p>

                    <
                    div className = "filters" >
                    <
                    label > Filter by Kebele: < /label> <
                    select value = { kebeleFilter }
                    onChange = {
                        (e) => setKebeleFilter(e.target.value) } >
                    <
                    option value = "all" > All Kebeles < /option> {
                        kebeles.map(kebele => ( <
                            option key = { kebele }
                            value = { kebele } > { kebele } < /option>
                        ))
                    } <
                    /select> <
                    /div> <
                    /div>

                    <
                    div className = "farmers-list" > {
                        farmers
                        .filter(farmer => kebeleFilter === 'all' || farmer.kebele_name === kebeleFilter)
                        .map((farmer) => ( <
                            div key = { farmer.id }
                            className = "farmer-card" >
                            <
                            div className = "farmer-info" >
                            <
                            h3 > { farmer.full_name } < /h3> <
                            p > Phone: { farmer.phone_number } < /p> <
                            p > Kebele: { farmer.kebele_name } < /p> <
                            p > Registered: { new Date(farmer.created_at).toLocaleDateString() } < /p> <
                            /div> <
                            div className = "farmer-requests" >
                            <
                            h4 > Recent Requests: < /h4> {
                                requests
                                    .filter(req => req.farmer_id === farmer.id)
                                    .slice(0, 3)
                                    .map(req => ( <
                                        div key = { req.id }
                                        className = "request-item" >
                                        <
                                        span className = "product-name" > { req.product_name } < /span> { getStatusBadge(req.status) } <
                                        /div>
                                    ))
                            } {
                                requests.filter(req => req.farmer_id === farmer.id).length === 0 && ( <
                                    p > No requests from this farmer < /p>
                                )
                            } <
                            /div> <
                            /div>
                        ))
                    } {
                        farmers.length === 0 && ( <
                            p > No farmers found in your woreda. < /p>
                        )
                    } <
                    /div> <
                    /div>
                )
            }

            { /* Products Tab */ } {
                activeTab === 'products' && ( <
                    div className = "products-tab" >
                    <
                    div className = "tab-header" >
                    <
                    h2 > Product Management < /h2> <
                    p > Managing products in your woreda < /p> <
                    button onClick = {
                        () => setShowProductModal(true) }
                    className = "add-btn" > Add New Product <
                    /button> <
                    /div>

                    <
                    div className = "products-section" >
                    <
                    h3 > My Products < /h3> <
                    div className = "products-list" > {
                        myProducts.map((product) => ( <
                            div key = { product.id }
                            className = "product-card" >
                            <
                            div className = "product-info" >
                            <
                            h3 > { product.name } < /h3> <
                            p > { product.description } < /p> <
                            p > Category: { product.category } < /p> <
                            p > Amount: { product.amount } < /p> <
                            p > Created: { new Date(product.created_at).toLocaleDateString() } < /p> <
                            /div> <
                            div className = "product-actions" >
                            <
                            button onClick = {
                                () => handleEditProduct(product) }
                            className = "edit-btn" > Edit <
                            /button> <
                            button onClick = {
                                () => handleDeleteProduct(product.id) }
                            className = "delete-btn" > Delete <
                            /button> <
                            /div> <
                            /div>
                        ))
                    } {
                        myProducts.length === 0 && ( <
                            p > You haven 't added any products yet.</p>
                        )
                    } <
                    /div> <
                    /div>

                    <
                    div className = "products-section" >
                    <
                    h3 > Products from Other Admins < /h3> <
                    div className = "products-list" > {
                        otherProducts.map((product) => ( <
                            div key = { product.id }
                            className = "product-card" >
                            <
                            div className = "product-info" >
                            <
                            h3 > { product.name } < /h3> <
                            p > { product.description } < /p> <
                            p > Category: { product.category } < /p> <
                            p > Amount: { product.amount } < /p> <
                            p > Added by: { product.created_by_name }({ product.admin_role }) < /p> <
                            p > Location: { product.region_name }
                            / {product.zone_name} / { product.woreda_name }
                            / {product.kebele_name}</p >
                            <
                            p > Created: { new Date(product.created_at).toLocaleDateString() } < /p> <
                            /div> <
                            /div>
                        ))
                    } {
                        otherProducts.length === 0 && ( <
                            p > No products from other admins. < /p>
                        )
                    } <
                    /div> <
                    /div> <
                    /div>
                )
            }

            { /* Requests Tab */ } {
                activeTab === 'requests' && ( <
                    div className = "requests-tab" >
                    <
                    div className = "tab-header" >
                    <
                    h2 > Request Management < /h2> <
                    p > Manage farmer requests in your woreda: { user.woreda_name } < /p>

                    <
                    div className = "filters" >
                    <
                    label > Filter by Status: < /label> <
                    select value = { statusFilter }
                    onChange = {
                        (e) => setStatusFilter(e.target.value) } >
                    <
                    option value = "all" > All Requests < /option> <
                    option value = "pending" > Pending < /option> <
                    option value = "approved" > Approved < /option> <
                    option value = "accepted" > Accepted < /option> <
                    option value = "rejected" > Rejected < /option> <
                    /select>

                    <
                    label > Filter by Kebele: < /label> <
                    select value = { kebeleFilter }
                    onChange = {
                        (e) => setKebeleFilter(e.target.value) } >
                    <
                    option value = "all" > All Kebeles < /option> {
                        kebeles.map(kebele => ( <
                            option key = { kebele }
                            value = { kebele } > { kebele } < /option>
                        ))
                    } <
                    /select> <
                    /div>

                    {
                        filteredRequests.filter(r => canWoredaAdminAct(r)).length > 0 && ( <
                            div className = "bulk-actions" >
                            <
                            select value = { bulkAction }
                            onChange = {
                                (e) => setBulkAction(e.target.value) } >
                            <
                            option value = "" > Select Bulk Action < /option> <
                            option value = "approve" > Approve < /option> <
                            option value = "accept" > Accept < /option> <
                            option value = "reject" > Reject < /option> <
                            /select> <
                            button onClick = { handleBulkUpdate }
                            className = "bulk-btn" >
                            Apply to Selected({ selectedRequests.length }) <
                            /button> <
                            button onClick = { selectAllRequests }
                            className = "select-all-btn" > {
                                selectedRequests.length === filteredRequests.filter(r => canWoredaAdminAct(r)).length ?
                                'Deselect All' : 'Select All Available'
                            } <
                            /button> <
                            /div>
                        )
                    } <
                    /div>

                    <
                    div className = "requests-list" > {
                        filteredRequests.map((request) => ( <
                                div key = { request.id }
                                className = "request-card" >
                                <
                                div className = "request-info" >
                                <
                                h3 > { request.product_name } < /h3> <
                                p > Farmer: { request.farmer_name }({ request.farmer_phone }) < /p> <
                                p > Quantity: { request.quantity } < /p> {
                                    request.note && < p > Note: { request.note } < /p>} <
                                        p > Product Amount: { request.product_amount } < /p> <
                                        p > Kebele: { request.kebele_name } < /p> <
                                        p > Date: { new Date(request.created_at).toLocaleDateString() } < /p> { getStatusBadge(request.status) } {
                                            request.handled_by_admin && ( <
                                                p > Handled by: { request.handled_by_admin } < /p>
                                            )
                                        } <
                                        button onClick = {
                                            () => viewRequestDetails(request.id) }
                                    className = "details-btn" >
                                        View Details <
                                        /button> <
                                        /div> <
                                        div className = "request-actions" > {
                                            canWoredaAdminAct(request) && ( <
                                                >
                                                <
                                                div className = "request-checkbox" >
                                                <
                                                input type = "checkbox"
                                                checked = { selectedRequests.includes(request.id) }
                                                onChange = {
                                                    () => toggleRequestSelection(request.id) }
                                                /> <
                                                label > Select < /label> <
                                                /div> <
                                                div className = "status-options" >
                                                <
                                                button onClick = {
                                                    () => handleStatusUpdate(request, 'Approved') }
                                                className = "approve-btn" >
                                                Approve <
                                                /button> <
                                                button onClick = {
                                                    () => handleStatusUpdate(request, 'Accepted') }
                                                className = "accept-btn" >
                                                Accept <
                                                /button> <
                                                button onClick = {
                                                    () => handleStatusUpdate(request, 'Rejected') }
                                                className = "reject-btn" >
                                                Reject <
                                                /button> <
                                                /div> <
                                                />
                                            )
                                        } {
                                            !canWoredaAdminAct(request) && ( <
                                                p > Not available
                                                for action < /p>
                                            )
                                        } {
                                            canDeleteRequest(request) && ( <
                                                button onClick = {
                                                    () => handleDeleteRequest(request.id) }
                                                className = "delete-btn" > Delete <
                                                /button>
                                            )
                                        } <
                                        /div> <
                                        /div>
                                ))
                        } {
                            filteredRequests.length === 0 && ( <
                                p > No requests found in your woreda. < /p>
                            )
                        } <
                        /div>

                        { /* Status Update Modal */ } {
                            showRequestModal && selectedRequest && ( <
                                div className = "modal" >
                                <
                                div className = "modal-content" >
                                <
                                h3 > { requestStatus ? `Update Request Status` : `Request Details` } < /h3>

                                <
                                div className = "request-details" >
                                <
                                h4 > Request Information < /h4> <
                                p > < strong > Product: < /strong> {selectedRequest.product_name}</p >
                                <
                                p > < strong > Farmer: < /strong> {selectedRequest.farmer_name}</p >
                                <
                                p > < strong > Quantity: < /strong> {selectedRequest.quantity}</p >
                                <
                                p > < strong > Current Status: < /strong> {selectedRequest.status}</p >

                                <
                                h4 > Approval Status at Each Level < /h4> { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) } { getLevelStatus('federal', selectedRequest) } <
                                /div>

                                {
                                    requestStatus && ( <
                                        >
                                        <
                                        div className = "form-group" >
                                        <
                                        label > Decision Reason(Optional): < /label> <
                                        textarea value = { decisionReason }
                                        onChange = {
                                            (e) => setDecisionReason(e.target.value) }
                                        rows = "3"
                                        placeholder = "Add any notes about your decision..." /
                                        >
                                        <
                                        /div>

                                        <
                                        div className = "modal-actions" >
                                        <
                                        button onClick = { confirmStatusUpdate } >
                                        Confirm { requestStatus } <
                                        /button> <
                                        button onClick = {
                                            () => {
                                                setShowRequestModal(false);
                                                setSelectedRequest(null);
                                                setRequestStatus('');
                                                setDecisionReason('');
                                            }
                                        } >
                                        Cancel <
                                        /button> <
                                        /div> <
                                        />
                                    )
                                }

                                {
                                    !requestStatus && ( <
                                        div className = "modal-actions" >
                                        <
                                        button onClick = {
                                            () => {
                                                setShowRequestModal(false);
                                                setSelectedRequest(null);
                                            }
                                        } >
                                        Close <
                                        /button> <
                                        /div>
                                    )
                                } <
                                /div> <
                                /div>
                            )
                        } <
                        /div>
                    )
                }

                { /* Admin Modal */ } {
                    showAdminModal && ( <
                        div className = "modal" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > { editingAdmin ? 'Edit Kebele Admin' : 'Register New Kebele Admin' } < /h3> <
                        form onSubmit = { handleAdminSubmit } >
                        <
                        div className = "form-group" >
                        <
                        label > Full Name: < /label> <
                        input type = "text"
                        value = { adminForm.fullName }
                        onChange = {
                            (e) => setAdminForm({...adminForm, fullName: e.target.value }) }
                        required /
                        >
                        <
                        /div> <
                        div className = "form-group" >
                        <
                        label > Phone Number: < /label> <
                        input type = "tel"
                        value = { adminForm.phoneNumber }
                        onChange = {
                            (e) => setAdminForm({...adminForm, phoneNumber: e.target.value }) }
                        required /
                        >
                        <
                        /div> <
                        div className = "form-group" >
                        <
                        label > Password: < /label> <
                        input type = "password"
                        value = { adminForm.password }
                        onChange = {
                            (e) => setAdminForm({...adminForm, password: e.target.value }) }
                        required = {!editingAdmin }
                        /> <
                        /div> <
                        div className = "form-group" >
                        <
                        label > Confirm Password: < /label> <
                        input type = "password"
                        value = { adminForm.confirmPassword }
                        onChange = {
                            (e) => setAdminForm({...adminForm, confirmPassword: e.target.value }) }
                        required = {!editingAdmin }
                        /> <
                        /div> <
                        div className = "form-group" >
                        <
                        label > Kebele Name: < /label> <
                        input type = "text"
                        value = { adminForm.kebele_name }
                        onChange = {
                            (e) => setAdminForm({...adminForm, kebele_name: e.target.value }) }
                        required /
                        >
                        <
                        /div> <
                        div className = "form-group" >
                        <
                        label > Role: < /label> <
                        select value = { adminForm.role }
                        onChange = {
                            (e) => setAdminForm({...adminForm, role: e.target.value }) } >
                        <
                        option value = "Kebele" > Kebele Admin < /option> <
                        /select> <
                        /div> <
                        div className = "modal-actions" >
                        <
                        button type = "submit" > { editingAdmin ? 'Update Admin' : 'Register Admin' } <
                        /button> <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowAdminModal(false);
                                setEditingAdmin(null);
                                setAdminForm({
                                    fullName: '',
                                    phoneNumber: '',
                                    password: '',
                                    confirmPassword: '',
                                    role: 'Kebele',
                                    kebele_name: ''
                                });
                            }
                        } >
                        Cancel <
                        /button> <
                        /div> <
                        /form> <
                        /div> <
                        /div>
                    )
                }

                { /* Product Modal */ } {
                    showProductModal && ( <
                        div className = "modal" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > { editingProduct ? 'Edit Product' : 'Add New Product' } < /h3> <
                        form onSubmit = { handleProductSubmit } >
                        <
                        div className = "form-group" >
                        <
                        label > Product Name: < /label> <
                        input type = "text"
                        value = { productForm.name }
                        onChange = {
                            (e) => setProductForm({...productForm, name: e.target.value }) }
                        required /
                        >
                        <
                        /div> <
                        div className = "form-group" >
                        <
                        label > Description: < /label> <
                        textarea value = { productForm.description }
                        onChange = {
                            (e) => setProductForm({...productForm, description: e.target.value }) }
                        required /
                        >
                        <
                        /div> <
                        div className = "form-group" >
                        <
                        label > Category: < /label> <
                        input type = "text"
                        value = { productForm.category }
                        onChange = {
                            (e) => setProductForm({...productForm, category: e.target.value }) }
                        required /
                        >
                        <
                        /div> <
                        div className = "form-group" >
                        <
                        label > Amount: < /label> <
                        input type = "number"
                        value = { productForm.amount }
                        onChange = {
                            (e) => setProductForm({...productForm, amount: e.target.value }) }
                        required /
                        >
                        <
                        /div> <
                        div className = "modal-actions" >
                        <
                        button type = "submit" > { editingProduct ? 'Update Product' : 'Add Product' } <
                        /button> <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowProductModal(false);
                                setEditingProduct(null);
                                setProductForm({ name: '', description: '', category: '', amount: '' });
                            }
                        } >
                        Cancel <
                        /button> <
                        /div> <
                        /form> <
                        /div> <
                        /div>
                    )
                } <
                /main> <
                /div> <
                /div>
            );
        };

        export default WoredaDashboard;
