import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, onAuthStateChanged, signInAnonymously } from 'firebase/auth';
import { getFirestore, doc, setDoc, getDoc, collection, query, where, onSnapshot, serverTimestamp, addDoc, updateDoc, getDocs, deleteDoc } from 'firebase/firestore';
import { useForm } from 'react-hook-form';
import Sidebar from './components/Sidebar'; // Corrected import path with a capital 'S'
import './index.css';

// ----------------------
// Firebase Initialization
// ----------------------
// The following global variables are provided by the Canvas environment.
// For local development, we provide fallback values to avoid compilation errors.
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {
    // You can replace these with your own Firebase project configuration for full functionality
    apiKey: "AIzaSyBh7ErxnKurtNkeK_0PW5XW4joDe_r9Ut4",
    authDomain: "farmers-management-syste-84643.firebaseapp.com",
    projectId: "farmers-management-syste-84643",
    storageBucket: "farmers-management-syste-84643.firebasestorage.app",
    messagingSenderId: "410779424956",
    appId: "1:410779424956:web:b1d26dab35719b1e9a18b9",
    measurementId: "G-RD879ML6B6"
};
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : '';

// Initialize Firebase app and services
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// ----------------------
// Constants & Mock Data
// ----------------------
const ADMIN_ROLES = {
    FEDERAL: 'Federal',
    REGION: 'Region',
    ZONE: 'Zone',
    WOREDA: 'Woreda',
    KEBELE: 'Kebele',
};

// The user has requested to keep this constant, but it is not used in this implementation.
const ADMIN_ROLES_UNUSED = ['admin'];

const productTypes = [
    { id: 1, name: 'Tractor', department: 'Biological Input Supply', description: 'Used for plowing' },
    { id: 2, name: 'Harvester', department: 'Biological Input Supply', description: 'Used for harvesting' },
    { id: 3, name: 'Fertilizer (DAP)', department: 'Chemical Input Supply', description: 'Di-ammonium phosphate fertilizer' },
    { id: 4, name: 'Pesticide', department: 'Chemical Input Supply', description: 'Pesticide for crop protection' },
];

const mockUsers = [
    { phone: '0912345678', password: 'password123', role: ADMIN_ROLES.FEDERAL, fullName: 'Federal Admin', location: null, uid: 'federal-admin-uid' },
    { phone: '0987654321', password: 'password123', role: ADMIN_ROLES.REGION, fullName: 'Oromia Region Admin', location: 'Oromia', uid: 'oromia-admin-uid' },
    { phone: '0911223344', password: 'password123', role: ADMIN_ROLES.ZONE, fullName: 'Arsi Zone Admin', location: 'Arsi', uid: 'arsi-admin-uid' },
    { phone: '0933445566', password: 'password123', role: ADMIN_ROLES.WOREDA, fullName: 'Tiyo Woreda Admin', location: 'Tiyo', uid: 'tiyo-admin-uid' },
    { phone: '0955667788', password: 'password123', role: ADMIN_ROLES.KEBELE, fullName: 'Wendo Kebele Rep', location: 'Wendo', uid: 'wendo-admin-uid' },
];

// ----------------------
// Main App Component
// ----------------------
export default function App() {
    const [currentUser, setCurrentUser] = useState(null);
    const [authReady, setAuthReady] = useState(false);
    const [loading, setLoading] = useState(true);
    const [requests, setRequests] = useState([]);
    const [locations, setLocations] = useState({ regions: [], zones: [], woredas: [], kebeles: [] });
    const [allUsers, setAllUsers] = useState([]);
    const [currentPage, setCurrentPage] = useState('login');
    const [showModal, setShowModal] = useState(false);
    const [modalContent, setModalContent] = useState('');
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [selectedAdminToEdit, setSelectedAdminToEdit] = useState(null);

    // Initial Firebase setup and auth state listener
    useEffect(() => {
        const setupFirebase = async() => {
            try {
                // Check if the initialAuthToken exists and is not empty
                if (initialAuthToken) {
                    await signInWithCustomToken(auth, initialAuthToken);
                } else {
                    await signInAnonymously(auth);
                }
            } catch (error) {
                console.error("Firebase Auth Error:", error);
                // Fallback to anonymous sign-in if custom token fails
                try {
                    await signInAnonymously(auth);
                } catch (anonError) {
                    console.error("Anonymous sign-in failed:", anonError);
                }
            }
        };
        setupFirebase();

        const unsubscribeAuth = onAuthStateChanged(auth, (user) => {
            if (user) {
                const userRef = doc(db, 'users', user.uid);
                onSnapshot(userRef, (docSnap) => {
                    if (docSnap.exists()) {
                        const userData = { id: docSnap.id, ...docSnap.data() };
                        setCurrentUser(userData);
                    } else {
                        // For mock users, we set a default role based on the UID
                        const mockUser = mockUsers.find(u => u.uid === user.uid);
                        if (mockUser) {
                            setCurrentUser({...mockUser, id: user.uid });
                        } else {
                            // If a new anonymous user, set a default role or handle as needed
                            setCurrentUser({ id: user.uid, role: 'Farmer' });
                        }
                    }
                    setAuthReady(true);
                }, (error) => {
                    console.error("Error fetching user data:", error);
                });
            } else {
                setCurrentUser(null);
                setAuthReady(true);
            }
        });

        return () => unsubscribeAuth();
    }, []);

    // Fetch requests and locations once auth is ready
    useEffect(() => {
        if (!authReady || !currentUser) return;

        const fetchRequests = () => {
            const requestsCollectionRef = collection(db, 'requests');
            let requestsQuery;

            // Apply filters based on admin level
            if (currentUser.role === ADMIN_ROLES.FEDERAL) {
                requestsQuery = query(requestsCollectionRef);
            } else if (currentUser.role === ADMIN_ROLES.REGION) {
                requestsQuery = query(requestsCollectionRef, where('location.region', '==', currentUser.location));
            } else if (currentUser.role === ADMIN_ROLES.ZONE) {
                requestsQuery = query(requestsCollectionRef, where('location.zone', '==', currentUser.location));
            } else if (currentUser.role === ADMIN_ROLES.WOREDA) {
                requestsQuery = query(requestsCollectionRef, where('location.woreda', '==', currentUser.location));
            } else if (currentUser.role === ADMIN_ROLES.KEBELE) {
                requestsQuery = query(requestsCollectionRef, where('location.kebele', '==', currentUser.location));
            } else {
                requestsQuery = query(requestsCollectionRef, where('requesterId', '==', currentUser.id));
            }

            const unsubscribeRequests = onSnapshot(requestsQuery, (snapshot) => {
                const fetchedRequests = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
                // Corrected the sorting logic to avoid the syntax error
                const sortedRequests = fetchedRequests.sort((a, b) => {
                    const timeA = (a.timestamp && a.timestamp.seconds) || 0;
                    const timeB = (b.timestamp && b.timestamp.seconds) || 0;
                    return timeB - timeA;
                });
                setRequests(sortedRequests);
            }, (error) => {
                console.error("Error fetching requests:", error);
            });
            return unsubscribeRequests;
        };

        const fetchLocations = async() => {
            const fetchedRegions = await getDocs(collection(db, 'locations_regions')).then(s => s.docs.map(d => d.data().name));
            const fetchedZones = await getDocs(collection(db, 'locations_zones')).then(s => s.docs.map(d => d.data().name));
            const fetchedWoredas = await getDocs(collection(db, 'locations_woredas')).then(s => s.docs.map(d => d.data().name));
            const fetchedKebeles = await getDocs(collection(db, 'locations_kebeles')).then(s => s.docs.map(d => d.data().name));
            setLocations({
                regions: fetchedRegions,
                zones: fetchedZones,
                woredas: fetchedWoredas,
                kebeles: fetchedKebeles,
            });
        };

        const fetchUsers = async() => {
            const usersCollectionRef = collection(db, 'users');
            let usersQuery;
            if (currentUser.role === ADMIN_ROLES.FEDERAL) {
                usersQuery = query(usersCollectionRef, where('role', '!=', ADMIN_ROLES.FEDERAL));
            } else if (currentUser.role === ADMIN_ROLES.REGION) {
                usersQuery = query(usersCollectionRef, where('role', '==', ADMIN_ROLES.ZONE), where('location', '==', currentUser.location));
            } else if (currentUser.role === ADMIN_ROLES.ZONE) {
                usersQuery = query(usersCollectionRef, where('role', '==', ADMIN_ROLES.WOREDA), where('location', '==', currentUser.location));
            } else if (currentUser.role === ADMIN_ROLES.WOREDA) {
                usersQuery = query(usersCollectionRef, where('role', '==', ADMIN_ROLES.KEBELE), where('location', '==', currentUser.location));
            } else {
                usersQuery = query(usersCollectionRef, where('role', '==', 'Farmer'));
            }

            const unsubscribeUsers = onSnapshot(usersQuery, (snapshot) => {
                const fetchedUsers = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
                setAllUsers(fetchedUsers);
            }, (error) => {
                console.error("Error fetching users:", error);
            });
            return unsubscribeUsers;
        }

        const unsubscribeRequests = fetchRequests();
        fetchLocations();
        const unsubscribeUsers = fetchUsers();
        setLoading(false);

        return () => {
            unsubscribeRequests();
            unsubscribeUsers();
        };
    }, [authReady, currentUser]);


    // Helper functions for UI and state management
    const showMessage = (message) => {
        setModalContent(message);
        setShowModal(true);
    };

    const handleLogout = async() => {
        await auth.signOut();
        setCurrentUser(null);
        setCurrentPage('login');
    };

    const handleLogin = async(phone, password) => {
        const mockUser = mockUsers.find(u => u.phone === phone && u.password === password);
        if (mockUser) {
            // Simulate login by signing in with the user's custom UID
            try {
                const customToken = mockUser.uid; // In a real app, this would be a real custom token
                await signInWithCustomToken(auth, customToken);
                showMessage(`Welcome, ${mockUser.fullName}!`);
                setCurrentPage('dashboard');
            } catch (error) {
                console.error("Login failed:", error);
                showMessage("Login failed. Please check your credentials.");
            }
        } else {
            showMessage("Invalid phone number or password.");
        }
    };


    const handleRequestAction = async(requestId, newStatus, feedback) => {
        if (!currentUser) {
            showMessage("You must be logged in to perform this action.");
            return;
        }

        try {
            const requestRef = doc(db, 'requests', requestId);
            const requestSnapshot = await getDoc(requestRef);
            if (!requestSnapshot.exists()) {
                showMessage("Request not found.");
                return;
            }

            const existingApprovals = requestSnapshot.data().approvals || {};
            const newApprovals = {
                ...existingApprovals,
                [currentUser.role.toLowerCase()]: {
                    status: newStatus,
                    by: currentUser.fullName,
                    uid: currentUser.id,
                    feedback: feedback || null,
                    timestamp: serverTimestamp(),
                }
            };
            await updateDoc(requestRef, {
                status: newStatus, // Update overall status to the latest approval
                approvals: newApprovals
            });
            showMessage(`Request ${requestId} has been ${newStatus}.`);
            setSelectedRequest(null); // Close the form
        } catch (error) {
            console.error("Error updating request:", error);
            showMessage("Failed to update request status. Please try again.");
        }
    };

    // Components for different views
    const MainDashboard = () => {
        const statusCounts = requests.reduce((acc, req) => {
            acc[req.status] = (acc[req.status] || 0) + 1;
            return acc;
        }, {});
        return ( <
            div className = "p-8" >
            <
            div className = "grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-6 mb-8" >
            <
            DashboardCard title = "Total Requests"
            count = { requests.length }
            icon = "" / >
            <
            DashboardCard title = "Approved"
            count = { statusCounts['Approved'] || 0 }
            icon = "" / >
            <
            DashboardCard title = "Pending"
            count = { statusCounts['Pending'] || 0 }
            icon = "" / >
            <
            DashboardCard title = "Rejected"
            count = { statusCounts['Rejected'] || 0 }
            icon = "" / >
            <
            DashboardCard title = "Delivered"
            count = { statusCounts['Delivered'] || 0 }
            icon = "" / >
            <
            /div> <
            h2 className = "text-2xl font-bold mb-4" > Product Requests Table View < /h2> <
            RequestsTable / >
            <
            /div>
        );
    };

    const RequestsTable = () => {
        return ( <
            div className = "bg-white p-6 rounded-xl shadow-lg" >
            <
            div className = "overflow-x-auto" >
            <
            table className = "min-w-full text-left" >
            <
            thead >
            <
            tr className = "bg-gray-100 text-gray-600 uppercase text-sm leading-normal" >
            <
            th className = "py-3 px-6 text-left" > Request ID < /th> <
            th className = "py-3 px-6 text-left" > Requester < /th> <
            th className = "py-3 px-6 text-left" > Product < /th> <
            th className = "py-3 px-6 text-left" > Location < /th> <
            th className = "py-3 px-6 text-left" > Status < /th> <
            th className = "py-3 px-6 text-center" > Actions < /th> < /
            tr > <
            /thead> <
            tbody className = "text-gray-600 text-sm font-light" > {
                requests.length > 0 ? (
                    requests.map(req => ( <
                        tr key = { req.id }
                        className = "border-b border-gray-200 hover:bg-gray-50" >
                        <
                        td className = "py-3 px-6 text-left whitespace-nowrap" > { req.id } < /td> <
                        td className = "py-3 px-6 text-left" > { req.requesterName } < /td> <
                        td className = "py-3 px-6 text-left" > { req.productType } < /td> <
                        td className = "py-3 px-6 text-left" > { req.location && req.location.region } > { req.location && req.location.zone } > { req.location && req.location.woreda } > { req.location && req.location.kebele } <
                        /td> <
                        td className = "py-3 px-6 text-left" >
                        <
                        span className = { `py-1 px-3 rounded-full text-xs font-semibold ${
                                                req.status === 'Approved' ? 'bg-green-200 text-green-600' :
                                                req.status === 'Pending' ? 'bg-yellow-200 text-yellow-600' :
                                                'bg-red-200 text-red-600'
                                            }` } > { req.status } <
                        /span> < /
                        td > <
                        td className = "py-3 px-6 text-center" >
                        <
                        button onClick = {
                            () => setSelectedRequest(req)
                        }
                        className = "bg-blue-500 text-white p-2 rounded-lg hover:bg-blue-600 transition-colors" > View <
                        /button> < /
                        td > <
                        /tr>
                    ))
                ) : ( <
                    tr >
                    <
                    td colSpan = "6"
                    className = "text-center py-4 text-gray-500" > No requests found. < /td> < /
                    tr >
                )
            } <
            /tbody> < /
            table > <
            /div> < /
            div >
        );
    };

    const RequestApprovalForm = ({ request, onClose }) => {
            const { register, handleSubmit } = useForm();
            const onSubmit = data => {
                const newStatus = data.action === 'approve' ? 'Approved' : 'Rejected';
                handleRequestAction(request.id, newStatus, data.feedback);
                onClose();
            };
            const canApprove = () => {
                if (!currentUser) return false;
                const userRole = currentUser.role.toLowerCase();
                const approvals = request.approvals || {};
                // Check if the current user has already approved
                if (approvals[userRole] && (approvals[userRole].status === 'Approved' || approvals[userRole].status === 'Rejected')) {
                    return false;
                }
                // Allow approval if the previous level has approved (or if it's the lowest level)
                if (userRole === 'kebele') {
                    return true;
                }
                if (userRole === 'woreda' && approvals.kebele && approvals.kebele.status === 'Approved') {
                    return true;
                }
                if (userRole === 'zone' && approvals.woreda && approvals.woreda.status === 'Approved') {
                    return true;
                }
                if (userRole === 'region' && approvals.zone && approvals.zone.status === 'Approved') {
                    return true;
                }
                if (userRole === 'federal' && approvals.region && approvals.region.status === 'Approved') {
                    return true;
                }
                return false;
            };
            return ( <
                    Modal show = { true }
                    onClose = { onClose }
                    title = { `Request Approval for ${request.id}` } >
                    <
                    div className = "p-6" >
                    <
                    div className = "bg-gray-100 p-4 rounded-lg mb-4" >
                    <
                    h3 className = "font-bold text-lg mb-2" > Request Details < /h3> <
                    p > < strong > Requested By: < /strong> {request.requesterName}</p >
                    <
                    p > < strong > Product Type: < /strong> {request.productType}</p >
                    <
                    p > < strong > Amount: < /strong> {request.amount}</p >
                    <
                    p > < strong > Purpose: < /strong> {request.purpose}</p >
                    <
                    p > < strong > Location: < /strong> {request.location && request.location.region} > {request.location && request.location.zone} > {request.location && request.location.woreda} > {request.location && request.location.kebele}</p >
                    <
                    p className = "mt-2 font-bold" > Previous Approvals: < /p> {
                    request.approvals && Object.keys(request.approvals).length > 0 ? (
                        Object.entries(request.approvals).map(([role, approval]) => ( <
                            p key = { role } > -{ role }: < span className = { `font-semibold ${approval.status === 'Approved' ? 'text-green-600' : 'text-red-600'}` } > { approval.status } < /span> by {approval.by} </p >
                        ))
                    ) : ( <
                        p > -No previous approvals. < /p>
                    )
                } <
                /div> {
            canApprove() ? ( <
                form onSubmit = { handleSubmit(onSubmit) } >
                <
                div className = "mb-4" >
                <
                label className = "inline-flex items-center mr-4" >
                <
                input type = "radio" {...register("action", { required: true })
                }
                value = "approve"
                className = "form-radio text-green-500" / >
                <
                span className = "ml-2" > Approve < /span> < /
                label > <
                label className = "inline-flex items-center" >
                <
                input type = "radio" {...register("action", { required: true })
                }
                value = "reject"
                className = "form-radio text-red-500" / >
                <
                span className = "ml-2" > Reject < /span> < /
                label > <
                /div> <
                div className = "mb-4" >
                <
                label className = "block text-gray-700 font-bold mb-2" > Feedback < /label> <
                textarea {...register("feedback")
                }
                rows = "3"
                className = "w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder = "Add optional feedback..." > < /textarea> < /
                div > <
                button type = "submit"
                className = "w-full bg-blue-500 text-white font-bold py-2 px-4 rounded-lg hover:bg-blue-600" > Submit < /button> < /
                form >
            ) : ( <
                div className = "bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4"
                role = "alert" >
                <
                p className = "font-bold" > Cannot Approve / Reject < /p> <
                p > You cannot take action on this request at this time. < /p> < /
                div >
            )
        } <
        /div> < /
        Modal >
);
};

