        /div>
    );
};

const ManageAdmins = () => {
    const { register, handleSubmit, reset, setValue } = useForm();
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
        if (selectedAdminToEdit) {
            setIsEditing(true);
            setValue('fullName', selectedAdminToEdit.fullName);
            setValue('role', selectedAdminToEdit.role);
            setValue('location', selectedAdminToEdit.location);
        } else {
            setIsEditing(false);
            reset();
        }
    }, [selectedAdminToEdit, setValue, reset]);

    const onSubmit = async(data) => {
        try {
            if (isEditing) {
                const userRef = doc(db, 'users', selectedAdminToEdit.id);
                await updateDoc(userRef, data);
                showMessage(`Admin ${data.fullName} updated successfully.`);
            } else {
                const { phone, password, ...rest } = data; // Assuming phone/password are for a different flow
                await addDoc(collection(db, 'users'), {
                    ...rest,
                    createdAt: serverTimestamp(),
                });
                showMessage(`Admin ${data.fullName} added successfully.`);
            }
            reset();
            setSelectedAdminToEdit(null);
        } catch (error) {
            console.error("Error saving admin:", error);
            showMessage("Failed to save admin. Please try again.");
        }
    };

    const handleEdit = (admin) => {
        setSelectedAdminToEdit(admin);
    };

    const handleDelete = async(adminId) => {
        try {
            await deleteDoc(doc(db, 'users', adminId));
            showMessage("Admin deleted successfully.");
        } catch (error) {
            console.error("Error deleting admin:", error);
            showMessage("Failed to delete admin. Please try again.");
        }
    };

    return ( <
            div className = "p-8" >
            <
            h2 className = "text-2xl font-bold mb-4" > Manage Administrators < /h2> <
            div className = "grid grid-cols-1 lg:grid-cols-2 gap-6" >
            <
            div className = "bg-white p-6 rounded-xl shadow-lg" >
            <
            h3 className = "text-xl font-bold mb-4" > { isEditing ? 'Edit Administrator' : 'Add New Administrator' } < /h3> <
            form onSubmit = { handleSubmit(onSubmit) } >
            <
            div className = "mb-4" >
            <
            label className = "block text-gray-700 font-bold mb-2" > Full Name < /label> <
            input type = "text" {...register("fullName", { required: true })
            }
            className = "w-full p-2 border border-gray-300 rounded-lg" / >
            <
            /div> <
            div className = "mb-4" >
            <
            label className = "block text-gray-700 font-bold mb-2" > Role < /label> <
            select {...register("role", { required: true })
            }
            className = "w-full p-2 border border-gray-300 rounded-lg" > {
                Object.values(ADMIN_ROLES).map(role => ( <
                    option key = { role }
                    value = { role } > { role } < /option>
                ))
            } <
            /select> < /
            div > <
            div className = "mb-4" >
            <
            label className = "block text-gray-700 font-bold mb-2" > Location < /label> <
            input type = "text" {...register("location")
            }
            className = "w-full p-2 border border-gray-300 rounded-lg" / >
            <
            /div> <
            div className = "flex space-x-2" >
            <
            button type = "submit"
            className = "flex-grow bg-blue-500 text-white font-bold py-2 px-4 rounded-lg hover:bg-blue-600" > { isEditing ? 'Update Admin' : 'Add Admin' } <
            /button> {
            isEditing && ( <
                button type = "button"
                onClick = {
                    () => setSelectedAdminToEdit(null)
                }
                className = "flex-grow bg-gray-500 text-white font-bold py-2 px-4 rounded-lg hover:bg-gray-600" > Cancel < /button>
            )
        } <
        /div> < /
        form > <
        /div> <
    div className = "bg-white p-6 rounded-xl shadow-lg" >
        <
        h3 className = "text-xl font-bold mb-4" > Existing Admins < /h3> <
    ul className = "space-y-2" > {
            allUsers.filter(u => u.role !== 'Farmer').map(admin => ( <
                li key = { admin.id }
                className = "p-4 bg-gray-50 rounded-lg shadow-sm flex justify-between items-center" >
                <
                div >
                <
                div className = "font-semibold" > { admin.fullName } < /div> <
                div className = "text-sm text-gray-500" > { admin.role } { admin.location && `(${admin.location})` } < /div> < /
                div > <
                div className = "space-x-2" >
                <
                button onClick = {
                    () => handleEdit(admin)
                }
                className = "bg-yellow-500 text-white p-2 rounded-lg hover:bg-yellow-600" > Edit < /button> <
                button onClick = {
                    () => handleDelete(admin.id)
                }
                className = "bg-red-500 text-white p-2 rounded-lg hover:bg-red-600" > Delete < /button> < /
                div > <
                /li>
            ))
        } <
        /ul> < /
        div > <
        /div> < /
        div >
);
};

const Modal = ({ show, onClose, title, message, children }) => {
        if (!show) return null;
        return ( <
            div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
            <
            div className = "relative mx-auto p-5 border w-96 shadow-lg rounded-md bg-white" >
            <
            div className = "flex justify-between items-center pb-3" >
            <
            h3 className = "text-lg font-bold" > { title } < /h3> <
            button onClick = { onClose }
            className = "text-gray-400 hover:text-gray-600" >
            <
            svg className = "h-6 w-6"
            fill = "none"
            viewBox = "0 0 24 24"
            stroke = "currentColor" >
            <
            path strokeLinecap = "round"
            strokeLinejoin = "round"
            strokeWidth = "2"
            d = "M6 18L18 6M6 6l12 12" / >
            <
            /svg> < /
            button > <
            /div> <
            div className = "mt-2" > {
                children || < p > { message } < /p> } </div >
                <
                /div> < /
                div >
            );
        };

        const DashboardCard = ({ title, count, icon }) => ( <
            div className = "bg-white p-5 rounded-xl shadow-lg transform hover:scale-105 transition-transform duration-200" >
            <
            div className = "flex items-center" >
            <
            div className = "p-3 rounded-full bg-indigo-100 text-indigo-600 mr-4" > { icon } < /div> <
            div >
            <
            h3 className = "text-gray-500 text-sm font-medium uppercase leading-tight" > { title } < /h3> <
            div className = "text-3xl font-bold text-gray-900" > { count } < /div> < /
            div > <
            /div> < /
            div >
        );

        if (!authReady || loading) {
            return ( <
                div className = "flex items-center justify-center min-h-screen bg-gray-100" >
                <
                h1 className = "text-3xl font-bold text-gray-700 animate-pulse" > Loading... < /h1> < /
                div >
            );
        }

        if (!currentUser) {
            // Login Page
            return ( <
                div className = "min-h-screen bg-gray-100 flex items-center justify-center p-4" >
                <
                div className = "bg-white p-8 rounded-xl shadow-2xl w-full max-w-md" >
                <
                h2 className = "text-3xl font-bold text-center text-gray-800 mb-6" > Login < /h2> <
                form onSubmit = {
                    (e) => {
                        e.preventDefault();
                        handleLogin(e.target.phone.value, e.target.password.value);
                    }
                } >
                <
                div className = "mb-4" >
                <
                label className = "block text-gray-700 font-bold mb-2" > Phone Number < /label> <
                input type = "tel"
                name = "phone"
                className = "w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder = "e.g., 0912345678"
                required / >
                <
                /div> <
                div className = "mb-6" >
                <
                label className = "block text-gray-700 font-bold mb-2" > Password < /label> <
                input type = "password"
                name = "password"
                className = "w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required / >
                <
                /div> <
                button type = "submit"
                className = "w-full bg-blue-500 text-white font-bold py-3 px-4 rounded-lg hover:bg-blue-600 transition-colors" > Login < /button> < /
                form > <
                /div> <
                Modal show = { showModal }
                onClose = {
                    () => setShowModal(false)
                }
                title = "Notification"
                message = { modalContent }
                /> < /
                div >
            );
        }

        // Dashboard for logged-in users
        return ( <
            div className = "flex min-h-screen bg-gray-100" >
            <
            Sidebar onLogout = { handleLogout }
            onNavigate = { setCurrentPage }
            currentUser = { currentUser }
            /> <
            main className = "flex-1 p-6" > {
                currentPage === 'dashboard' && < MainDashboard / >
            } {
                currentPage === 'master-data' && < MasterData / >
            } {
                currentPage === 'reports' && < Reports / >
            } {
                currentPage === 'manage-admins' && currentUser.role === ADMIN_ROLES.FEDERAL && < ManageAdmins / >
            } {
                selectedRequest && < RequestApprovalForm request = { selectedRequest }
                onClose = {
                    () => setSelectedRequest(null)
                }
                />} < /
                main > <
                    Modal show = { showModal }
                onClose = {
                    () => setShowModal(false)
                }
                title = "Notification"
                message = { modalContent }
                /> < /
                div >
            );
        }
