  // --------------- Reports ---------------
  async function fetchReports() {
    try {
      var token = localStorage.getItem("token");
      var [r1, r2] = await Promise.all([fetch(API("/api/admins/reports/my"), {
        headers: {
          Authorization: "Bearer " + token
        }
      }), fetch(API("/api/admins/reports"), {
        headers: {
          Authorization: "Bearer " + token
        }
      })]);
      if (r1.ok) setMyReports(await r1.json());
      if (r2.ok) {
        var rs = await r2.json();
        // Optional: Region may focus on their region scope; backend already does scope, keep as-is
        setScopeReports(rs);
      }
    } catch (e) {}
  }
  async function fetchReportStats() {
    try {
      var token = localStorage.getItem("token");
      var res = await fetch(API("/api/admins/reports/statistics"), {
        headers: {
          Authorization: "Bearer " + token
        }
      });
      if (res.ok) setReportStats(await res.json());
    } catch (e) {}
  }
  async function createReport(e) {
    e.preventDefault();
    if (!reportForm.title || !reportForm.body) {
      alert("Title & body required");
      return;
    }
    try {
      var token = localStorage.getItem("token");
      var res = await fetch(API("/api/admins/reports"), {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token
        },
        body: JSON.stringify({
          title: reportForm.title,
          body: reportForm.body
        })
      });
      var data = await res.json();
      if (!res.ok) {
        alert(data.message || "Failed to create report");
        return;
      }
      setReportForm({
        title: "",
        body: ""
      });
      await fetchReports();
      alert("Report submitted");
    } catch (e) {
      alert("Network error");
    }
  }
  async function updateReportStatus(id, status) {
    try {
      var token = localStorage.getItem("token");
      var res = await fetch(API("/api/admins/reports/" + id + "/status"), {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token
        },
        body: JSON.stringify({
          status: status
        })
      });
      var data = await res.json();
      if (!res.ok) {
        alert(data.message || "Failed to update status");
        return;
      }
      await fetchReports();
      await fetchReportStats();
    } catch (e) {
      alert("Network error");
    }
  }

