import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";

/**
 * RegionDashboard.js — full, self-contained dashboard for Region admins
 *
 * Covers ALL relevant backend controllers & routes exposed under adminRoutes.js and approvalWorkflowRoutes.js
 * and uses only endpoints that Region is authorized to call.
 *
 * What this file implements
 * -------------------------
 * 1) Authentication gate (requires stored user with role === "Region")
 * 2) Requests workflow (list + filter + detail + single/bulk status update)
 *    - GET   /api/admins/requests/status (list with detailed per-level status)
 *    - GET   /api/admins/requests/filter?status=... (server-side filter)
 *    - GET   /api/admins/requests/:id/status (detail)
 *    - PUT   /api/admins/requests/:id/status (update at Region level; backend enforces zone-approved & scope)
 *    - DELETE /api/admins/deleterequest/:id (allowed roles include Region; used to remove rejected)
 *
 * 3) Products (full CRUD for Region admin's own products + browse all & others)
 *    - GET   /api/admins/getproducts (all in scope)
 *    - GET   /api/admins/myproducts (mine)
 *    - GET   /api/admins/otherproducts (others')
 *    - POST  /api/admins/addproduct
 *    - PUT   /api/admins/editproduct/:id
 *    - DELETE /api/admins/deleteproduct/:id
 *
 * 4) Admin management (Region can manage Zone admins ONLY within its scope)
 *    - GET   /api/admins/admins (list all lower accounts in my scope)
 *    - POST  /api/admins/register (create Zone admin)
 *    - PUT   /api/admins/edit/:id (update Zone admin)
 *
 * 5) Farmers directory (read-only for Region)
 *    - GET   /api/admins/farmers (farmers in my scope)
 *
 * 6) Reports (create my report, list mine, list in scope, update status, get statistics)
 *    - POST  /api/admins/reports
 *    - GET   /api/admins/reports/my
 *    - GET   /api/admins/reports (in scope)
 *    - PUT   /api/admins/reports/:id/status (Region allowed)
 *    - GET   /api/admins/reports/statistics
 *
 * Notes & Conventions
 * -------------------
 * - No optional chaining used to stay compatible with older toolchains.
 * - Defensive null/undefined checks everywhere.
 * - Tailwind CSS utility classes for styling (safe to remove if not used in your app).
 * - All fetch calls include Authorization: Bearer <token> from localStorage.
 * - Bulk actions iterate client-side over currently filter-matching, Region-actionable requests.
 * - Region can act only when Zone has Approved AND Region is still Pending (backend also enforces this).
 */

const API = (path) => `http://localhost:5000${path}`;

function cx() {
    var out = "";
    for (var i = 0; i < arguments.length; i++) {
        if (arguments[i]) out += (out ? " " : "") + arguments[i];
    }
    return out;
}

const Badge = ({ text }) => {
    const map = {
        Pending: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
        Approved: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
        Accepted: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
        Rejected: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
    };
    var cls = map[text] ? map[text] : "bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200";
    return <span className = { cx("px-2 py-1 rounded-full text-xs font-medium", cls) } > { text || "Pending" } < /span>;
};

const Section = ({ title, right, children }) => ( <
    div className = "mb-8" >
    <
    div className = "flex items-center justify-between mb-3" >
    <
    h2 className = "text-xl font-semibold" > { title } < /h2> { right } <
    /div> <
    div className = "rounded-2xl border p-4 bg-white dark:bg-gray-900 dark:border-gray-700 shadow-sm" > { children } < /div> <
    /div>
);

const Confirm = ({ open, title, message, onConfirm, onCancel }) => {
    if (!open) return null;
    return ( <
        div className = "fixed inset-0 z-50 flex items-center justify-center bg-black/40 p-4" >
        <
        div className = "w-full max-w-md rounded-xl bg-white dark:bg-gray-900 p-4" >
        <
        div className = "text-lg font-semibold mb-2" > { title } < /div> <
        div className = "text-sm text-gray-600 dark:text-gray-300 mb-4" > { message } < /div> <
        div className = "flex justify-end gap-2" >
        <
        button className = "px-3 py-2 rounded-lg bg-gray-200 dark:bg-gray-700"
        onClick = { onCancel } > Cancel < /button> <
        button className = "px-3 py-2 rounded-lg bg-red-600 text-white"
        onClick = { onConfirm } > Confirm < /button> <
        /div> <
        /div> <
        /div>
    );
};

const Tabs = ({ value, onChange, items }) => ( <
    div className = "flex gap-2 mb-6 overflow-x-auto" > {
        items.map(function(it) {
            var active = value === it.value;
            return ( <
                button key = { it.value }
                onClick = {
                    function() { onChange(it.value); } }
                className = {
                    cx(
                        "px-3 py-2 rounded-xl whitespace-nowrap",
                        active ? "bg-blue-600 text-white" : "bg-gray-200 dark:bg-gray-800"
                    )
                } >
                { it.label } <
                /button>
            );
        })
    } <
    /div>
);

const TextInput = (props) => ( <
    input {...props }
    className = {
        cx(
            "w-full rounded-xl border px-3 py-2 outline-none",
            "bg-white dark:bg-gray-900 dark:border-gray-700",
            props.className
        )
    }
    />
);

const TextArea = (props) => ( <
    textarea {...props }
    className = {
        cx(
            "w-full rounded-xl border px-3 py-2 outline-none min-h-[100px]",
            "bg-white dark:bg-gray-900 dark:border-gray-700",
            props.className
        )
    }
    />
);

const Select = ({ value, onChange, options, className }) => ( <
    select value = { value }
    onChange = { onChange }
    className = { cx("w-full rounded-xl border px-3 py-2 bg-white dark:bg-gray-900 dark:border-gray-700", className) } >
    {
        options.map(function(opt) {
            return ( <
                option key = { String(opt.value) }
                value = { opt.value } > { opt.label } <
                /option>
            );
        })
    } <
    /select>
);

// ---------------------
// Main Component
// ---------------------

export default function RegionDashboard() {
    const navigate = useNavigate();

    // Auth & UI state
    const [user, setUser] = useState(null);
    const [activeTab, setActiveTab] = useState("overview");
    const [darkMode, setDarkMode] = useState(false);

    // Requests
    const [requests, setRequests] = useState([]);
    const [statusFilter, setStatusFilter] = useState("pending"); // pending | approved | accepted | rejected | all
    const [loadingRequests, setLoadingRequests] = useState(false);
    const [reqError, setReqError] = useState("");
    const [detailsOpen, setDetailsOpen] = useState(false);
    const [detail, setDetail] = useState(null);
    const [decisionReason, setDecisionReason] = useState("");
    const [confirm, setConfirm] = useState({ open: false, id: null, status: "" });

    // Products
    const [productFilter, setProductFilter] = useState("all");
    const [products, setProducts] = useState([]);
    const [myProducts, setMyProducts] = useState([]);
    const [otherProducts, setOtherProducts] = useState([]);
    const [productForm, setProductForm] = useState({
        id: null,
        name: "",
        category: "",
        subCategory: "",
        unit: "",
        amount: "",
        price: "",
        description: "",
        manufacturer: "",
        expiryDate: "",
    });
    const [productModal, setProductModal] = useState(false);

    // Zone Admins
    const [zoneAdmins, setZoneAdmins] = useState([]);
    const [adminModal, setAdminModal] = useState(false);
    const [adminForm, setAdminForm] = useState({ id: null, fullName: "", phoneNumber: "", password: "", confirmPassword: "", zone_name: "" });

    // Farmers (read-only)
    const [farmers, setFarmers] = useState([]);

    // Reports
    const [myReports, setMyReports] = useState([]);
    const [scopeReports, setScopeReports] = useState([]);
    const [reportStats, setReportStats] = useState(null);
    const [reportForm, setReportForm] = useState({ title: "", body: "" });

    // --------------- Auth & boot ---------------
    useEffect(function() {
        var token = localStorage.getItem("token");
        var raw = localStorage.getItem("user");
        var parsed = raw ? JSON.parse(raw) : null;
        var type = localStorage.getItem("userType");

        if (!token || !parsed || parsed.role !== "Region" || type !== "Region") {
            navigate("/login");
            return;
        }
        setUser(parsed);
    }, [navigate]);

    useEffect(function() {
        if (user !== null) {
            refreshAll();
        }
    }, [user]);

    useEffect(function() {
        if (darkMode) document.documentElement.classList.add("dark");
        else document.documentElement.classList.remove("dark");
    }, [darkMode]);

    async function refreshAll() {
        await Promise.all([
            fetchRequests(),
            fetchProducts(),
            fetchAdmins(),
            fetchFarmers(),
            fetchReports(),
            fetchReportStats(),
        ]);
    }

    // --------------- Requests ---------------
    function canRegionActOn(r) {
        var regionPending = !r.region_status || r.region_status === "Pending";
        return r.zone_status === "Approved" && regionPending;
    }

    const filteredRequests = useMemo(function() {
        var list = requests;
        var out = [];
        for (var i = 0; i < list.length; i++) {
            var r = list[i];
            if (statusFilter === "all") out.push(r);
            else if (statusFilter === "pending" && (!r.region_status || r.region_status === "Pending")) out.push(r);
            else if (statusFilter === "approved" && r.region_status === "Approved") out.push(r);
            else if (statusFilter === "accepted" && r.region_status === "Accepted") out.push(r);
            else if (statusFilter === "rejected" && r.region_status === "Rejected") out.push(r);
        }
        return out;
    }, [requests, statusFilter]);

    async function fetchRequests() {
        try {
            setLoadingRequests(true);
            setReqError("");
            var token = localStorage.getItem("token");
            var res = await fetch(API("/api/admins/requests/status"), {
                headers: { Authorization: "Bearer " + token },
            });
            if (!res.ok) {
                var err = await res.json();
                setReqError(err.message || "Failed to load requests");
                setLoadingRequests(false);
                return;
            }
            var data = await res.json();
            // Keep only my region's requests
            var scoped = [];
            for (var i = 0; i < data.length; i++)
                if (data[i].region_name === user.region_name) scoped.push(data[i]);
            setRequests(scoped);
            setLoadingRequests(false);
        } catch (e) {
            setReqError("Network error");
            setLoadingRequests(false);
        }
    }

    async function openRequestDetail(id) {
        try {
            var token = localStorage.getItem("token");
            var res = await fetch(API("/api/admins/requests/" + id + "/status"), { headers: { Authorization: "Bearer " + token } });
            if (res.ok) {
                var data = await res.json();
                setDetail(data);
                setDetailsOpen(true);
            } else {
                var err = await res.json();
                alert(err.message || "Failed to get details");
            }
        } catch (e) {
            alert("Network error");
        }
    }

    async function updateRequestStatus(id, status, feedback) {
        try {
            var token = localStorage.getItem("token");
            var res = await fetch(API("/api/admins/requests/" + id + "/status"), {
                method: "PUT",
                headers: { "Content-Type": "application/json", Authorization: "Bearer " + token },
                body: JSON.stringify({ status: status, feedback: feedback || "" }),
            });
            var data = await res.json();
            if (!res.ok) {
                alert(data.message || "Update failed");
                return;
            }
            setDetailsOpen(false);
            setDetail(null);
            setDecisionReason("");
            await fetchRequests();
            alert("Request " + status + " successfully");
        } catch (e) {
            alert("Network error");
        }
    }

    async function bulkAction(status) {
        var list = filteredRequests;
        var ids = [];
        for (var i = 0; i < list.length; i++)
            if (canRegionActOn(list[i])) ids.push(list[i].id);
        for (var j = 0; j < ids.length; j++) {
            await updateRequestStatus(ids[j], status, "Bulk by Region");
        }
        await fetchRequests();
    }

    async function deleteRejected(id) {
        setConfirm({ open: true, id: id, status: "delete" });
    }

    async function performConfirm() {
        var id = confirm.id;
        if (confirm.status === "delete") {
            try {
                var token = localStorage.getItem("token");
                var res = await fetch(API("/api/admins/deleterequest/" + id), { method: "DELETE", headers: { Authorization: "Bearer " + token } });
                if (!res.ok) {
                    var err = await res.json();
                    alert(err.message || "Delete failed");
                } else {
                    await fetchRequests();
                }
            } catch (e) {
                alert("Network error");
            }
        } else if (confirm.status === "approve" || confirm.status === "accept" || confirm.status === "reject") {
            var status = confirm.status === "approve" ? "Approved" : confirm.status === "accept" ? "Accepted" : "Rejected";
            await updateRequestStatus(id, status, decisionReason);
        }
        setConfirm({ open: false, id: null, status: "" });
    }

    // --------------- Products ---------------
    function visibleProducts() {
        if (productFilter === "own") return myProducts;
        if (productFilter === "others") return otherProducts;
        return products;
    }

    async function fetchProducts() {
        try {
            var token = localStorage.getItem("token");
            var [r1, r2, r3] = await Promise.all([
                fetch(API("/api/admins/getproducts"), { headers: { Authorization: "Bearer " + token } }),
                fetch(API("/api/admins/myproducts"), { headers: { Authorization: "Bearer " + token } }),
                fetch(API("/api/admins/otherproducts"), { headers: { Authorization: "Bearer " + token } }),
            ]);
            if (r1.ok) setProducts(await r1.json());
            if (r2.ok) setMyProducts(await r2.json());
            if (r3.ok) setOtherProducts(await r3.json());
        } catch (e) {}
    }

    function openProductModal(p) {
        if (p === null) {
            setProductForm({ id: null, name: "", category: "", subCategory: "", unit: "", amount: "", price: "", description: "", manufacturer: "", expiryDate: "" });
        } else {
            setProductForm({
                id: p.id,
                name: p.name || "",
                category: p.category || "",
                subCategory: p.sub_category || "",
                unit: p.unit || "",
                amount: String(p.amount || ""),
                price: String(p.price || ""),
                description: p.description || "",
                manufacturer: p.manufacturer || "",
                expiryDate: p.expiry_date || "",
            });
        }
        setProductModal(true);
    }

    async function saveProduct(e) {
        e.preventDefault();
        var amt = parseFloat(productForm.amount);
        var pr = parseFloat(productForm.price);
        if (!productForm.name || !productForm.category) { alert("Name & Category required"); return; }
        if (isNaN(amt) || amt <= 0) { alert("Amount must be positive number"); return; }
        if (isNaN(pr) || pr <= 0) { alert("Price must be positive number"); return; }

        try {
            var token = localStorage.getItem("token");
            var isEdit = productForm.id !== null;
            var url = isEdit ? API("/api/admins/editproduct/" + productForm.id) : API("/api/admins/addproduct");
            var method = isEdit ? "PUT" : "POST";
            var res = await fetch(url, {
                method: method,
                headers: { "Content-Type": "application/json", Authorization: "Bearer " + token },
                body: JSON.stringify({
                    name: productForm.name,
                    category: productForm.category,
                    subCategory: productForm.subCategory,
                    unit: productForm.unit,
                    amount: productForm.amount,
                    price: productForm.price,
                    description: productForm.description,
                    manufacturer: productForm.manufacturer,
                    expiryDate: productForm.expiryDate,
                }),
            });
            var data = await res.json();
            if (!res.ok) { alert(data.message || "Save failed"); return; }
            setProductModal(false);
            await fetchProducts();
            alert(isEdit ? "Product updated" : "Product added");
        } catch (e) { alert("Network error"); }
    }

    async function deleteProduct(id, createdByAdminId) {
        if (createdByAdminId !== user.id) { alert("You can delete only your own products"); return; }
        if (!window.confirm("Delete this product?")) return;
        try {
            var token = localStorage.getItem("token");
            var res = await fetch(API("/api/admins/deleteproduct/" + id), { method: "DELETE", headers: { Authorization: "Bearer " + token } });
            if (!res.ok) { var err = await res.json();
                alert(err.message || "Delete failed"); return; }
            await fetchProducts();
        } catch (e) { alert("Network error"); }
    }

    // --------------- Zone Admins ---------------
    async function fetchAdmins() {
        try {
            var token = localStorage.getItem("token");
            var res = await fetch(API("/api/admins/admins"), { headers: { Authorization: "Bearer " + token } });
            if (!res.ok) return;
            var data = await res.json();
            var zones = [];
            for (var i = 0; i < data.length; i++) {
                var a = data[i];
                if ((a.type === "admin" && a.role === "Zone") || a.role === "Zone") {
                    if (a.region_name === user.region_name) zones.push(a);
                }
            }
            setZoneAdmins(zones);
        } catch (e) {}
    }

    function openAdminModal(admin) {
        if (admin === null) setAdminForm({ id: null, fullName: "", phoneNumber: "", password: "", confirmPassword: "", zone_name: "" });
        else setAdminForm({ id: admin.id, fullName: admin.full_name || "", phoneNumber: admin.phone_number || "", password: "", confirmPassword: "", zone_name: admin.zone_name || "" });
        setAdminModal(true);
    }

    async function saveZoneAdmin(e) {
        e.preventDefault();
        if (!adminForm.fullName || !adminForm.phoneNumber) { alert("Full name & phone are required"); return; }
        var token = localStorage.getItem("token");
        if (adminForm.id === null) {
            // Create Zone admin — POST /register
            if (!adminForm.password) { alert("Password required for new admin"); return; }
            if (adminForm.password !== adminForm.confirmPassword) { alert("Passwords do not match"); return; }
            var payload = {
                fullName: adminForm.fullName,
                phoneNumber: adminForm.phoneNumber,
                password: adminForm.password,
                role: "Zone",
                region_name: user.region_name,
                zone_name: adminForm.zone_name,
            };
            var res = await fetch(API("/api/admins/register"), { method: "POST", headers: { "Content-Type": "application/json", Authorization: "Bearer " + token }, body: JSON.stringify(payload) });
            var data = await res.json();
            if (!res.ok) { alert(data.message || "Create failed"); return; }
            setAdminModal(false);
            await fetchAdmins();
            alert("Zone admin created");
        } else {
            // Update Zone admin — PUT /edit/:id
            var payloadU = {
                fullName: adminForm.fullName,
                phoneNumber: adminForm.phoneNumber,
                zone_name: adminForm.zone_name,
            };
            // Only include password if provided, along with confirm
            if (adminForm.password) {
                if (adminForm.password !== adminForm.confirmPassword) { alert("Passwords do not match"); return; }
                payloadU.password = adminForm.password;
                payloadU.confirmPassword = adminForm.confirmPassword;
            }
            var resU = await fetch(API("/api/admins/edit/" + adminForm.id), { method: "PUT", headers: { "Content-Type": "application/json", Authorization: "Bearer " + token }, body: JSON.stringify(payloadU) });
            var dataU = await resU.json();
            if (!resU.ok) { alert(dataU.message || "Update failed"); return; }
            setAdminModal(false);
            await fetchAdmins();
            alert("Zone admin updated");
        }
    }

    // --------------- Farmers (read-only) ---------------
    async function fetchFarmers() {
        try {
            var token = localStorage.getItem("token");
            var res = await fetch(API("/api/admins/farmers"), { headers: { Authorization: "Bearer " + token } });
            if (!res.ok) return;
            var data = await res.json();
            // Region sees all farmers in region
            var scoped = [];
            for (var i = 0; i < data.length; i++)
                if (data[i].region_name === user.region_name) scoped.push(data[i]);
            setFarmers(scoped);
        } catch (e) {}
    }

    // --------------- Reports ---------------
    async function fetchReports() {
        try {
            var token = localStorage.getItem("token");
            var [r1, r2] = await Promise.all([
                fetch(API("/api/admins/reports/my"), { headers: { Authorization: "Bearer " + token } }),
                fetch(API("/api/admins/reports"), { headers: { Authorization: "Bearer " + token } }),
            ]);
            if (r1.ok) setMyReports(await r1.json());
            if (r2.ok) {
                var rs = await r2.json();
                // Optional: Region may focus on their region scope; backend already does scope, keep as-is
                setScopeReports(rs);
            }
        } catch (e) {}
    }

    async function fetchReportStats() {
        try {
            var token = localStorage.getItem("token");
            var res = await fetch(API("/api/admins/reports/statistics"), { headers: { Authorization: "Bearer " + token } });
            if (res.ok) setReportStats(await res.json());
        } catch (e) {}
    }

    async function createReport(e) {
        e.preventDefault();
        if (!reportForm.title || !reportForm.body) { alert("Title & body required"); return; }
        try {
            var token = localStorage.getItem("token");
            var res = await fetch(API("/api/admins/reports"), { method: "POST", headers: { "Content-Type": "application/json", Authorization: "Bearer " + token }, body: JSON.stringify({ title: reportForm.title, body: reportForm.body }) });
            var data = await res.json();
            if (!res.ok) { alert(data.message || "Failed to create report"); return; }
            setReportForm({ title: "", body: "" });
            await fetchReports();
            alert("Report submitted");
        } catch (e) { alert("Network error"); }
    }

    async function updateReportStatus(id, status) {
        try {
            var token = localStorage.getItem("token");
            var res = await fetch(API("/api/admins/reports/" + id + "/status"), { method: "PUT", headers: { "Content-Type": "application/json", Authorization: "Bearer " + token }, body: JSON.stringify({ status: status }) });
            var data = await res.json();
            if (!res.ok) { alert(data.message || "Failed to update status"); return; }
            await fetchReports();
            await fetchReportStats();
        } catch (e) { alert("Network error"); }
    }

