    var counts = useMemo(function() {
        var c = { total: requests.length, pending: 0, approved: 0, accepted: 0, rejected: 0 };
        for (var i = 0; i < requests.length; i++) {
            var s = requests[i].region_status;
            if (!s || s === "Pending") c.pending++;
            else if (s === "Approved") c.approved++;
            else if (s === "Accepted") c.accepted++;
            else if (s === "Rejected") c.rejected++;
        }
        return c;
    }, [requests]);

