import React from 'react';

export default function DashboardPage({ user, requests, onViewRequest }) {
    const userId = user ? user.uid : 'Unknown';

    const getFilteredRequests = () => {
        if (!user || user.role === 'farmer') {
            return requests.filter(req => req.userId === userId);
        } else if (user.role === 'woreda') {
            return requests.filter(req => req.woreda === user.locationId);
        } else if (user.role === 'zone') {
            const woredaIds = requests.filter(req => req.zone === user.locationId).map(req => req.woreda);
            return requests.filter(req => woredaIds.includes(req.woreda));
        } else if (user.role === 'region') {
            const zoneIds = requests.filter(req => req.region === user.locationId).map(req => req.zone);
            const woredaIds = requests.filter(req => zoneIds.includes(req.zone)).map(req => req.woreda);
            return requests.filter(req => woredaIds.includes(req.woreda));
        }
        return requests;
    };

    const filteredRequests = getFilteredRequests();

    const renderRequestTable = (requests) => ( <
        div className = "bg-white p-4 rounded-lg shadow overflow-x-auto" >
        <
        table className = "min-w-full divide-y divide-gray-200" >
        <
        thead className = "bg-gray-50" >
        <
        tr >
        <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > ID < /th> <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Type < /th> <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Quantity < /th> <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Status < /th> <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Actions < /th> <
        /tr> <
        /thead> <
        tbody className = "bg-white divide-y divide-gray-200" > {
            requests.map(request => ( <
                tr key = { request.id } >
                <
                td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900" > { request.id.substring(0, 8) }... < /td> <
                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { request.requestType } < /td> <
                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { request.quantity } < /td> <
                td className = "px-6 py-4 whitespace-nowrap text-sm" >
                <
                span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${request.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' : request.status === 'Approved' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}` } > { request.status } <
                /span> <
                /td> <
                td className = "px-6 py-4 whitespace-nowrap text-sm font-medium" >
                <
                button onClick = {
                    () => onViewRequest(request) }
                className = "text-blue-600 hover:text-blue-900" > View < /button> <
                /td> <
                /tr>
            ))
        } <
        /tbody> <
        /table> <
        /div>
    );

    return ( <
        div className = "p-8 bg-white rounded-lg shadow-xl" >
        <
        h2 className = "text-3xl font-bold text-gray-800 mb-4" > Dashboard < /h2> <
        p className = "text-gray-700" > Welcome, { user && user.role }! < /p> <
        p className = "text-gray-700" > Your User ID is: { user && user.uid } < /p> <
        p className = "mt-4 text-gray-600" > This is a summary of all relevant requests based on your role and location. < /p> <
        div className = "mt-8" >
        <
        h3 className = "text-xl font-semibold text-gray-700 mb-4" > Requests({ filteredRequests.length }) < /h3> {
            filteredRequests.length > 0 ? renderRequestTable(filteredRequests) : < p className = "text-gray-500" > No requests found. < /p>} <
                /div> <
                /div>
        );
    }
