// components/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
        const [credentials, setCredentials] = useState({
            phoneNumber: '',
            password: ''
        });
        const [error, setError] = useState('');
        const [loading, setLoading] = useState(false);
        const navigate = useNavigate();

        const handleSubmit = async(e) => {
            e.preventDefault();
            setLoading(true);
            setError('');

            try {
                const response = await fetch('http://localhost:5000/api/admins/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(credentials),
                });

                const data = await response.json();

                if (response.ok) {
                    localStorage.setItem('token', data.token);
                    localStorage.setItem('user', JSON.stringify(data.user));
                    navigate('/dashboard');
                } else {
                    setError(data.message || 'Invalid credentials');
                }
            } catch (err) {
                setError('Network error. Please try again.');
            } finally {
                setLoading(false);
            }
        };

        return ( <
            div className = "login-container" >
            <
            div className = "login-form" >
            <
            h2 > Agricultural Admin System < /h2> <
            form onSubmit = { handleSubmit } >
            <
            div className = "form-group" >
            <
            label > Phone Number < /label> <
            input type = "tel"
            value = { credentials.phoneNumber }
            onChange = {
                (e) => setCredentials({...credentials, phoneNumber: e.target.value }) }
            required placeholder = "Enter your phone number" /
            >
            <
            /div> <
            div className = "form-group" >
            <
            label > Password < /label> <
            input type = "password"
            value = { credentials.password }
            onChange = {
                (e) => setCredentials({...credentials, password: e.target.value }) }
            required placeholder = "Enter your password" /
            >
            <
            /div> {
                error && < div className = "error-message" > { error } < /div>} <
                    button type = "submit"
                disabled = { loading } > { loading ? 'Logging in...' : 'Login' } <
                    /button> <
                    /form> <
                    /div> <
                    /div>
            );
        };

        export default Login;
