import React, { useState, useEffect } from 'react';
import './index.css';
import { useForm } from 'react-hook-form';

// ----------------------
// Mock Data (Simulates a database)
// ----------------------
const initialUsers = [{
    phoneNumber: '111-222-3333',
    password: 'password',
    role: 'Federal',
    fullName: 'Federal Admin',
    id: 'fed-1'
}, {
    phoneNumber: '444-555-6666',
    password: 'password',
    role: 'Region',
    fullName: 'Amhara Regional Admin',
    regionName: 'Amhara',
    id: 'reg-1'
}, {
    phoneNumber: '777-888-9999',
    password: 'password',
    role: 'Zone',
    fullName: 'North Shewa Zone Admin',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    id: 'zone-1'
}, {
    phoneNumber: '987-654-3210',
    password: 'password',
    role: 'Woreda',
    fullName: 'Kewet Woreda Admin',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    id: 'wor-1'
}, {
    phoneNumber: '987-654-3211',
    password: 'password',
    role: 'Kebele',
    fullName: 'Alem Ketema Kebele Rep',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    id: 'keb-1'
}, {
    phoneNumber: '123-456-7890',
    password: 'password',
    role: 'Farmer',
    fullName: 'Abebe Kebede',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    id: 'farm-1'
}];

const initialProducts = [{
    id: 'prod-1',
    name: 'Tractor',
    description: 'Used for plowing and land prep',
    category: 'Machinery',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet'
}, {
    id: 'prod-2',
    name: 'Fertilizer A',
    description: 'High-yield fertilizer for corn',
    category: 'Fertilizer',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet'
}, {
    id: 'prod-3',
    name: 'Harvester',
    description: 'Used for harvesting teff',
    category: 'Machinery',
    regionName: 'Amhara'
}];

const initialRequests = [{
    id: 'REQ12345',
    requesterName: 'Abebe Kebede',
    requesterId: 'farm-1',
    productName: 'Tractor',
    productPurpose: 'Land preparation',
    amount: 1,
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    status: 'Pending',
    previousApprovals: {
        Woreda: 'Approved'
    }
}];

// ----------------------
// Modal Component
// ----------------------
const Modal = ({ show, onClose, title, message }) => {
    if (!show) return null;
    return ( <
        div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
        <
        div className = "relative p-5 border w-96 shadow-lg rounded-xl bg-white" >
        <
        div className = "mt-3 text-center" >
        <
        h3 className = "text-xl leading-6 font-bold text-gray-900" > { title } < /h3> <
        div className = "mt-2 px-7 py-3" >
        <
        p className = "text-sm text-gray-500" > { message } < /p> <
        /div> <
        div className = "items-center px-4 py-3" >
        <
        button onClick = { onClose }
        className = "px-4 py-2 bg-blue-600 text-white text-base font-medium rounded-lg w-full shadow-md hover:bg-blue-700 transition" >
        OK <
        /button> <
        /div> <
        /div> <
        /div> <
        /div>
    );
};

// ----------------------
// Main App Component
// ----------------------
const App = () => {
        const [currentUser, setCurrentUser] = useState(null);
        const [users, setUsers] = useState(initialUsers);
        const [products, setProducts] = useState(initialProducts);
        const [requests, setRequests] = useState(initialRequests);
        const [showModal, setShowModal] = useState(false);
        const [modalContent, setModalContent] = useState({ title: '', message: '' });
        const [currentPage, setCurrentPage] = useState('dashboard');
        const [selectedRequest, setSelectedRequest] = useState(null);

        const handleLogin = (user) => {
            setCurrentUser(user);
            setCurrentPage('dashboard');
        };

        const handleLogout = () => {
            setCurrentUser(null);
        };

        const handleRegistration = (newUser) => {
            if (users.some(u => u.phoneNumber === newUser.phoneNumber)) {
                setModalContent({ title: 'Error', message: 'User with this phone number already exists.' });
                setShowModal(true);
                return;
            }

            const newUserWithId = {...newUser, id: `${newUser.role.toLowerCase()}-${users.length + 1}` };
            setUsers([...users, newUserWithId]);
            setModalContent({
                title: 'Success',
                message: `${newUser.fullName} has been registered as a ${newUser.role}. Their login details are Phone: ${newUser.phoneNumber}, Password: ${newUser.password}`
            });
            setShowModal(true);
        };

        const handleDeleteUser = (userId, role) => {
            setUsers(users.filter(u => u.id !== userId));
            setModalContent({ title: 'Success', message: `A ${role} account has been deleted.` });
            setShowModal(true);
        };

        const handleAddProduct = (newProduct) => {
            const newProductWithId = {...newProduct, id: `prod-${products.length + 1}` };
            setProducts([...products, newProductWithId]);
            setModalContent({ title: 'Success', message: 'New product added successfully.' });
            setShowModal(true);
        };

        const handleUpdateProduct = (updatedProduct) => {
            setProducts(products.map(p => p.id === updatedProduct.id ? updatedProduct : p));
            setModalContent({ title: 'Success', message: 'Product updated successfully.' });
            setShowModal(true);
        };

        const handleDeleteProduct = (productId) => {
            setProducts(products.filter(p => p.id !== productId));
            setModalContent({ title: 'Success', message: 'Product deleted successfully.' });
            setShowModal(true);
        };

        const handleApprovalDecision = (requestId, decision, feedback) => {
            setRequests(requests.map(req => {
                if (req.id === requestId) {
                    // Determine the next status based on the decision
                    const newStatus = decision === 'Approve' ? 'Approved' : 'Rejected';
                    // Add this approval to the request's history
                    const newPreviousApprovals = {
                        ...req.previousApprovals,
                        [currentUser.role]: newStatus
                    };

                    return {
                        ...req,
                        status: newStatus,
                        feedback,
                        previousApprovals: newPreviousApprovals
                    };
                }
                return req;
            }));
            setSelectedRequest(null); // Close the form
            setModalContent({ title: 'Decision Submitted', message: `The request has been ${decision.toLowerCase()}.` });
            setShowModal(true);
        };

        const handleProductRequest = (request) => {
            const newRequest = {
                ...request,
                id: `REQ${10000 + requests.length}`,
                status: 'Pending',
                previousApprovals: {}
            };
            setRequests([...requests, newRequest]);
            setModalContent({ title: 'Request Sent', message: 'Your product request has been submitted.' });
            setShowModal(true);
        };

        const renderMainContent = () => {
                if (!currentUser) {
                    return <LoginPage onLogin = { handleLogin }
                    users = { users }
                    />;
                }

                // Render a full dashboard with navigation
                return ( <
                        div className = "flex bg-gray-100 min-h-screen" >
                        <
                        Sidebar currentUser = { currentUser }
                        currentPage = { currentPage }
                        setCurrentPage = { setCurrentPage }
                        /> <
                        div className = "flex-1 overflow-auto" >
                        <
                        TopBar currentUser = { currentUser }
                        onLogout = { handleLogout }
                        /> <
                        main className = "p-8" > {
                            currentPage === 'dashboard' && < DashboardOverview requests = { requests }
                            currentUser = { currentUser }
                            users = { users }
                            />} {
                                currentPage === 'product-requests' && < RequestsTable requests = { requests }
                                currentUser = { currentUser }
                                setSelectedRequest = { setSelectedRequest }
                                />} {
                                    currentPage === 'approvals' && < RequestApprovalForm request = { selectedRequest }
                                    onDecision = { handleApprovalDecision }
                                    onClose = {
                                        () => setSelectedRequest(null) }
                                    />} {
                                        currentPage === 'master-data' && < MasterData currentUser = { currentUser }
                                        users = { users }
                                        products = { products }
                                        onRegister = { handleRegistration }
                                        onDeleteUser = { handleDeleteUser }
                                        onAddProduct = { handleAddProduct }
                                        onUpdateProduct = { handleUpdateProduct }
                                        onDeleteProduct = { handleDeleteProduct }
                                        />} {
                                            currentPage === 'reports' && ['Federal', 'Region'].includes(currentUser.role) && < Reports currentUser = { currentUser }
                                            requests = { requests }
                                            />} {
                                                currentPage === 'farmer-requests' && < FarmerRequestsTable requests = { requests }
                                                currentUser = { currentUser }
                                                />} <
                                                /main> <
                                                /div> <
                                                Modal show = { showModal }
                                                onClose = {
                                                    () => setShowModal(false) }
                                                title = { modalContent.title }
                                                message = { modalContent.message }
                                                /> <
                                                /div>
                                            );
                                        };

                                        if (!currentUser) {
                                            return <LoginPage onLogin = { handleLogin }
                                            users = { users }
                                            />;
                                        }

                                        // For farmers, render a simpler view
                                        if (currentUser.role === 'Farmer') {
                                            return ( <
                                                div className = "bg-gray-100 min-h-screen font-sans antialiased" >
                                                <
                                                TopBar currentUser = { currentUser }
                                                onLogout = { handleLogout }
                                                /> <
                                                main className = "p-8" >
                                                <
                                                FarmerDashboard currentUser = { currentUser }
                                                products = { products.filter(p => p.kebeleName === currentUser.kebeleName) }
                                                requests = { requests.filter(r => r.requesterId === currentUser.id) }
                                                onRequest = { handleProductRequest }
                                                /> <
                                                /main> <
                                                /div>
                                            );
                                        }

                                        // For all other users (Admins)
                                        return renderMainContent();
                                    };

                                    // ----------------------
                                    // Sidebar Component
                                    // ----------------------
                                    const Sidebar = ({ currentUser, currentPage, setCurrentPage }) => {
                                        const isAdmin = currentUser.role !== 'Farmer';
                                        const menuItems = {
                                            'dashboard': 'ðŸ  Dashboard',
                                            'product-requests': 'ðŸ“¦ Product Requests',
                                            'approvals': 'âœ… Approvals',
                                            'master-data': 'ðŸ›  Master Data',
                                            'reports': 'ðŸ“Š Reports'
                                        };

                                        // Filter menu based on user role
                                        const filteredMenuItems = Object.keys(menuItems).filter(key => {
                                            if (key === 'reports' && !['Federal', 'Region'].includes(currentUser.role)) {
                                                return false;
                                            }
                                            return true;
                                        });

                                        return ( <
                                            aside className = "w-64 min-h-screen bg-gray-800 text-gray-100 flex flex-col" >
                                            <
                                            div className = "p-4 text-center text-2xl font-bold border-b border-gray-700" >
                                            FMS <
                                            /div> <
                                            nav className = "flex-1" >
                                            <
                                            ul className = "space-y-2 p-4" > {
                                                filteredMenuItems.map(key => ( <
                                                    li key = { key } >
                                                    <
                                                    button onClick = {
                                                        () => setCurrentPage(key) }
                                                    className = { `w-full text-left p-3 rounded-lg transition-colors duration-200 ${currentPage === key ? 'bg-gray-700 font-bold' : 'hover:bg-gray-700'}` } >
                                                    { menuItems[key] } <
                                                    /button> <
                                                    /li>
                                                ))
                                            } <
                                            /ul> <
                                            /nav> <
                                            /aside>
                                        );
                                    };

                                    // ----------------------
                                    // TopBar Component
                                    // ----------------------
                                    const TopBar = ({ currentUser, onLogout }) => ( <
                                        header className = "flex justify-between items-center p-4 bg-white border-b shadow-sm sticky top-0 z-10" >
                                        <
