                                                                                            req.status === 'Pending' && < button onClick = {
                                                                                                () => setSelectedRequest(req) }
                                                                                            className = "text-green-600 hover:text-green-900" > âœ”ï¸Approve / Reject < /button>} <
                                                                                                /td> <
                                                                                                /tr>
                                                                                        ))
                                                                                } <
                                                                                /tbody> <
                                                                                /table> <
                                                                                /div> <
                                                                                /div>
                                                                            );
                                                                        };

                                                                        // ----------------------
                                                                        // Request Approval Form Component
                                                                        // ----------------------
                                                                        const RequestApprovalForm = ({ request, onDecision, onClose }) => {
                                                                            const { register, handleSubmit } = useForm();

                                                                            if (!request) {
