                                                                            );
                                                                        };

                                                                        // ----------------------
                                                                        // Master Data Management Component
                                                                        // ----------------------
                                                                        const MasterData = ({ currentUser, users, products, onRegister, onDeleteUser, onAddProduct, onUpdateProduct, onDeleteProduct }) => {
                                                                            const { register, handleSubmit, reset, setValue, watch } = useForm();
                                                                            const [isEditingProduct, setIsEditingProduct] = useState(false);
                                                                            const [editingProductId, setEditingProductId] = useState(null);

                                                                            const onSubmitProduct = (data) => {
                                                                                if (isEditingProduct) {
                                                                                    onUpdateProduct({...data, id: editingProductId });
                                                                                    setIsEditingProduct(false);
                                                                                    setEditingProductId(null);
                                                                                } else {
                                                                                    onAddProduct({
                                                                                        ...data,
                                                                                        // Add location based on admin level
                                                                                        ...(currentUser.regionName && { regionName: currentUser.regionName }),
                                                                                        ...(currentUser.zoneName && { zoneName: currentUser.zoneName }),
                                                                                        ...(currentUser.woredaName && { woredaName: currentUser.woredaName }),
                                                                                        ...(currentUser.kebeleName && { kebeleName: currentUser.kebeleName }),
                                                                                    });
                                                                                }
                                                                                reset();
                                                                            };

                                                                            const handleEditProductClick = (product) => {
                                                                                setValue('name', product.name);
                                                                                setValue('description', product.description);
                                                                                setValue('category', product.category);
                                                                                setIsEditingProduct(true);
                                                                                setEditingProductId(product.id);
                                                                            };

                                                                            const handleDeleteProductClick = (productId) => {
                                                                                onDeleteProduct(productId);
                                                                            };

                                                                            // Filtered products for the current admin's level
                                                                            const productsToManage = products.filter(p => {
                                                                                switch (currentUser.role) {
                                                                                    case 'Federal':
                                                                                        return true;
                                                                                    case 'Region':
                                                                                        return p.regionName === currentUser.regionName;
                                                                                    case 'Zone':
                                                                                        return p.zoneName === currentUser.zoneName;
                                                                                    case 'Woreda':
                                                                                        return p.woredaName === currentUser.woredaName;
                                                                                    case 'Kebele':
                                                                                        return p.kebeleName === currentUser.kebeleName;
                                                                                    default:
                                                                                        return false;
                                                                                }
                                                                            });

                                                                            return ( <
                                                                                div className = "space-y-8" >
                                                                                <
                                                                                h1 className = "text-3xl font-bold text-gray-800" > Master Data Management < /h1>

