                                                                                type = "text"
                                                                                placeholder = "Product Name"
                                                                                className = "p-2 border rounded-md" / >
                                                                                <
                                                                                input {...register("description", { required: true }) }
                                                                                type = "text"
                                                                                placeholder = "Description and category"
                                                                                className = "p-2 border rounded-md" / >
                                                                                <
                                                                                input {...register("category", { required: true }) }
                                                                                type = "text"
                                                                                placeholder = "Category"
                                                                                className = "p-2 border rounded-md" / >
                                                                                <
                                                                                /div> <
                                                                                button type = "submit"
                                                                                className = "w-full px-4 py-2 bg-green-600 text-white font-medium rounded-lg hover:bg-green-700 transition shadow-md" > { isEditingProduct ? 'Update Product Type' : '+ Add Product Type' } <
                                                                                /button> <
                                                                                /form> <
                                                                                div className = "overflow-x-auto" >
                                                                                <
                                                                                table className = "min-w-full divide-y divide-gray-200" >
                                                                                <
                                                                                thead className = "bg-gray-50" >
                                                                                <
                                                                                tr >
                                                                                <
                                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > ID < /th> <
                                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Name < /th> <
                                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Description & Category < /th> <
                                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Actions < /th> <
                                                                                /tr> <
                                                                                /thead> <
                                                                                tbody className = "bg-white divide-y divide-gray-200" > {
                                                                                    productsToManage.map(p => ( <
                                                                                        tr key = { p.id } >
                                                                                        <
                                                                                        td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900" > { p.id } < /td> <
