                                                                                        button onClick = {
                                                                                            () => handleDeleteProductClick(p.id) }
                                                                                        className = "text-red-600 hover:text-red-900" > ðŸ—‘Delete < /button> <
                                                                                        /td> <
                                                                                        /tr>
                                                                                    ))
                                                                                } <
                                                                                /tbody> <
                                                                                /table> <
                                                                                /div> <
                                                                                /div>

                                                                                { /* Location & User Management (Simplified for now) */ } <
                                                                                div className = "bg-white p-6 rounded-xl shadow-md" >
                                                                                <
                                                                                h2 className = "text-2xl font-bold mb-4" > User Management < /h2> {
                                                                                    currentUser.role === 'Federal' && ( <
                                                                                        div className = "p-4 border rounded-lg" >
                                                                                        <
                                                                                        h3 className = "text-xl font-semibold mb-2" > Create Regional Admin < /h3> <
                                                                                        RegistrationForm role = "Region"
                                                                                        onRegister = { onRegister }
                                                                                        locationKeys = {
                                                                                            ['regionName'] }
                                                                                        /> <
                                                                                        UserManagement users = { users }
                                                                                        role = "Federal"
                                                                                        onDeleteUser = { onDeleteUser }
                                                                                        /> <
                                                                                        /div>
                                                                                    )
                                                                                } {
                                                                                    currentUser.role === 'Region' && ( <
                                                                                        div className = "p-4 border rounded-lg" >
                                                                                        <
                                                                                        h3 className = "text-xl font-semibold mb-2" > Create Zone Admin < /h3> <
                                                                                        RegistrationForm role = "Zone"
                                                                                        onRegister = { onRegister }
                                                                                        locationKeys = {
                                                                                            ['zoneName'] }
                                                                                        currentUserLocation = { currentUser }
                                                                                        /> <
                                                                                        UserManagement users = { users }
                                                                                        role = "Region"
                                                                                        onDeleteUser = { onDeleteUser }
                                                                                        /> <
                                                                                        /div>
                                                                                    )
                                                                                } {
                                                                                    currentUser.role === 'Zone' && ( <
                                                                                        div className = "p-4 border rounded-lg" >
                                                                                        <
                                                                                        h3 className = "text-xl font-semibold mb-2" > Create Woreda Admin < /h3> <
                                                                                        RegistrationForm role = "Woreda"
                                                                                        onRegister = { onRegister }
                                                                                        locationKeys = {
                                                                                            ['woredaName'] }
                                                                                        currentUserLocation = { currentUser }
                                                                                        /> <
                                                                                        UserManagement users = { users }
                                                                                        role = "Zone"
                                                                                        onDeleteUser = { onDeleteUser }
                                                                                        /> <
                                                                                        /div>
                                                                                    )
                                                                                } {
                                                                                    currentUser.role === 'Woreda' && ( <
                                                                                        div className = "p-4 border rounded-lg" >
                                                                                        <
                                                                                        h3 className = "text-xl font-semibold mb-2" > Create Kebele Rep < /h3> <
                                                                                        RegistrationForm role = "Kebele"
                                                                                        onRegister = { onRegister }
                                                                                        locationKeys = {
                                                                                            ['kebeleName'] }
                                                                                        currentUserLocation = { currentUser }
                                                                                        /> <
                                                                                        UserManagement users = { users }
                                                                                        role = "Woreda"
                                                                                        onDeleteUser = { onDeleteUser }
                                                                                        /> <
                                                                                        /div>
                                                                                    )
                                                                                } {
                                                                                    currentUser.role === 'Kebele' && ( <
                                                                                        div className = "p-4 border rounded-lg" >
                                                                                        <
                                                                                        h3 className = "text-xl font-semibold mb-2" > Create Farmer Account < /h3> <
                                                                                        RegistrationForm role = "Farmer"
                                                                                        onRegister = { onRegister }
                                                                                        currentUserLocation = { currentUser }
                                                                                        /> <
                                                                                        /div>
                                                                                    )
                                                                                } <
                                                                                /div> <
                                                                                /div>
                                                                            );
                                                                        };

                                                                        // ----------------------
                                                                        // Reports Component (Federal & Region only)
                                                                        // ----------------------
                                                                        const Reports = ({ currentUser, requests }) => {
                                                                            // Role-based filtering of requests for reports
                                                                            const filteredRequests = requests.filter(req => {
                                                                                if (currentUser.role === 'Federal') return true;
                                                                                if (currentUser.role === 'Region' && req.regionName === currentUser.regionName) return true;
                                                                                return false;
                                                                            });

                                                                            const requestsByStatus = filteredRequests.reduce((acc, req) => {
                                                                                acc[req.status] = (acc[req.status] || 0) + 1;
                                                                                return acc;
                                                                            }, {});

                                                                            // Mock data for charts
                                                                            const chartData = {
                                                                                requestsByRegion: {
                                                                                    'Amhara': 50,
                                                                                    'Oromia': 70
                                                                                },
                                                                                productUsage: {
                                                                                    'Tractor': 40,
                                                                                    'Fertilizer': 60,
                                                                                    'Harvester': 20
                                                                                },
                                                                            };

                                                                            return ( <
                                                                                div className = "space-y-8" >
                                                                                <
                                                                                h1 className = "text-3xl font-bold text-gray-800" > Reports < /h1> <
                                                                                div className = "grid grid-cols-1 md:grid-cols-2 gap-6" >
                                                                                <
                                                                                div className = "bg-white p-6 rounded-xl shadow-md" >
                                                                                <
                                                                                h2 className = "text-xl font-bold mb-4" > Requests by Status < /h2> <
                                                                                pre > { JSON.stringify(requestsByStatus, null, 2) } < /pre> <
                                                                                /div> <
                                                                                div className = "bg-white p-6 rounded-xl shadow-md" >
                                                                                <
                                                                                h2 className = "text-xl font-bold mb-4" > Requests by Region(Mock) < /h2> <
                                                                                pre > { JSON.stringify(chartData.requestsByRegion, null, 2) } < /pre> <
                                                                                /div> <
                                                                                div className = "bg-white p-6 rounded-xl shadow-md" >
                                                                                <
                                                                                h2 className = "text-xl font-bold mb-4" > Product Type Usage(Mock) < /h2> <
                                                                                pre > { JSON.stringify(chartData.productUsage, null, 2) } < /pre> <
                                                                                /div> <
                                                                                /div> <
                                                                                /div>
                                                                            );
                                                                        };


                                                                        // ----------------------
                                                                        // Registration and User/Product Management components (reused from previous response)
                                                                        // ----------------------
                                                                        const RegistrationForm = ({ onRegister, role, locationKeys = [], currentUserLocation = {} }) => {
                                                                            const { register, handleSubmit, reset } = useForm();

                                                                            const onSubmit = (data) => {
                                                                                const newUser = {
                                                                                    ...data,
                                                                                    role,
                                                                                    ...currentUserLocation,
                                                                                };
                                                                                onRegister(newUser);
                                                                                reset();
                                                                            };

                                                                            return ( <
                                                                                form onSubmit = { handleSubmit(onSubmit) }
                                                                                className = "space-y-4" >
                                                                                <
                                                                                div >
                                                                                <
                                                                                label className = "block text-sm font-medium text-gray-700" > Full Name < /label> <
                                                                                input {...register("fullName", { required: true }) }
                                                                                type = "text"
                                                                                className = "mt-1 block w-full p-2 border rounded-md" / >
                                                                                <
                                                                                /div> <
                                                                                div >
                                                                                <
                                                                                label className = "block text-sm font-medium text-gray-700" > Phone Number < /label> <
                                                                                input {...register("phoneNumber", { required: true }) }
                                                                                type = "text"
                                                                                className = "mt-1 block w-full p-2 border rounded-md" / >
                                                                                <
                                                                                /div> <
                                                                                div >
                                                                                <
                                                                                label className = "block text-sm font-medium text-gray-700" > Password < /label> <
                                                                                input {...register("password", { required: true }) }
                                                                                type = "password"
                                                                                className = "mt-1 block w-full p-2 border rounded-md" / >
                                                                                <
                                                                                /div> {
                                                                                    locationKeys.map(key => ( <
                                                                                        div key = { key } >
                                                                                        <
                                                                                        label className = "block text-sm font-medium text-gray-700" > { key.replace('Name', '').replace(/([A-Z])/g, ' $1').trim() }
                                                                                        Name < /label> <
                                                                                        input {...register(key, { required: true }) }
                                                                                        type = "text"
                                                                                        className = "mt-1 block w-full p-2 border rounded-md" /
                                                                                        >
                                                                                        <
                                                                                        /div>
                                                                                    ))
                                                                                } <
                                                                                button type = "submit"
                                                                                className = "w-full px-4 py-2 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition shadow-md" >
                                                                                Create { role }
                                                                                Account <
                                                                                /button> <
                                                                                /form>
                                                                            );
                                                                        };

                                                                        const UserManagement = ({ users, role, onDeleteUser }) => {
                                                                            const nextLevelRole = {
                                                                                'Federal': 'Region',
                                                                                'Region': 'Zone',
                                                                                'Zone': 'Woreda',
                                                                                'Woreda': 'Kebele'
                                                                            }[role];

                                                                            const usersToManage = users.filter(user => user.role === nextLevelRole);

                                                                            return ( <
                                                                                div className = "bg-white p-6 rounded-xl shadow-md mt-8" >
                                                                                <
                                                                                h2 className = "text-2xl font-bold mb-4" > Manage { nextLevelRole }
                                                                                Admins < /h2> {
                                                                                    usersToManage.length > 0 ? ( <
                                                                                        ul className = "divide-y divide-gray-200" > {
                                                                                            usersToManage.map(user => ( <
                                                                                                li key = { user.id }
                                                                                                className = "py-4 flex justify-between items-center" >
                                                                                                <
                                                                                                div >
                                                                                                <
                                                                                                div className = "text-lg font-medium text-gray-900" > { user.fullName } < /div> <
                                                                                                div className = "text-sm text-gray-500" > Phone: { user.phoneNumber } < /div> <
                                                                                                /div> <
                                                                                                button onClick = {
                                                                                                    () => onDeleteUser(user.id, user.role) }
                                                                                                className = "px-4 py-2 bg-red-600 text-white font-medium rounded-lg hover:bg-red-700 transition shadow-md" >
                                                                                                Delete <
                                                                                                /button> <
                                                                                                /li>
                                                                                            ))
                                                                                        } <
                                                                                        /ul>
                                                                                    ) : ( <
                                                                                        p className = "text-gray-500" > No { nextLevelRole }
                                                                                        admins to display. < /p>
                                                                                    )
                                                                                } <
                                                                                /div>
                                                                            );
                                                                        };


                                                                        const FarmerDashboard = ({ currentUser, products, requests, onRequest }) => {
                                                                            const { register, handleSubmit, reset } = useForm();
                                                                            const onSubmit = (data) => {
                                                                                const product = products.find(p => p.id === data.productId);
                                                                                if (product) {
                                                                                    onRequest({
                                                                                        requesterId: currentUser.id,
                                                                                        requesterName: currentUser.fullName,
                                                                                        productName: product.name,
                                                                                        productPurpose: data.productPurpose,
                                                                                        amount: data.amount,
                                                                                        regionName: currentUser.regionName,
                                                                                        zoneName: currentUser.zoneName,
                                                                                        woredaName: currentUser.woredaName,
                                                                                        kebeleName: currentUser.kebeleName,
                                                                                    });
                                                                                    reset();
                                                                                }
                                                                            };

                                                                            return ( <
                                                                                div className = "p-8" >
                                                                                <
                                                                                h1 className = "text-3xl font-bold text-gray-800 mb-6" > Farmer Dashboard < /h1> <
                                                                                div className = "grid grid-cols-1 md:grid-cols-2 gap-8" >
                                                                                <
                                                                                div className = "bg-white p-6 rounded-xl shadow-md" >
                                                                                <
                                                                                h2 className = "text-2xl font-bold mb-4" > Request a Product < /h2> <
                                                                                form onSubmit = { handleSubmit(onSubmit) }
                                                                                className = "space-y-4" >
                                                                                <
                                                                                div >
                                                                                <
                                                                                label className = "block text-sm font-medium text-gray-700" > Select Product < /label> <
                                                                                select {...register("productId", { required: true }) }
                                                                                className = "mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm" >
                                                                                <
                                                                                option value = "" > --Select a product-- < /option> {
                                                                                    products.map(product => ( <
                                                                                        option key = { product.id }
                                                                                        value = { product.id } > { product.name } < /option>
                                                                                    ))
                                                                                } <
                                                                                /select> <
                                                                                /div> <
                                                                                div >
                                                                                <
                                                                                label className = "block text-sm font-medium text-gray-700" > Purpose < /label> <
                                                                                input {...register("productPurpose", { required: true }) }
                                                                                type = "text"
                                                                                className = "mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm"
                                                                                placeholder = "e.g., Land preparation" / >
                                                                                <
                                                                                /div> <
                                                                                div >
                                                                                <
                                                                                label className = "block text-sm font-medium text-gray-700" > Amount < /label> <
                                                                                input {...register("amount", { required: true }) }
                                                                                type = "number"
                                                                                min = "1"
                                                                                className = "mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm"
                                                                                placeholder = "e.g., 1" / >
                                                                                <
                                                                                /div> <
                                                                                button type = "submit"
                                                                                className = "w-full px-4 py-2 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition shadow-md" >
                                                                                Submit Request <
                                                                                /button> <
                                                                                /form> <
                                                                                /div> <
                                                                                div className = "bg-white p-6 rounded-xl shadow-md" >
                                                                                <
                                                                                h2 className = "text-2xl font-bold mb-4" > My Request Status < /h2> {
                                                                                    requests.length > 0 ? ( <
                                                                                        ul className = "divide-y divide-gray-200" > {
                                                                                            requests.map(request => ( <
                                                                                                li key = { request.id }
                                                                                                className = "py-4 flex justify-between items-center" >
                                                                                                <
                                                                                                div >
                                                                                                <
                                                                                                div className = "text-lg font-medium text-gray-900" > { request.productName } < /div> <
                                                                                                div className = "text-sm text-gray-500" > Status: { request.status } < /div> <
                                                                                                /div> <
                                                                                                span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${request.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' : request.status === 'Approved' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}` } > { request.status } <
                                                                                                /span> <
                                                                                                /li>
                                                                                            ))
                                                                                        } <
                                                                                        /ul>
                                                                                    ) : ( <
                                                                                        p className = "text-gray-500" > You have no pending requests. < /p>
                                                                                    )
                                                                                } <
                                                                                /div> <
                                                                                /div> <
                                                                                /div>
                                                                            );
                                                                        };

                                                                        export default App;
