import React, { useState, useEffect } from 'react';
import { Layout, Menu, Table, Tag, Space, Button, message } from 'antd';
import {
    DashboardOutlined,
    UserOutlined,
    FileOutlined,
    LogoutOutlined,
    CheckCircleOutlined,
    CloseCircleOutlined
} from '@ant-design/icons';
import api from '../../api';
import './FederalDashboard.css';

const { Header, Sider, Content } = Layout;

const FederalDashboard = ({ currentUser }) => {
    const [collapsed, setCollapsed] = useState(false);
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchRequests();
    }, []);
    const fetchRequests = async() => {
        try {
            const response = await api.get('/admin/requests'); // This should work if your baseURL is correct
            setRequests(response.data); // Changed from response.data.requests
            setLoading(false);
        } catch (error) {
            message.error('Failed to fetch requests');
            setLoading(false);
        }
    };

    const handleApprove = async(requestId) => {
        try {
            await api.put(`/admin/requests/${requestId}/status`, {
                status: 'accepted' // Changed from 'approve' to match our route
            });
            message.success('Request approved successfully');
            fetchRequests();
        } catch (error) {
            message.error('Failed to approve request');
        }
    };

    const handleReject = async(requestId) => {
        try {
            await api.put(`/admin/requests/${requestId}/status`, {
                status: 'rejected' // Changed from 'reject' to match our route
            });
            message.success('Request rejected successfully');
            fetchRequests();
        } catch (error) {
            message.error('Failed to reject request');
        }
    };
    const handleLogout = () => {
        localStorage.removeItem('token');
        window.location.href = '/login';
    };

    const columns = [{
            title: 'Request ID',
            dataIndex: 'id',
            key: 'id',
        },
        {
            title: 'Farmer',
            dataIndex: 'farmerName',
            key: 'farmerName',
        },
        {
            title: 'Product',
            dataIndex: 'productName',
            key: 'productName',
        },
        {
            title: 'Quantity',
            dataIndex: 'quantity',
            key: 'quantity',
        },
        {
            title: 'Status',
            dataIndex: 'status',
            key: 'status',
            render: status => ( <
                Tag color = { status === 'approved' ? 'green' : status === 'pending' ? 'orange' : 'red' } > { status.toUpperCase() } <
                /Tag>
            ),
        },
        {
            title: 'Action',
            key: 'action',
            render: (_, record) => ( <
                Space size = "middle" > {
                    record.status === 'pending' && ( <
                        >
                        <
                        Button type = "primary"
                        icon = { < CheckCircleOutlined / > }
                        onClick = {
                            () => handleApprove(record.id)
                        } >
                        Approve <
                        /Button> <
                        Button danger icon = { < CloseCircleOutlined / > }
                        onClick = {
                            () => handleReject(record.id)
                        } >
                        Reject <
                        /Button> < / >
                    )
                } <
                /Space>
            ),
        },
    ];

    return ( <
        Layout style = {
            { minHeight: '100vh' }
        } >
        <
        Sider collapsible collapsed = { collapsed }
        onCollapse = { setCollapsed } >
        <
        div className = "logo" > Federal Admin < /div> <
        Menu theme = "dark"
        defaultSelectedKeys = {
            ['1']
        }
        mode = "inline" >
        <
        Menu.Item key = "1"
        icon = { < DashboardOutlined / > } >
        Dashboard <
        /Menu.Item> <
        Menu.Item key = "2"
        icon = { < UserOutlined / > } >
        Farmers <
        /Menu.Item> <
        Menu.Item key = "3"
        icon = { < FileOutlined / > } >
        Reports <
        /Menu.Item> <
        Menu.Item key = "4"
        icon = { < LogoutOutlined / > }
        onClick = { handleLogout } >
        Logout <
        /Menu.Item> < /
        Menu > <
        /Sider> <
        Layout className = "site-layout" >
        <
        Header className = "site-layout-background"
        style = {
            { padding: 0 }
        }
        /> <
        Content style = {
            { margin: '0 16px' }
        } >
        <
        div className = "site-layout-background"
        style = {
            { padding: 24, minHeight: 360 }
        } >
        <
        h2 > Request Approvals < /h2> <
        Table columns = { columns }
        dataSource = { requests }
        rowKey = "id"
        loading = { loading }
        /> < /
        div > <
        /Content> < /
        Layout > <
        /Layout>
    );
};

export default FederalDashboard;
