import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';

// The following imports are for Firebase, which you requested to avoid.
// They are commented out to demonstrate where they would go if you were to use Firebase.
// import { initializeApp } from 'firebase/app';
// import { getAuth, signInAnonymously, signInWithCustomToken } from 'firebase/auth';
// import { getFirestore, collection, getDocs, onSnapshot, doc, updateDoc, addDoc } from 'firebase/firestore';

// Tailwind CSS is assumed to be available from the setup.
// No import is needed here.

// ----------------------
// Mock Data (Simulates a MySQL database)
// ----------------------
// This data is used to simulate a backend for a local project.
// In a real-world scenario, you would replace these arrays with API calls
// to your MySQL database server.
const initialUsers = [
    { phoneNumber: '111-222-3333', password: 'password', role: 'Federal', fullName: 'Federal Admin', id: 'fed-1' },
    { phoneNumber: '444-555-6666', password: 'password', role: 'Region', fullName: 'Amhara Regional Admin', regionName: 'Amhara', id: 'reg-1' },
    { phoneNumber: '777-888-9999', password: 'password', role: 'Zone', fullName: 'North Shewa Zone Admin', regionName: 'Amhara', zoneName: 'North Shewa', id: 'zone-1' },
    { phoneNumber: '987-654-3210', password: 'password', role: 'Woreda', fullName: 'Kewet Woreda Admin', regionName: 'Amhara', zoneName: 'North Shewa', woredaName: 'Kewet', id: 'wor-1' },
    { phoneNumber: '123-456-7890', password: 'password', role: 'Farmer', fullName: 'Abebe Kebede', regionName: 'Amhara', zoneName: 'North Shewa', woredaName: 'Kewet', kebeleName: 'Alem Ketema', id: 'far-1' },
    { phoneNumber: '098-765-4321', password: 'password', role: 'Farmer', fullName: 'Chala Debele', regionName: 'Amhara', zoneName: 'North Shewa', woredaName: 'Kewet', kebeleName: 'Alaltu', id: 'far-2' },
    { phoneNumber: '555-555-5555', password: 'password', role: 'Kebele', fullName: 'Alaltu Kebele Admin', regionName: 'Amhara', zoneName: 'North Shewa', woredaName: 'Kewet', kebeleName: 'Alaltu', id: 'keb-1' },
];

const initialRequests = [
    { id: 'req-1', productName: 'Fertilizer', productCategory: 'Chemical Input Supply', amount: 50, status: 'Pending', farmerId: 'far-1' },
    { id: 'req-2', productName: 'Seed', productCategory: 'Biological Input Supply', amount: 100, status: 'Approved', farmerId: 'far-2' },
    { id: 'req-3', productName: 'Pesticide', productCategory: 'Chemical Input Supply', amount: 25, status: 'Rejected', farmerId: 'far-1' },
];

const initialProducts = [
    { id: 'prod-1', name: 'Fertilizer', description: 'Urea Fertilizer', category: 'Chemical Input Supply' },
    { id: 'prod-2', name: 'Seed', description: 'Teff Seed', category: 'Biological Input Supply' },
];

const App = () => {
        const [currentUser, setCurrentUser] = useState(null);
        const [view, setView] = useState('login');
        const [users, setUsers] = useState(initialUsers);
        const [requests, setRequests] = useState(initialRequests);
        const [products, setProducts] = useState(initialProducts);
        const [showModal, setShowModal] = useState(false);
        const [modalContent, setModalContent] = useState({ title: '', message: '' });

        // Handles user authentication
        const handleLogin = (data) => {
            const user = users.find(u => u.phoneNumber === data.phoneNumber && u.password === data.password);
            if (user) {
                setCurrentUser(user);
                setView('dashboard');
            } else {
                setModalContent({ title: 'Login Failed', message: 'Invalid phone number or password.' });
                setShowModal(true);
            }
        };

        // Submits a new request for a farmer
        const submitRequest = (data) => {
            const newRequest = {
                id: `req-${requests.length + 1}`,
                ...data,
                status: 'Pending',
                farmerId: currentUser.id
            };
            setRequests([...requests, newRequest]);
            setModalContent({ title: 'Request Submitted', message: 'Your request has been submitted and is awaiting approval.' });
            setShowModal(true);
        };

        // Handles admin actions for requests (approve/reject)
        const handleRequestAction = (id, newStatus) => {
            const updatedRequests = requests.map(req =>
                req.id === id ? {...req, status: newStatus } : req
            );
            setRequests(updatedRequests);
            setModalContent({ title: 'Action Successful', message: `Request ${id} has been ${newStatus.toLowerCase()}.` });
            setShowModal(true);
        };

        // Handles creating a new product for an admin
        const createProduct = (data) => {
            const newProduct = {
                id: `prod-${products.length + 1}`,
                ...data,
            };
            setProducts([...products, newProduct]);
            setModalContent({ title: 'Product Saved', message: `Product "${data.name}" has been created.` });
            setShowModal(true);
        };

        // Renders a generic modal for messages and alerts
        const Modal = ({ show, onClose, title, message }) => {
            if (!show) return null;
            return ( <
                div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center" >
                <
                div className = "p-8 border w-96 shadow-lg rounded-md bg-white" >
                <
                h3 className = "text-xl font-bold mb-4" > { title } < /h3> <
                p > { message } < /p> <
                div className = "mt-4 flex justify-end" >
                <
                button onClick = { onClose }
                className = "px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700" >
                Close <
                /button> <
                /div> <
                /div> <
                /div>
            );
        };

        // Header component
        const Header = () => ( <
            header className = "bg-gray-800 text-white p-4 shadow-lg flex justify-between items-center" >
            <
            h1 className = "text-2xl font-bold" > Farm Input Supply System < /h1> {
                currentUser && ( <
                    div className = "flex items-center space-x-4" >
                    <
                    span className = "text-sm" > Welcome, { currentUser.fullName }! < /span> <
                    button onClick = {
                        () => { setCurrentUser(null);
                            setView('login'); } }
                    className = "text-sm text-blue-300 hover:underline" >
                    Logout <
                    /button> <
                    /div>
                )
            } <
            /header>
        );

        // Login page component
        const Login = () => {
                const { register, handleSubmit, formState: { errors } } = useForm();
                return ( <
                        div className = "flex items-center justify-center min-h-screen bg-gray-100" >
                        <
                        div className = "bg-white p-8 rounded-lg shadow-xl w-full max-w-md" >
                        <
                        h2 className = "text-3xl font-bold text-center mb-6 text-gray-800" > Login < /h2> <
                        form onSubmit = { handleSubmit(handleLogin) }
                        className = "space-y-6" >
                        <
                        div >
                        <
                        label className = "block text-gray-700 text-sm font-medium mb-1" > Phone Number < /label> <
                        input type = "tel"
                        placeholder = "e.g. 0912345678" {...register('phoneNumber', {
                                required: 'Phone number is required',
                                pattern: {
                                    value: /^\d{10}$/,
                                    message: 'Invalid phone number format (e.g., 0912345678)',
                                },
                            })
                        }
                        className = "w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500" /
                        > {
                            errors.phoneNumber && < p className = "text-red-500 text-sm mt-1" > { errors.phoneNumber.message } < /p>} <
                            /div> <
                            div >
                            <
                            label className = "block text-gray-700 text-sm font-medium mb-1" > Password < /label> <
                            input
                            type = "password"
                            placeholder = "Password" {...register('password', { required: 'Password is required' }) }
                            className = "w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500" /
                            > {
                                errors.password && < p className = "text-red-500 text-sm mt-1" > { errors.password.message } < /p>} <
                                /div> <
                                button type = "submit"
                                className = "w-full bg-blue-600 text-white p-3 rounded-lg font-bold hover:bg-blue-700 transition-colors" >
                                Log In <
                                /button> <
                                /form> <
                                /div> <
                                /div>
                            );
                        };

                        // Farmer dashboard component
                        const ClientDashboard = () => {
                                const { register, handleSubmit, reset, formState: { errors } } = useForm();
                                const farmerRequests = requests.filter(req => req.farmerId === currentUser.id);

                                return ( <
                                        div className = "p-8 space-y-8 bg-gray-50" >
                                        <
                                        div className = "bg-white p-6 rounded-xl shadow-md" >
                                        <
                                        h2 className = "text-2xl font-bold mb-4" > Submit New Request < /h2> <
                                        form onSubmit = { handleSubmit(data => { submitRequest(data);
                                                reset(); }) }
                                        className = "space-y-4" >
                                        <
                                        div >
                                        <
                                        label className = "block text-gray-700 text-sm font-medium mb-1" > Product < /label> <
                                        select {...register('productName', { required: 'Product name is required' }) }
                                        className = "w-full p-2 border border-gray-300 rounded-lg" >
                                        <
                                        option value = "" > Select a product < /option> {
                                            products.map(product => ( <
                                                option key = { product.id }
                                                value = { product.name } > { product.name } < /option>
                                            ))
                                        } <
                                        /select> {
                                            errors.productName && < p className = "text-red-500 text-xs mt-1" > { errors.productName.message } < /p>} <
                                                /div> <
                                                div >
                                                <
                                                label className = "block text-gray-700 text-sm font-medium mb-1" > Amount < /label> <
                                                input
                                            type = "number" {...register('amount', {
                                                    required: 'Amount is required',
                                                    min: { value: 1, message: 'Amount must be at least 1' }
                                                })
                                            }
                                            placeholder = "Enter amount"
                                            className = "w-full p-2 border border-gray-300 rounded-lg" /
                                                > {
                                                    errors.amount && < p className = "text-red-500 text-xs mt-1" > { errors.amount.message } < /p>} <
                                                    /div> <
                                                    button type = "submit"
                                                    className = "w-full bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition shadow-md p-2" >
                                                    Submit Request <
                                                    /button> <
                                                    /form> <
                                                    /div> <
                                                    div className = "bg-white p-6 rounded-xl shadow-md" >
                                                    <
                                                    h2 className = "text-2xl font-bold mb-4" > My Request Status < /h2> {
                                                        farmerRequests.length > 0 ? ( <
                                                            ul className = "divide-y divide-gray-200" > {
                                                                farmerRequests.map(request => ( <
                                                                    li key = { request.id }
                                                                    className = "py-4 flex justify-between items-center" >
                                                                    <
                                                                    div >
                                                                    <
                                                                    div className = "text-lg font-medium text-gray-900" > { request.productName } < /div> <
                                                                    div className = "text-sm text-gray-500" > Status: { request.status } < /div> <
                                                                    /div> <
                                                                    span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                                        request.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                                        request.status === 'Approved' ? 'bg-green-100 text-green-800' :
                                        'bg-red-100 text-red-800'
                                    }` } > { request.status } <
                                                                    /span> <
                                                                    /li>
                                                                ))
                                                            } <
                                                            /ul>
                                                        ) : ( <
                                                            p className = "text-gray-500" > You have no requests yet. < /p>
                                                        )
                                                    } <
                                                    /div> <
                                                    /div>
                                                );
                                        };

                                        // Admin dashboard component
                                        const AdminDashboard = () => {
                                                const [filterStatus, setFilterStatus] = useState('All');
                                                const [filterDepartment, setFilterDepartment] = useState('All');

                                                const filteredRequests = requests.filter(req => {
                                                    const statusMatch = filterStatus === 'All' || req.status === filterStatus;
                                                    const departmentMatch = filterDepartment === 'All' || req.productCategory === filterDepartment;
                                                    return statusMatch && departmentMatch;
                                                });

                                                // This component is for admin users to register new users
                                                const RegisterUser = () => {
                                                        const { register, handleSubmit, reset, formState: { errors } } = useForm();
                                                        const onSubmit = (data) => {
                                                            const newUser = {
                                                                id: `new-${users.length + 1}`,
                                                                ...data,
                                                                password: 'password', // Default password
                                                            };
                                                            setUsers([...users, newUser]);
                                                            setModalContent({ title: 'User Registered', message: `${newUser.fullName} has been registered as a ${newUser.role}.` });
                                                            setShowModal(true);
                                                            reset();
                                                        };

                                                        return ( <
                                                                div className = "p-8 bg-gray-50 min-h-screen" >
                                                                <
                                                                div className = "bg-white p-6 rounded-xl shadow-md max-w-md mx-auto" >
                                                                <
                                                                h2 className = "text-2xl font-bold mb-4 text-center" > Register New User < /h2> <
                                                                form onSubmit = { handleSubmit(onSubmit) }
                                                                className = "space-y-4" >
                                                                <
                                                                input type = "text"
                                                                placeholder = "Full Name" {...register('fullName', { required: 'Full name is required' }) }
                                                                className = "w-full p-2 border rounded-lg" / > {
                                                                    errors.fullName && < p className = "text-red-500 text-xs mt-1" > { errors.fullName.message } < /p>} <
                                                                    input type = "tel"
                                                                    placeholder = "Phone Number" {...register('phoneNumber', { required: 'Phone number is required' }) }
                                                                    className = "w-full p-2 border rounded-lg" / > {
                                                                        errors.phoneNumber && < p className = "text-red-500 text-xs mt-1" > { errors.phoneNumber.message } < /p>} <
                                                                        select {...register('role', { required: 'Role is required' }) }
                                                                        className = "w-full p-2 border rounded-lg" >
                                                                        <
                                                                        option value = "" > Select Role < /option> <
                                                                        option value = "Farmer" > Farmer < /option> <
                                                                        option value = "Kebele" > Kebele Admin < /option> <
                                                                        option value = "Woreda" > Woreda Admin < /option> <
                                                                        /select> {
                                                                            errors.role && < p className = "text-red-500 text-xs mt-1" > { errors.role.message } < /p>} <
                                                                                button type = "submit"
                                                                            className = "w-full p-2 bg-green-600 text-white rounded-lg hover:bg-green-700" > Register < /button> <
                                                                                /form> <
                                                                                /div> <
                                                                                /div>
                                                                        );
                                                                    };

                                                                    const CreateProductForm = () => {
                                                                        const { register, handleSubmit, reset, formState: { errors } } = useForm();

                                                                        return ( <
                                                                            div className = "bg-white dark:bg-gray-800 p-6 rounded-lg shadow-lg" >
                                                                            <
                                                                            h2 className = "text-2xl font-bold mb-4 text-gray-900 dark:text-white" > Create New Product < /h2> <
                                                                            form onSubmit = { handleSubmit(data => { createProduct(data);
                                                                                    reset(); }) }
                                                                            className = "space-y-4" >
                                                                            <
                                                                            input type = "text"
                                                                            placeholder = "Product Name" {...register('name', { required: "Product name is required" }) }
                                                                            className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                                                                            > {
                                                                                errors.name && < p className = "text-red-500 text-xs mt-1" > { errors.name.message } < /p>} <
                                                                                textarea
                                                                                placeholder = "Description" {...register('description') }
                                                                                className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                                                                                >
                                                                                <
                                                                                input
                                                                                type = "text"
                                                                                placeholder = "Category" {...register('category', { required: "Category is required" }) }
                                                                                className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                                                                                > {
                                                                                    errors.category && < p className = "text-red-500 text-xs mt-1" > { errors.category.message } < /p>} <
                                                                                    button
                                                                                    type = "submit"
                                                                                    className = "w-full p-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700" >
                                                                                    Save Product <
                                                                                    /button> <
                                                                                    /form> <
                                                                                    /div>
                                                                                );
                                                                            };

                                                                            return ( <
                                                                                div className = "p-8 space-y-8 bg-gray-50 min-h-screen" >
                                                                                <
                                                                                div className = "flex space-x-4 mb-4" >
                                                                                <
                                                                                button onClick = {
                                                                                    () => setView('createProduct') }
                                                                                className = "px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700" > Create New Product < /button> <
                                                                                button onClick = {
                                                                                    () => setView('registerUser') }
                                                                                className = "px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700" > Register New User < /button> <
                                                                                /div> { view === 'createProduct' && < CreateProductForm / > } { view === 'registerUser' && < RegisterUser / > } <
                                                                                div className = "bg-white p-6 rounded-xl shadow-md" >
                                                                                <
                                                                                h2 className = "text-2xl font-bold mb-4" > Requests Dashboard < /h2> <
                                                                                div className = "flex space-x-4 mb-4" >
                                                                                <
                                                                                select onChange = {
                                                                                    (e) => setFilterStatus(e.target.value) }
                                                                                className = "p-2 border rounded-lg" >
                                                                                <
                                                                                option value = "All" > All Statuses < /option> <
                                                                                option value = "Pending" > Pending < /option> <
                                                                                option value = "Approved" > Approved < /option> <
                                                                                option value = "Rejected" > Rejected < /option> <
                                                                                /select> <
                                                                                select onChange = {
                                                                                    (e) => setFilterDepartment(e.target.value) }
                                                                                className = "p-2 border rounded-lg" >
                                                                                <
                                                                                option value = "All" > All Departments < /option> <
                                                                                option value = "Biological Input Supply" > Biological Input Supply < /option> <
                                                                                option value = "Chemical Input Supply" > Chemical Input Supply < /option> <
                                                                                /select> <
                                                                                /div> <
                                                                                div className = "overflow-x-auto" >
                                                                                <
                                                                                table className = "min-w-full divide-y divide-gray-200 shadow-md rounded-lg" >
                                                                                <
                                                                                thead className = "bg-gray-50" >
                                                                                <
                                                                                tr >
                                                                                <
                                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > ID < /th> <
                                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Product Name < /th> <
                                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Department < /th> <
                                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Amount < /th> <
                                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Status < /th> <
                                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Actions < /th> <
                                                                                /tr> <
                                                                                /thead> <
                                                                                tbody className = "bg-white divide-y divide-gray-200" > {
                                                                                    filteredRequests.map(req => ( <
                                                                                        tr key = { req.id } >
                                                                                        <
                                                                                        td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900" > { req.id } < /td> <
                                                                                        td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { req.productName } < /td> <
                                                                                        td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { req.productCategory } < /td> <
                                                                                        td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { req.amount } < /td> <
                                                                                        td className = "px-6 py-4 whitespace-nowrap" >
                                                                                        <
                                                                                        span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                                                req.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                                                req.status === 'Approved' ? 'bg-green-100 text-green-800' :
                                                'bg-red-100 text-red-800'
                                            }` } > { req.status } <
                                                                                        /span> <
                                                                                        /td> <
                                                                                        td className = "px-6 py-4 whitespace-nowrap text-sm font-medium" > {
                                                                                            req.status === 'Pending' && ( <
                                                                                                div className = "flex space-x-2" >
                                                                                                <
                                                                                                button onClick = {
                                                                                                    () => handleRequestAction(req.id, 'Approved') }
                                                                                                className = "text-green-600 hover:text-green-900" > Approve < /button> <
                                                                                                button onClick = {
                                                                                                    () => handleRequestAction(req.id, 'Rejected') }
                                                                                                className = "text-red-600 hover:text-red-900" > Reject < /button> <
                                                                                                /div>
                                                                                            )
                                                                                        } <
                                                                                        /td> <
                                                                                        /tr>
                                                                                    ))
                                                                                } <
                                                                                /tbody> <
                                                                                /table> <
                                                                                /div> <
                                                                                /div> <
                                                                                /div>
                                                                            );
                                                                        };

                                                                        // Main App render logic
                                                                        return ( <
                                                                            div className = "min-h-screen bg-gray-100 font-sans text-gray-800" >
                                                                            <
                                                                            Modal show = { showModal }
                                                                            onClose = {
                                                                                () => setShowModal(false) }
                                                                            title = { modalContent.title }
                                                                            message = { modalContent.message }
                                                                            /> <
                                                                            Header / >
                                                                            <
                                                                            main > { view === 'login' && < Login / > } { view === 'dashboard' && currentUser.role === 'Farmer' && < ClientDashboard / > } { view === 'dashboard' && currentUser.role !== 'Farmer' && < AdminDashboard / > } <
                                                                            /main> <
                                                                            /div>
                                                                        );
                                                                    };

                                                                    export default App;
