                localStorage.setItem('user', JSON.stringify(farmerData));
                localStorage.setItem('userType', 'Farmer');
                onClose();
                navigate('/farmer-dashboard');

            } else {
                const adminData = data.user || data;
                const token = data.token;
                const actualUserType = adminData.role;

