                }
            }
            className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded dark:bg-gray-600 dark:hover:bg-gray-700 dark:text-white" >
            Close <
            /button> {
            requestStatus && ( <
                button onClick = { confirmStatusUpdate }
                className = { `px-4 py-2 rounded text-white ${requestStatus === 'Approved' ? 'bg-green-500 hover:bg-green-600 dark:bg-green-600 dark:hover:bg-green-700' : 'bg-red-500 hover:bg-red-600 dark:bg-red-600 dark:hover:bg-red-700'}` } >
                Confirm { requestStatus } <
                /button>
            )
        } <
        /div> < /
    div > <
        /div>
)
} <
/div>
);
};

export default ZoneDashboard;
