                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                required /
                >
                <
                /div>

                {
                    !editingZone && ( <
                        >
                        <
                        div className = "mb-4" >
                        <
                        label className = "block text-gray-700 dark:text-gray-300 mb-1" > Password < /label> <
                        input type = "password"
                        value = { zoneForm.password }
                        onChange = {
                            (e) => setZoneForm({...zoneForm, password: e.target.value }) }
                        className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                        required = {!editingZone }
                        /> <
                        /div>

                        <
                        div className = "mb-4" >
                        <
                        label className = "block text-gray-700 dark:text-gray-300 mb-1" > Confirm Password < /label> <
                        input type = "password"
                        value = { zoneForm.confirmPassword }
                        onChange = {
                            (e) => setZoneForm({...zoneForm, confirmPassword: e.target.value }) }
                        className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                        required = {!editingZone }
                        /> <
                        /div> <
                        />
                    )
                }

                {
                    editingZone && ( <
                        div className = "mb-4" >
                        <
                        label className = "block text-gray-700 dark:text-gray-300 mb-1" > New Password(leave blank to keep current) < /label> <
                        input type = "password"
                        value = { zoneForm.password }
                        onChange = {
                            (e) => setZoneForm({...zoneForm, password: e.target.value }) }
                        className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600" /
                        >
                        <
                        /div>
                    )
                }

                {
                    zoneError && ( <
                        div className = "mb-4 text-red-500 dark:text-red-400" > { zoneError } < /div>
                    )
                }

                <
                div className = "flex justify-end space-x-2" >
                <
                button type = "button"
                onClick = {
                    () => {
                        setShowZoneForm(false);
                        setEditingZone(null);
                        setZoneForm({
                            fullName: '',
                            phoneNumber: '',
                            password: '',
                            confirmPassword: '',
                            zoneName: ''
                        });
                        setZoneError('');
                    }
                }
                className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded dark:bg-gray-600 dark:hover:bg-gray-700 dark:text-white" >
                Cancel <
                /button> <
                button type = "submit"
                className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded dark:bg-blue-600 dark:hover:bg-blue-700" >
                { editingZone ? 'Update Zone Admin' : 'Register Zone Admin' } <
                /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        }

        { /* Farmer Form Modal */ } {
            showFarmerForm && ( <
                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                <
                div className = "bg-white dark:bg-gray-800 rounded-lg p-6 w-full max-w-md" >
                <
                div className = "flex justify-between items-center mb-4" >
                <
                h3 className = "text-xl font-bold dark:text-white" > { editingFarmer ? 'Edit Farmer' : 'Register Farmer' } <
                /h3> <
                button onClick = {
                    () => {
                        setShowFarmerForm(false);
                        setEditingFarmer(null);
                        setFarmerForm({
                            fullName: '',
                            phoneNumber: '',
                            location: '',
                            kebele: ''
                        });
                        setFarmerError('');
                    }
                }
                className = "text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200" >
                
                <
                /button> <
                /div>

                <
                form onSubmit = { handleFarmerSubmit } >
                <
                div className = "mb-4" >
                <
                label className = "block text-gray-700 dark:text-gray-300 mb-1" > Full Name < /label> <
                input type = "text"
                value = { farmerForm.fullName }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, fullName: e.target.value }) }
                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                required /
                >
                <
                /div>

                <
                div className = "mb-4" >
                <
                label className = "block text-gray-700 dark:text-gray-300 mb-1" > Phone Number < /label> <
                input type = "tel"
                value = { farmerForm.phoneNumber }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, phoneNumber: e.target.value }) }
                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                required /
                >
                <
                /div>

                <
                div className = "mb-4" >
                <
                label className = "block text-gray-700 dark:text-gray-300 mb-1" > Location < /label> <
                input type = "text"
                value = { farmerForm.location }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, location: e.target.value }) }
                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                required /
                >
                <
                /div>

                <
                div className = "mb-4" >
                <
                label className = "block text-gray-700 dark:text-gray-300 mb-1" > Kebele < /label> <
                input type = "text"
                value = { farmerForm.kebele }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, kebele: e.target.value }) }
                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                required /
                >
                <
                /div>

                {
                    farmerError && ( <
                        div className = "mb-4 text-red-500 dark:text-red-400" > { farmerError } < /div>
                    )
                }

                <
                div className = "flex justify-end space-x-2" >
                <
                button type = "button"
                onClick = {
                    () => {
                        setShowFarmerForm(false);
                        setEditingFarmer(null);
                        setFarmerForm({
                            fullName: '',
                            phoneNumber: '',
                            location: '',
                            kebele: ''
                        });
                        setFarmerError('');
                    }
                }
                className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded dark:bg-gray-600 dark:hover:bg-gray-700 dark:text-white" >
                Cancel <
                /button> <
                button type = "submit"
                className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded dark:bg-blue-600 dark:hover:bg-blue-700" >
                { editingFarmer ? 'Update Farmer' : 'Register Farmer' } <
                /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        } <
        /div>
    );
};

export default RegionDashboard;
