import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
        const [credentials, setCredentials] = useState({
            phone: '',
            password: '',
            role: 'Farmer'
        });
        const [error, setError] = useState('');
        const [isLoading, setIsLoading] = useState(false);
        const navigate = useNavigate();

        const handleChange = (e) => {
            const { name, value } = e.target;
            setCredentials({
                ...credentials,
                [name]: value
            });
        };

        const formatPhoneNumber = (phone) => {
            // Remove any non-digit characters
            const cleaned = phone.replace(/\D/g, '');

            // Format based on length (simple formatting for demonstration)
            if (cleaned.length <= 3) {
                return cleaned;
            } else if (cleaned.length <= 6) {
                return `${cleaned.slice(0, 3)}-${cleaned.slice(3)}`;
            } else {
                return `${cleaned.slice(0, 3)}-${cleaned.slice(3, 6)}-${cleaned.slice(6, 10)}`;
            }
        };

        const handlePhoneChange = (e) => {
            const { value } = e.target;
            const formattedPhone = formatPhoneNumber(value);
            setCredentials({
                ...credentials,
                phone: formattedPhone
            });
        };

        const handleSubmit = async(e) => {
            e.preventDefault();
            setIsLoading(true);
            setError('');

            // Validate phone number (remove formatting for API call)
            const rawPhone = credentials.phone.replace(/\D/g, '');
            if (rawPhone.length < 10) {
                setError('Please enter a valid phone number');
                setIsLoading(false);
                return;
            }

            try {
                let endpoint;

                // Determine which endpoint to use based on role
                if (credentials.role === 'Farmer') {
                    endpoint = '/api/farmers/login';
                } else {
                    endpoint = '/api/admins/login';
                }

                const response = await fetch(endpoint, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        phone: rawPhone, // Send raw phone number without formatting
                        password: credentials.password
                    })
                });

                const data = await response.json();

                if (response.ok) {
                    // Store token and user info
                    localStorage.setItem('token', data.token);
                    localStorage.setItem('userRole', credentials.role);
                    localStorage.setItem('userInfo', JSON.stringify(data.user));

                    // Redirect based on role
                    switch (credentials.role) {
                        case 'Farmer':
                            navigate('/farmer-dashboard');
                            break;
                        case 'Kebele':
                            navigate('/kebele-dashboard');
                            break;
                        case 'Woreda':
                            navigate('/woreda-dashboard');
                            break;
                        case 'Zone':
                            navigate('/zone-dashboard');
                            break;
                        case 'Region':
                            navigate('/region-dashboard');
                            break;
                        case 'Federal':
                            navigate('/federal-dashboard');
                            break;
                        default:
                            navigate('/');
                    }
                } else {
                    setError(data.message || 'Login failed. Please check your credentials.');
                }
            } catch (err) {
                setError('Network error. Please try again.');
            } finally {
                setIsLoading(false);
            }
        };

        return ( <
            div className = "login-container" >
            <
            div className = "login-card" >
            <
            div className = "login-header" >
            <
            h1 > Agricultural Platform < /h1> <
            p > Sign in with your phone number < /p> <
            /div>

            <
            form onSubmit = { handleSubmit }
            className = "login-form" > {
                error && < div className = "error-message" > { error } < /div>}

                <
                div className = "form-group" >
                <
                label htmlFor = "role" > User Type < /label> <
                select
                id = "role"
                name = "role"
                value = { credentials.role }
                onChange = { handleChange }
                className = "form-control" >
                <
                option value = "Farmer" > Farmer < /option> <
                option value = "Kebele" > Kebele Admin < /option> <
                option value = "Woreda" > Woreda Admin < /option> <
                option value = "Zone" > Zone Admin < /option> <
                option value = "Region" > Region Admin < /option> <
                option value = "Federal" > Federal Admin < /option> <
                /select> <
                /div>

                <
                div className = "form-group" >
                <
                label htmlFor = "phone" > Phone Number < /label> <
                input
                type = "tel"
                id = "phone"
                name = "phone"
                value = { credentials.phone }
                onChange = { handlePhoneChange }
                className = "form-control"
                placeholder = "Enter your phone number (e.g., 251-911-123456)"
                pattern = "[0-9]{3}-[0-9]{3}-[0-9]{4,6}"
                required /
                >
                <
                small className = "input-hint" > Format: 251 - 911 - 123456 < /small> <
                    /div>

                    <
                    div className = "form-group" >
                    <
                    label htmlFor = "password" > Password < /label> <
                    input
                type = "password"
                id = "password"
                name = "password"
                value = { credentials.password }
                onChange = { handleChange }
                className = "form-control"
                placeholder = "Enter your password"
                required /
                >
                <
                /div>

                <
                button
                type = "submit"
                className = "login-btn"
                disabled = { isLoading } >
                { isLoading ? 'Signing in...' : 'Sign In' } <
                /button> <
                /form>

                <
                div className = "login-footer" >
                <
                p > Don 't have an account? Contact your administrator.</p> <
                /div> <
                /div> <
                /div>
            );
        };

        export default Login;
