import axios from 'axios';

const API_URL = 'http://localhost:5000'; // update to your backend URL

const api = {
    login: (data) => axios.post(`${API_URL}/login`, data),
    getRequests: () => axios.get(`${API_URL}/requests`),
    getProducts: () => axios.get(`${API_URL}/products`),
    createRequest: (data) => axios.post(`${API_URL}/requests`, data),
    approveRequest: (id) => axios.put(`${API_URL}/requests/${id}/approve`)
};

export default api;
