import React, { useState } from 'react';
import { getFirestore, doc, updateDoc, arrayUnion } from 'firebase/firestore';
import { getAuth } from 'firebase/auth';

const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';

export default function RequestApprovalForm({ user, request, showModal }) {
    const db = getFirestore();
    const auth = getAuth();
    const [action, setAction] = useState('');
    const [comment, setComment] = useState('');

    const handleApprovalAction = async(e) => {
        e.preventDefault();
        if (!action) {
            showModal('Please select an action (Approve or Reject).');
            return;
        }

        try {
            const requestRef = doc(db, 'artifacts', appId, 'public', 'data', 'requests', request.id);

            const newStatus = action === 'approve' ? 'Approved' : 'Rejected';
            const newLogEntry = {
                timestamp: new Date().toISOString(),
                userId: auth.currentUser ? auth.currentUser.uid : 'anonymous',
                userName: user.role, // Using role as a name for simplicity
                action: newStatus,
                comment: comment,
            };

            await updateDoc(requestRef, {
                status: newStatus,
                approvals: arrayUnion(newLogEntry),
            });

            showModal(`Request has been ${newStatus}.`);
        } catch (error) {
            console.error('Error updating request status:', error);
            showModal('Failed to update request. Please try again.');
        }
    };

    if (!request) {
        return <div className = "p-4 bg-white rounded-lg shadow" > No request selected. < /div>;
    }

    return ( <
        div className = "p-8 bg-white rounded-lg shadow-xl" >
        <
        h2 className = "text-3xl font-bold text-gray-800 mb-6 border-b pb-4" > Request Approval < /h2> <
        div className = "mb-6" >
        <
        h3 className = "text-xl font-semibold text-gray-700 mb-2" > Request Details < /h3> <
        p className = "text-gray-600" > ** ID: ** { request.id } < /p> <
        p className = "text-gray-600" > ** Type: ** { request.requestType } < /p> <
        p className = "text-gray-600" > ** Quantity: ** { request.quantity } < /p> <
        p className = "text-gray-600" > ** Status: ** < span className = { `font-semibold ${request.status === 'Pending' ? 'text-yellow-600' : request.status === 'Approved' ? 'text-green-600' : 'text-red-600'}` } > { request.status } < /span></p >
        <
        p className = "text-gray-600" > ** Submitted by: ** { request.userId } < /p> <
        /div>

        <
        form onSubmit = { handleApprovalAction }
        className = "space-y-4" >
        <
        div className = "flex items-center space-x-4" >
        <
        label htmlFor = "action"
        className = "text-gray-700 font-medium" > Action: < /label> <
        select id = "action"
        value = { action }
        onChange = {
            (e) => setAction(e.target.value) }
        className = "flex-1 border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-colors" >
        <
        option value = "" > --Select Action-- < /option> <
        option value = "approve" > Approve < /option> <
        option value = "reject" > Reject < /option> <
        /select> <
        /div> <
        div >
        <
        label htmlFor = "comment"
        className = "block text-gray-700 font-medium mb-1" > Comments: < /label> <
        textarea id = "comment"
        value = { comment }
        onChange = {
            (e) => setComment(e.target.value) }
        rows = "4"
        className = "w-full border border-gray-300 rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-colors"
        placeholder = "Add your comments here..." >
        < /textarea> <
        /div> <
        button type = "submit"
        className = "w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg shadow-md transition-colors duration-200" >
        Submit Decision <
        /button> <
        /form> <
        /div>
    );
}
