import React, { useState } from 'react';
import { api } from './api'; // make sure api.js exports { api }
import FederalDashboard from './dashboards/FederalDashboard';
import RegionDashboard from './dashboards/RegionDashboard';
import ZoneDashboard from './dashboards/ZoneDashboard';
import WoredaDashboard from './dashboards/WoredaDashboard';
import KebeleDashboard from './dashboards/KebeleDashboard';
import FarmerDashboard from './dashboards/FarmerDashboard';

function App() {
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');
    const [currentUser, setCurrentUser] = useState(null);
    const [currentPage, setCurrentPage] = useState('login');

    const handleLogin = async(e) => {
        e.preventDefault();
        try {
            const user = await api.login(phoneNumber, password); // call backend login
            setCurrentUser(user);
            setCurrentPage('dashboard');
        } catch (err) {
            const message =
                (err.response && err.response.data && err.response.data.message) ||
                err.message ||
                'Unknown error';
            alert('Login failed: ' + message);
        }
    };

    if (currentPage === 'login') {
        return ( <
            div className = "flex justify-center items-center h-screen bg-gray-100" >
            <
            form onSubmit = { handleLogin }
            className = "bg-white p-8 rounded shadow-md w-96" >
            <
            h2 className = "text-2xl font-bold mb-6 text-center" > Login < /h2>

            <
            div className = "mb-4" >
            <
            label htmlFor = "phone"
            className = "block text-gray-700 mb-2" >
            Phone Number <
            /label> <
            input id = "phone"
            type = "text"
            value = { phoneNumber }
            onChange = {
                (e) => setPhoneNumber(e.target.value) }
            className = "w-full px-3 py-2 border rounded"
            placeholder = "Enter your phone number"
            required /
            >
            <
            /div>

            <
            div className = "mb-6" >
            <
            label htmlFor = "password"
            className = "block text-gray-700 mb-2" >
            Password <
            /label> <
            input id = "password"
            type = "password"
            value = { password }
            onChange = {
                (e) => setPassword(e.target.value) }
            className = "w-full px-3 py-2 border rounded"
            placeholder = "Enter your password"
            required /
            >
            <
            /div>

            <
            button type = "submit"
            className = "w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700" >
            Login <
            /button> <
            /form> <
            /div>
        );
    }

    if (currentPage === 'dashboard' && currentUser) {
        switch (currentUser.role) {
            case 'Federal':
                return <FederalDashboard currentUser = { currentUser }
                />;
            case 'Region':
                return <RegionDashboard currentUser = { currentUser }
                />;
            case 'Zone':
                return <ZoneDashboard currentUser = { currentUser }
                />;
            case 'Woreda':
                return <WoredaDashboard currentUser = { currentUser }
                />;
            case 'Kebele':
                return <KebeleDashboard currentUser = { currentUser }
                />;
            case 'Farmer':
                return <FarmerDashboard currentUser = { currentUser }
                />;
            default:
                return <p > Unknown role < /p>;
        }
    }

    return <p > Loading... < /p>;
}

export default App;
