        className = "w-full border border-gray-300 rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
        rows = "3"
        required /
        >
        <
        /div> <
        div className = "mb-4" >
        <
        label className = "block text-gray-700 dark:text-gray-300 mb-2" > Category < /label> <
        input type = "text"
        value = { productForm.category }
        onChange = {
            (e) => setProductForm({...productForm, category: e.target.value })
        }
        className = "w-full border border-gray-300 rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
        required /
        >
        <
        /div> <
        div className = "mb-4" >
        <
        label className = "block text-gray-700 dark:text-gray-300 mb-2" > Amount < /label> <
        input type = "number"
        value = { productForm.amount }
        onChange = {
            (e) => setProductForm({...productForm, amount: e.target.value })
        }
        className = "w-full border border-gray-300 rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
        required /
        >
        <
        /div> <
        div className = "flex justify-end space-x-2" >
        <
        button type = "button"
        onClick = {
            () => {
                setShowProductModal(false);
                setEditingProduct(null);
                setProductForm({ name: '', description: '', category: '', amount: '' });
            }
        }
        className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" >
        Cancel <
        /button> <
        button type = "submit"
        className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" > { editingProduct ? 'Update' : 'Add' } <
        /button> < /
        div > <
        /form> < /
        div > <
        /div>
    )
}

{ /* Request Status Modal */ } {
    showRequestModal && selectedRequest && ( <
        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
        <
        div className = "bg-white dark:bg-gray-800 rounded-lg p-6 w-full max-w-2xl max-h-[90vh] overflow-y-auto" >
        <
        h2 className = "text-xl font-semibold mb-4 dark:text-white" > Request Details < /h2>

        <
        div className = "mb-6" >
        <
        h3 className = "font-semibold text-lg dark:text-white" > Farmer Information < /h3> <
        p className = "text-gray-600 dark:text-gray-400" > < strong > Name: < /strong> {selectedRequest.farmer_name}</p >
        <
        p className = "text-gray-600 dark:text-gray-400" > < strong > Phone: < /strong> {selectedRequest.farmer_phone}</p >
        <
        p className = "text-gray-600 dark:text-gray-400" > < strong > Kebele: < /strong> {selectedRequest.kebele_name}</p >
        <
        /div>

        <
        div className = "mb-6" >
        <
        h3 className = "font-semibold text-lg dark:text-white" > Product Information < /h3> <
        p className = "text-gray-600 dark:text-gray-400" > < strong > Product: < /strong> {selectedRequest.product_name}</p >
        <
        p className = "text-gray-600 dark:text-gray-400" > < strong > Description: < /strong> {selectedRequest.product_description}</p >
        <
        p className = "text-gray-600 dark:text-gray-400" > < strong > Category: < /strong> {selectedRequest.product_category}</p >
        <
        p className = "text-gray-600 dark:text-gray-400" > < strong > Amount: < /strong> {selectedRequest.product_amount}</p >
        <
        /div>

        <
        div className = "mb-6" >
        <
        h3 className = "font-semibold text-lg dark:text-white" > Approval Status < /h3> { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } < /
        div >

        {
            canWoredaAdminAct(selectedRequest) && requestStatus && ( <
                div className = "mb-6" >
                <
                h3 className = "font-semibold text-lg dark:text-white" > Update Status to: { requestStatus } < /h3> <
                div className = "mt-4" >
                <
                label className = "block text-gray-700 dark:text-gray-300 mb-2" > Reason / Feedback(Optional): < /label> <
                textarea value = { decisionReason }
                onChange = {
                    (e) => setDecisionReason(e.target.value)
                }
                className = "w-full border border-gray-300 rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                rows = "3"
                placeholder = "Enter reason for your decision..." /
                >
                <
                /div> <
                div className = "flex justify-end space-x-2 mt-4" >
                <
                button onClick = {
                    () => {
                        setShowRequestModal(false);
                        setSelectedRequest(null);
                        setRequestStatus('');
                        setDecisionReason('');
                    }
                }
                className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" >
                Cancel <
                /button> <
                button onClick = { confirmStatusUpdate }
                className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" >
                Confirm { requestStatus } <
                /button> < /
                div > <
                /div>
            )
        }

        {
            !requestStatus && ( <
                div className = "flex justify-end" >
                <
                button onClick = {
                    () => {
                        setShowRequestModal(false);
                        setSelectedRequest(null);
                    }
                }
                className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" >
                Close <
                /button> < /
                div >
            )
        } <
        /div> < /
        div >
    )
} <
/div>
);
};

export default WoredaDashboard;
