// File: KebeleDashboard.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './KebeleDashboard.css';

const KebeleDashboard = () => {
    const [activeTab, setActiveTab] = useState('overview');
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    // Farmer Management State
    const [farmers, setFarmers] = useState([]);
    const [showFarmerModal, setShowFarmerModal] = useState(false);
    const [editingFarmer, setEditingFarmer] = useState(null);
    const [farmerForm, setFarmerForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: ''
    });

    // Product Management State
    const [products, setProducts] = useState([]);
    const [showProductModal, setShowProductModal] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        description: '',
        category: '',
        amount: ''
    });

    // Request Management State
    const [requests, setRequests] = useState([]);
    const [selectedRequests, setSelectedRequests] = useState([]);
    const [showRequestModal, setShowRequestModal] = useState(false);
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [requestStatus, setRequestStatus] = useState('');
    const [decisionReason, setDecisionReason] = useState('');
    const [bulkAction, setBulkAction] = useState('');

    // Stats State
    const [stats, setStats] = useState({
        totalRequests: 0,
        pendingRequests: 0,
        approvedRequests: 0,
        rejectedRequests: 0,
        totalFarmers: 0,
        totalProducts: 0
    });

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Kebele' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        fetchDashboardData();
    }, [navigate]);

    const fetchDashboardData = async() => {
        try {
            const token = localStorage.getItem('token');

            // Fetch farmers in scope
            const farmersResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (farmersResponse.ok) {
                const data = await farmersResponse.json();
                const farmersData = data.filter(item => item.type === 'farmer');
                setFarmers(farmersData);
                setStats(prev => ({...prev, totalFarmers: farmersData.length }));
            }

            // Fetch products
            const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
                setStats(prev => ({...prev, totalProducts: productsData.length }));
            }
            const handleDeleteRequest = async(requestId) => {
                if (window.confirm('Are you sure you want to delete this request?')) {
                    try {
                        const token = localStorage.getItem('token');
                        const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}`, {
                            method: 'DELETE',
                            headers: {
                                'Authorization': `Bearer ${token}`
                            }
                        });

                        if (response.ok) {
                            fetchDashboardData();
                            alert('Request deleted successfully');
                        } else {
                            const errorData = await response.json();
                            alert(errorData.message || 'Error deleting request');
                        }
                    } catch (error) {
                        console.error('Error deleting request:', error);
                        alert('Error deleting request');
                    }
                }
            };
            // Fetch requests using the approval routes
            const requestsResponse = await fetch('http://localhost:5000/api/approval/requests', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                setRequests(requestsData);

                // Calculate stats from requests data
                const totalRequests = requestsData.length;
                const pendingRequests = requestsData.filter(req => req.status === 'pending').length;
                const approvedRequests = requestsData.filter(req => req.status === 'approved').length;
                const rejectedRequests = requestsData.filter(req => req.status === 'rejected').length;
                const acceptedRequests = requestsData.filter(req => req.status === 'accepted').length;

                setStats(prev => ({
                    ...prev,
                    totalRequests,
                    pendingRequests,
                    approvedRequests: approvedRequests + acceptedRequests,
                    rejectedRequests
                }));
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        navigate('/login');
    };

    // Farmer Management Functions
    const handleFarmerSubmit = async(e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');

            if (farmerForm.password !== farmerForm.confirmPassword) {
                alert('Passwords do not match');
                return;
            }

            const url = editingFarmer ?
                `http://localhost:5000/api/admins/edit/${editingFarmer.id}` :
                'http://localhost:5000/api/admins/createfarmer';

            const method = editingFarmer ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    fullName: farmerForm.fullName,
                    phoneNumber: farmerForm.phoneNumber,
                    password: farmerForm.password,
                    role: 'Farmer',
                    region_name: user.region_name,
                    zone_name: user.zone_name,
                    woreda_name: user.woreda_name,
                    kebele_name: user.kebele_name
                })
            });

            if (response.ok) {
                setShowFarmerModal(false);
                setEditingFarmer(null);
                setFarmerForm({ fullName: '', phoneNumber: '', password: '', confirmPassword: '' });
                fetchDashboardData();
                alert(editingFarmer ? 'Farmer updated successfully' : 'Farmer registered successfully');
            } else {
                const errorData = await response.json();
                alert(errorData.message || 'Error processing farmer');
            }
        } catch (error) {
            console.error('Error processing farmer:', error);
            alert('Error processing farmer');
        }
    };

    const handleEditFarmer = (farmer) => {
        setEditingFarmer(farmer);
        setFarmerForm({
            fullName: farmer.full_name,
            phoneNumber: farmer.phone_number,
            password: '',
            confirmPassword: ''
        });
        setShowFarmerModal(true);
    };

    const handleDeleteFarmer = async(farmerId) => {
        if (window.confirm('Are you sure you want to delete this farmer?')) {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/delete/${farmerId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                    alert('Farmer deleted successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error deleting farmer');
                }
            } catch (error) {
                console.error('Error deleting farmer:', error);
                alert('Error deleting farmer');
            }
        }
    };

    // Product Management Functions
    const handleProductSubmit = async(e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');

            const url = editingProduct ?
                `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                'http://localhost:5000/api/admins/addproduct';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    name: productForm.name,
                    description: productForm.description,
                    category: productForm.category,
                    amount: productForm.amount
                })
            });

            if (response.ok) {
                setShowProductModal(false);
                setEditingProduct(null);
                setProductForm({ name: '', description: '', category: '', amount: '' });
                fetchDashboardData();
                alert(editingProduct ? 'Product updated successfully' : 'Product added successfully');
            } else {
                const errorData = await response.json();
                alert(errorData.message || 'Error processing product');
            }
        } catch (error) {
            console.error('Error processing product:', error);
            alert('Error processing product');
        }
    };

    const handleEditProduct = (product) => {
        setEditingProduct(product);
        setProductForm({
            name: product.name,
            description: product.description,
            category: product.category,
            amount: product.amount
        });
        setShowProductModal(true);
    };

    const handleDeleteProduct = async(productId) => {
        if (window.confirm('Are you sure you want to delete this product?')) {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                    alert('Product deleted successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error deleting product');
                }
            } catch (error) {
                console.error('Error deleting product:', error);
                alert('Error deleting product');
            }
        }
    };

    // Request Management Functions
    const updateRequestStatus = async(requestId, status, reason = '') => {
        try {
            const token = localStorage.getItem('token');

            // Map the status to match the backend expectations
            let backendStatus = status;
            if (status === 'approve') {
                backendStatus = 'Approved';
            } else if (status === 'accept') {
                backendStatus = 'Accepted';
            } else if (status === 'reject') {
                backendStatus = 'Rejected';
            }

            const response = await fetch(`http://localhost:5000/api/approval/requests/${requestId}/status`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    status: backendStatus,
                    feedback: reason
                })
            });

            const responseData = await response.json();

            if (response.ok) {
                setShowRequestModal(false);
                setSelectedRequest(null);
                setRequestStatus('');
                setDecisionReason('');
                fetchDashboardData();
                alert(`Request ${responseData.request.status} successfully`);
            } else {
                alert(responseData.message || 'Error updating request');
            }
        } catch (error) {
            console.error('Error updating request:', error);
            alert('Error updating request');
        }
    };
    const handleBulkUpdate = async() => {
        if (!bulkAction || selectedRequests.length === 0) {
            alert('Please select an action and at least one request');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:5000/api/approval/requests/bulk-update', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    requestIds: selectedRequests,
                    action: bulkAction
                })
            });

            const responseData = await response.json();

            if (response.ok) {
                setSelectedRequests([]);
                setBulkAction('');
                fetchDashboardData();
                alert('Bulk update completed successfully');
            } else {
                alert(responseData.message || 'Error performing bulk update');
            }
        } catch (error) {
            console.error('Error performing bulk update:', error);
            alert('Error performing bulk update');
        }
    };

    const handleStatusUpdate = (request, status) => {
        setSelectedRequest(request);
        setRequestStatus(status);
        setShowRequestModal(true);
    };

    const confirmStatusUpdate = () => {
        if (selectedRequest && requestStatus) {
            updateRequestStatus(selectedRequest.id, requestStatus, decisionReason);
        }
    };

    const toggleRequestSelection = (requestId) => {
        if (selectedRequests.includes(requestId)) {
            setSelectedRequests(selectedRequests.filter(id => id !== requestId));
        } else {
            setSelectedRequests([...selectedRequests, requestId]);
        }
    };

    const selectAllRequests = () => {
        if (selectedRequests.length === requests.filter(r => r.status === 'pending').length) {
            setSelectedRequests([]);
        } else {
            setSelectedRequests(requests.filter(r => r.status === 'pending').map(r => r.id));
        }
    };

    const getStatusBadge = (status) => {
        const statusClasses = {
            'pending': 'status-pending',
            'approved': 'status-approved',
            'accepted': 'status-accepted',
            'rejected': 'status-rejected'
        };

        return ( <
            span className = { `status-badge ${statusClasses[status] || ''}` } > { status.charAt(0).toUpperCase() + status.slice(1) } <
            /span>
        );
    };

    if (!user) {
        return <div className = "loading" > Loading... < /div>;
    }

    return ( <
        div className = "kebele-dashboard" > { /* Sidebar */ } <
        div className = "sidebar" >
        <
        h2 > Kebele Dashboard < /h2> <
        nav >
        <
        button className = { activeTab === 'overview' ? 'active' : '' }
        onClick = {
            () => setActiveTab('overview')
        } > 📊Overview <
        /button> <
        button className = { activeTab === 'farmers' ? 'active' : '' }
        onClick = {
            () => setActiveTab('farmers')
        } > 👨‍🌾Farmers <
        /button> <
        button className = { activeTab === 'products' ? 'active' : '' }
        onClick = {
            () => setActiveTab('products')
        } > 🛒Products <
        /button> <
        button className = { activeTab === 'requests' ? 'active' : '' }
        onClick = {
            () => setActiveTab('requests')
        } > 📋Requests <
        /button> < /
        nav > <
        /div>

        { /* Main Content */ } <
        div className = "main-content" >
        <
        header >
        <
        h1 > Welcome, { user.fullName }(Kebele Admin) < /h1> <
        button onClick = { handleLogout }
        className = "logout-btn" >
        Logout <
        /button> < /
        header >

        <
        main > { /* Overview Tab */ } {
            activeTab === 'overview' && ( <
                div className = "overview-tab" >
                <
                h2 > System Overview < /h2> <
                div className = "stats-grid" >
                <
                div className = "stat-card" >
                <
                h3 > { stats.totalRequests } < /h3> <
                p > Total Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.pendingRequests } < /h3> <
                p > Pending Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.approvedRequests } < /h3> <
                p > Approved Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.rejectedRequests } < /h3> <
                p > Rejected Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.totalFarmers } < /h3> <
                p > Farmers < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.totalProducts } < /h3> <
                p > Products < /p> < /
                div > <
                /div>

                <
                div className = "quick-actions" >
                <
                h2 > Quick Actions < /h2> <
                div className = "actions-grid" >
                <
                button onClick = {
                    () => setActiveTab('requests')
                }
                className = "action-btn" > 📋Manage Requests <
                /button> <
                button onClick = {
                    () => setActiveTab('farmers')
                }
                className = "action-btn" > 👨‍🌾Manage Farmers <
                /button> <
                button onClick = {
                    () => setActiveTab('products')
                }
                className = "action-btn" > 🛒Manage Products <
                /button> <
                button onClick = {
                    () => setShowFarmerModal(true)
                }
                className = "action-btn" > ➕Register Farmer <
                /button> <
                button onClick = {
                    () => setShowProductModal(true)
                }
                className = "action-btn" > ➕Add Product <
                /button> < /
                div > <
                /div> < /
                div >
            )
        }

        { /* Farmers Tab */ } {
            activeTab === 'farmers' && ( <
                    div className = "farmers-tab" >
                    <
                    div className = "tab-header" >
                    <
                    h2 > Farmer Management < /h2> <
                    p > Managing farmers in your kebele: { user.kebele_name } < /p> <
                    button onClick = {
                        () => setShowFarmerModal(true)
                    }
                    className = "add-btn" > ➕Register New Farmer <
                    /button> < /
                    div >

                    <
                    div className = "farmers-list" > {
                        farmers.map((farmer) => ( <
                                div key = { farmer.id }
                                className = "farmer-card" >
                                <
                                div className = "farmer-info" >
                                <
                                h3 > { farmer.full_name } < /h3> <
                                p > 📞Phone: { farmer.phone_number } < /p> <
                                p > 📍Kebele: { farmer.kebele_name } < /p> <
                                p > 📅Registered: { new Date(farmer.created_at).toLocaleDateString() } < /p> < /
                                div > <
                                div className = "farmer-actions" >
                                <
                                button onClick = {
                                    () => handleEditFarmer(farmer)
                                }
                                className = "edit-btn" > ✏️Edit <
                                /button> <
                                button onClick = {
                                    () => handleDeleteFarmer(farmer.id)
                                }
                                className = "delete-btn" > 🗑️Delete <
                                /button> < /
                                div > <
                                div className = "farmer-requests" >
                                <
                                h4 > Recent Requests: < /h4> {
                                requests
                                .filter(req => req.farmer_id === farmer.id)
                                .slice(0, 3)
                                .map(req => ( <
                                    div key = { req.id }
                                    className = "request-item" >
                                    <
                                    span className = "product-name" > { req.product_name } < /span> { getStatusBadge(req.status) } < /
                                    div >
                                ))
                            } {
                                requests.filter(req => req.farmer_id === farmer.id).length === 0 && ( <
                                    p > No requests from this farmer < /p>
                                )
                            } <
                            /div> < /
                            div >
                        ))
                } {
                    farmers.length === 0 && ( <
                        p > No farmers found in your kebele. < /p>
                    )
                } <
                /div> < /
            div >
        )
    }

    { /* Products Tab */ } {
        activeTab === 'products' && ( <
            div className = "products-tab" >
            <
            div className = "tab-header" >
            <
            h2 > Product Management < /h2> <
            p > Managing products in your kebele < /p> <
            button onClick = {
                () => setShowProductModal(true)
            }
            className = "add-btn" > ➕Add New Product <
            /button> < /
            div >

            <
            div className = "products-list" > {
                products.map((product) => ( <
                    div key = { product.id }
                    className = "product-card" >
                    <
                    div className = "product-info" >
                    <
                    h3 > { product.name } < /h3> <
                    p > { product.description } < /p> <
                    p > 📦Category: { product.category } < /p> <
                    p > 💰Amount: { product.amount } < /p> <
                    p > 📅Created: { new Date(product.created_at).toLocaleDateString() } < /p> < /
                    div > <
                    div className = "product-actions" >
                    <
                    button onClick = {
                        () => handleEditProduct(product)
                    }
                    className = "edit-btn" > ✏️Edit <
                    /button> <
                    button onClick = {
                        () => handleDeleteProduct(product.id)
                    }
                    className = "delete-btn" > 🗑️Delete <
                    /button> < /
                    div > <
                    /div>
                ))
            } {
                products.length === 0 && ( <
                    p > No products found. < /p>
                )
            } <
            /div> < /
            div >
        )
    }

    { /* Requests Tab */ } {
        activeTab === 'requests' && ( <
                div className = "requests-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Request Management < /h2> <
                p > Manage farmer requests in your kebele: { user.kebele_name } < /p>

                {
                    requests.filter(r => r.status === 'pending').length > 0 && ( <
                        div className = "bulk-actions" >
                        <
                        select value = { bulkAction }
                        onChange = {
                            (e) => setBulkAction(e.target.value)
                        } >
                        <
                        option value = "" > Select Bulk Action < /option> <
                        option value = "approve" > Approve(Intelligent Decision) < /option> <
                        option value = "accept" > Accept < /option> <
                        option value = "reject" > Reject < /option> < /
                        select > <
                        button onClick = { handleBulkUpdate }
                        className = "bulk-btn" >
                        Apply to Selected({ selectedRequests.length }) <
                        /button> <
                        button onClick = { selectAllRequests }
                        className = "select-all-btn" > {
                            selectedRequests.length === requests.filter(r => r.status === 'pending').length ?
                            'Deselect All' : 'Select All Pending'
                        } <
                        /button> < /
                        div >
                    )
                } <
                /div>

                <
                div className = "requests-list" > {
                    requests.map((request) => ( <
                            div key = { request.id }
                            className = "request-card" >
                            <
                            div className = "request-info" >
                            <
                            h3 > { request.product_name } < /h3> <
                            p > 👨‍🌾Farmer: { request.farmer_name }({ request.farmer_phone }) < /p> <
                            p > 📦Quantity: { request.quantity } < /p> {
                            request.note && < p > 📝Note: { request.note } < /p>} <
                            p > 💰Product Amount: { request.product_amount } < /p> <
                            p > 📅Date: { new Date(request.created_at).toLocaleDateString() } < /p> { getStatusBadge(request.status) } {
                            request.handled_by_admin && ( <
                                p > 👤Handled by: { request.handled_by_admin } < /p>
                            )
                        } <
                        /div> <
                        div className = "request-actions" > {
                            request.status === 'pending' && ( <
                                >
                                <
                                div className = "request-checkbox" >
                                <
                                input type = "checkbox"
                                checked = { selectedRequests.includes(request.id) }
                                onChange = {
                                    () => toggleRequestSelection(request.id)
                                }
                                /> <
                                label > Select < /label> < /
                                div > <
                                div className = "status-options" >
                                <
                                label >
                                <
                                input type = "radio"
                                name = { `status-${request.id}` }
                                onChange = {
                                    () => handleStatusUpdate(request, 'approve')
                                }
                                />
                                Approve(Intelligent) <
                                /label> <
                                label >
                                <
                                input type = "radio"
                                name = { `status-${request.id}` }
                                onChange = {
                                    () => handleStatusUpdate(request, 'accept')
                                }
                                />
                                Accept <
                                /label> <
                                label >
                                <
                                input type = "radio"
                                name = { `status-${request.id}` }
                                onChange = {
                                    () => handleStatusUpdate(request, 'reject')
                                }
                                />
                                Reject <
                                /label> < /
                                div > <
                                />
                            )
                        } {
                            (request.status === 'accepted' || request.status === 'approved' || request.status === 'rejected') && ( <
                                p > Processed < /p>
                            )
                        } <
                        /div> < /
                        div >
                    ))
            } {
                requests.length === 0 && ( <
                    p > No requests found in your kebele. < /p>
                )
            } <
            /div>

        { /* Status Update Modal */ } {
            showRequestModal && selectedRequest && ( <
                div className = "modal" >
                <
                div className = "modal-content" >
                <
                h3 > Update Request Status < /h3> <
                p >
                You are about to update the request
                for "{selectedRequest.product_name}"
                from { selectedRequest.farmer_name }. <
                /p>

                {
                    requestStatus === 'approve' && ( <
                        div className = "form-group" >
                        <
                        label > Decision Reason(Optional): < /label> <
                        textarea value = { decisionReason }
                        onChange = {
                            (e) => setDecisionReason(e.target.value)
                        }
                        rows = "3"
                        placeholder = "Add any notes about your decision..." /
                        >
                        <
                        /div>
                    )
                }

                <
                div className = "modal-actions" >
                <
                button onClick = { confirmStatusUpdate } >
                Confirm { requestStatus === 'approve' ? 'Intelligent Approval' : requestStatus } <
                /button> <
                button onClick = {
                    () => {
                        setShowRequestModal(false);
                        setSelectedRequest(null);
                        setRequestStatus('');
                        setDecisionReason('');
                    }
                } >
                Cancel <
                /button> < /
                div > <
                /div> < /
                div >
            )
        } <
        /div>
    )
}

{ /* Farmer Modal */ } {
    showFarmerModal && ( <
        div className = "modal" >
        <
        div className = "modal-content" >
        <
        h3 > { editingFarmer ? 'Edit Farmer' : 'Register New Farmer' } < /h3> <
        form onSubmit = { handleFarmerSubmit } >
        <
        div className = "form-group" >
        <
        label > Full Name: < /label> <
        input type = "text"
        value = { farmerForm.fullName }
        onChange = {
            (e) => setFarmerForm({...farmerForm, fullName: e.target.value })
        }
        required /
        >
        <
        /div> <
        div className = "form-group" >
        <
        label > Phone Number: < /label> <
        input type = "tel"
        value = { farmerForm.phoneNumber }
        onChange = {
            (e) => setFarmerForm({...farmerForm, phoneNumber: e.target.value })
        }
        required /
        >
        <
        /div> <
        div className = "form-group" >
        <
        label > Password: < /label> <
        input type = "password"
        value = { farmerForm.password }
        onChange = {
            (e) => setFarmerForm({...farmerForm, password: e.target.value })
        }
        required = {!editingFarmer }
        /> < /
        div > <
        div className = "form-group" >
        <
        label > Confirm Password: < /label> <
        input type = "password"
        value = { farmerForm.confirmPassword }
        onChange = {
            (e) => setFarmerForm({...farmerForm, confirmPassword: e.target.value })
        }
        required = {!editingFarmer }
        /> < /
        div > <
        div className = "modal-actions" >
        <
        button type = "submit" > { editingFarmer ? 'Update Farmer' : 'Register Farmer' } < /button> <
        button type = "button"
        onClick = {
            () => {
                setShowFarmerModal(false);
                setEditingFarmer(null);
                setFarmerForm({ fullName: '', phoneNumber: '', password: '', confirmPassword: '' });
            }
        } >
        Cancel <
        /button> < /
        div > <
        /form> < /
        div > <
        /div>
    )
}

{ /* Product Modal */ } {
    showProductModal && ( <
        div className = "modal" >
        <
        div className = "modal-content" >
        <
        h3 > { editingProduct ? 'Edit Product' : 'Add New Product' } < /h3> <
        form onSubmit = { handleProductSubmit } >
        <
        div className = "form-group" >
        <
        label > Product Name: < /label> <
        input type = "text"
        value = { productForm.name }
        onChange = {
            (e) => setProductForm({...productForm, name: e.target.value })
        }
        required /
        >
        <
        /div> <
        div className = "form-group" >
        <
        label > Description: < /label> <
        textarea value = { productForm.description }
        onChange = {
            (e) => setProductForm({...productForm, description: e.target.value })
        }
        rows = "3" /
        >
        <
        /div> <
        div className = "form-group" >
        <
        label > Category: < /label> <
        input type = "text"
        value = { productForm.category }
        onChange = {
            (e) => setProductForm({...productForm, category: e.target.value })
        }
        required /
        >
        <
        /div> <
        div className = "form-group" >
        <
        label > Amount: < /label> <
        input type = "number"
        value = { productForm.amount }
        onChange = {
            (e) => setProductForm({...productForm, amount: e.target.value })
        }
        required /
        >
        <
        /div> <
        div className = "modal-actions" >
        <
        button type = "submit" > { editingProduct ? 'Update Product' : 'Add Product' } < /button> <
        button type = "button"
        onClick = {
            () => {
                setShowProductModal(false);
                setEditingProduct(null);
                setProductForm({ name: '', description: '', category: '', amount: '' });
            }
        } >
        Cancel <
        /button> < /
        div > <
        /form> < /
        div > <
        /div>
    )
} <
/main> < /
div > <
    /div>
);
};

export default KebeleDashboard;
