import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './FederalDashboard.css';

const FederalDashboard = () => {
    const [activeTab, setActiveTab] = useState('overview');
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    // Product Management State
    const [products, setProducts] = useState([]);
    const [showProductForm, setShowProductForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        amount: '',
        description: ''
    });

    // Admin Management State
    const [admins, setAdmins] = useState([]);
    const [showAdminForm, setShowAdminForm] = useState(false);
    const [editingAdmin, setEditingAdmin] = useState(null);
    const [adminForm, setAdminForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        role: 'Region',
        region_name: ''
    });
    const [adminError, setAdminError] = useState('');

    // Stats State
    const [stats, setStats] = useState({
        totalRequests: 0,
        pendingRequests: 0,
        approvedRequests: 0,
        rejectedRequests: 0,
        totalAdmins: 0,
        totalProducts: 0
    });

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Federal' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        fetchDashboardData();
    }, [navigate]);

    const fetchDashboardData = async() => {
        try {
            const token = localStorage.getItem('token');

            // Fetch products - CORRECTED ENDPOINT
            const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
                setStats(prev => ({...prev, totalProducts: productsData.length }));
            }

            // Fetch admins in scope - CORRECT ENDPOINT (no change needed)
            const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (adminsResponse.ok) {
                const adminsData = await adminsResponse.json();
                const regionAdmins = adminsData.filter(admin => admin.role === 'Region');
                setAdmins(regionAdmins);
                setStats(prev => ({...prev, totalAdmins: regionAdmins.length }));
            }

            // Fetch requests stats - CORRECTED ENDPOINT
            const requestsResponse = await fetch('http://localhost:5000/api/admins/getrequests', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                const totalRequests = requestsData.length;
                const pendingRequests = requestsData.filter(req => req.status === 'Pending').length;
                const approvedRequests = requestsData.filter(req => req.status === 'Accepted').length;
                const rejectedRequests = requestsData.filter(req => req.status === 'Rejected').length;

                setStats(prev => ({
                    ...prev,
                    totalRequests,
                    pendingRequests,
                    approvedRequests,
                    rejectedRequests
                }));
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    const handleProductSubmit = async(e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            // CORRECTED ENDPOINTS
            const url = editingProduct ?
                `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                'http://localhost:5000/api/admins/addproduct';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(productForm)
            });

            if (response.ok) {
                setShowProductForm(false);
                setEditingProduct(null);
                setProductForm({ name: '', category: '', amount: '', description: '' });
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error saving product:', errorData.message);
                alert(errorData.message || 'Error saving product');
            }
        } catch (error) {
            console.error('Error saving product:', error);
            alert('Error saving product');
        }
    };

    const deleteProduct = async(productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            // CORRECTED ENDPOINT
            const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error deleting product:', errorData.message);
                alert(errorData.message || 'Error deleting product');
            }
        } catch (error) {
            console.error('Error deleting product:', error);
            alert('Error deleting product');
        }
    };
    const editAdmin = (admin) => {
        setEditingAdmin(admin);
        setAdminForm({
            fullName: admin.full_name || '',
            phoneNumber: admin.phone_number || '',
            password: '',
            confirmPassword: '',
            role: admin.role || 'Region',
            region_name: admin.region_name || ''
        });
        setShowAdminForm(true);
    };

    const editProduct = (product) => {
        setEditingProduct(product);
        setProductForm({
            name: product.name || '',
            category: product.category || '',
            amount: product.amount || '',
            description: product.description || ''
        });
        setShowProductForm(true);
    };

    if (!user) {
        return <div className = "loading" > Loading... < /div>;
    }

    return ( <
        div className = "federal-dashboard" > { /* Sidebar */ } <
        div className = "sidebar" >
        <
        h2 > Federal Dashboard < /h2> <
        nav >
        <
        button className = { activeTab === 'overview' ? 'active' : '' }
        onClick = {
            () => setActiveTab('overview')
        } > ðŸ“ŠOverview <
        /button> <
        button className = { activeTab === 'products' ? 'active' : '' }
        onClick = {
            () => setActiveTab('products')
        } > ðŸ“¦Products <
        /button> <
        button className = { activeTab === 'admins' ? 'active' : '' }
        onClick = {
            () => setActiveTab('admins')
        } > ðŸ‘¥Admins <
        /button> <
        button className = { activeTab === 'requests' ? 'active' : '' }
        onClick = {
            () => setActiveTab('requests')
        } > ðŸ“‹Requests <
        /button> < /
        nav > <
        /div>

        { /* Main Content */ } <
        div className = "main-content" >
        <
        header >
        <
        h1 > Welcome, { user.fullName }(Federal Admin) < /h1> <
        button onClick = { handleLogout }
        className = "logout-btn" >
        Logout <
        /button> < /
        header >

        <
        main > { /* Overview Tab */ } {
            activeTab === 'overview' && ( <
                div className = "overview-tab" >
                <
                h2 > System Overview < /h2> <
                div className = "stats-grid" >
                <
                div className = "stat-card" >
                <
                h3 > { stats.totalRequests } < /h3> <
                p > Total Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.pendingRequests } < /h3> <
                p > Pending Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.approvedRequests } < /h3> <
                p > Approved Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.rejectedRequests } < /h3> <
                p > Rejected Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.totalAdmins } < /h3> <
                p > Region Admins < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.totalProducts } < /h3> <
                p > Products < /p> < /
                div > <
                /div>

                <
                div className = "quick-actions" >
                <
                h2 > Quick Actions < /h2> <
                div className = "actions-grid" >
                <
                button onClick = {
                    () => setActiveTab('admins')
                }
                className = "action-btn" > ðŸ‘¥Create Region Admin <
                /button> <
                button onClick = {
                    () => setActiveTab('products')
                }
                className = "action-btn" > ðŸ“¦Add Product <
                /button> <
                button className = "action-btn" > ðŸ“ŠGenerate Reports <
                /button> <
                button className = "action-btn" > âš™ï¸System Settings <
                /button> < /
                div > <
                /div> < /
                div >
            )
        }

        { /* Products Tab */ } {
            activeTab === 'products' && ( <
                div className = "products-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Product Management < /h2> <
                button onClick = {
                    () => setShowProductForm(true)
                }
                className = "add-btn" >
                Add Product <
                /button> < /
                div >

                { /* Product Form Modal */ } {
                    showProductForm && ( <
                        div className = "modal" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > { editingProduct ? 'Edit Product' : 'Add Product' } < /h3> <
                        form onSubmit = { handleProductSubmit } >
                        <
                        input type = "text"
                        placeholder = "Product Name"
                        value = { productForm.name }
                        onChange = {
                            (e) => setProductForm({...productForm, name: e.target.value })
                        }
                        required /
                        >
                        <
                        input type = "text"
                        placeholder = "Category"
                        value = { productForm.category }
                        onChange = {
                            (e) => setProductForm({...productForm, category: e.target.value })
                        }
                        required /
                        >
                        <
                        input type = "number"
                        placeholder = "Amount"
                        value = { productForm.amount }
                        onChange = {
                            (e) => setProductForm({...productForm, amount: e.target.value })
                        }
                        required /
                        >
                        <
                        textarea placeholder = "Description"
                        value = { productForm.description }
                        onChange = {
                            (e) => setProductForm({...productForm, description: e.target.value })
                        }
                        rows = "3"
                        required /
                        >
                        <
                        div className = "modal-actions" >
                        <
                        button type = "submit" > Save < /button> <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowProductForm(false);
                                setEditingProduct(null);
                                setProductForm({ name: '', category: '', amount: '', description: '' });
                            }
                        } >
                        Cancel <
                        /button> < /
                        div > <
                        /form> < /
                        div > <
                        /div>
                    )
                }

                { /* Products List */ } <
                div className = "products-grid" > {
                    products.map((product) => ( <
                        div key = { product.id }
                        className = "product-card" >
                        <
                        h3 > { product.name } < /h3> <
                        p className = "category" > Category: { product.category } < /p> <
                        p className = "amount" > Amount: { product.amount } < /p> <
                        p className = "description" > { product.description } < /p> <
                        div className = "product-actions" >
                        <
                        button onClick = {
                            () => editProduct(product)
                        }
                        className = "edit-btn" >
                        Edit <
                        /button> <
                        button onClick = {
                            () => deleteProduct(product.id)
                        }
                        className = "delete-btn" >
                        Delete <
                        /button> < /
                        div > <
                        /div>
                    ))
                } <
                /div> < /
                div >
            )
        }

        { /* Admins Tab */ } {
            activeTab === 'admins' && ( <
                div className = "admins-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Region Admin Management < /h2> <
                button onClick = {
                    () => {
                        setEditingAdmin(null);
                        setAdminForm({
                            fullName: '',
                            phoneNumber: '',
                            password: '',
                            confirmPassword: '',
                            role: 'Region',
                            region_name: ''
                        });
                        setShowAdminForm(true);
                    }
                }
                className = "add-btn" >
                Register Region Admin <
                /button> < /
                div >

                { /* Admin Form Modal */ } {
                    showAdminForm && ( <
                            div className = "modal" >
                            <
                            div className = "modal-content" >
                            <
                            h3 > { editingAdmin ? 'Edit Region Admin' : 'Register Region Admin' } < /h3> {
                            adminError && ( <
                                div className = "error-message" > { adminError } < /div>
                            )
                        } <
                        form onSubmit = { handleAdminSubmit } >
                        <
                        input type = "text"
                    placeholder = "Full Name"
                    value = { adminForm.fullName }
                    onChange = {
                        (e) => setAdminForm({...adminForm, fullName: e.target.value })
                    }
                    required /
                        >
                        <
                        input type = "tel"
                    placeholder = "Phone Number"
                    value = { adminForm.phoneNumber }
                    onChange = {
                        (e) => setAdminForm({...adminForm, phoneNumber: e.target.value })
                    }
                    required /
                        >
                        {!editingAdmin && ( <
                                >
                                <
                                input type = "password"
                                placeholder = "Password"
                                value = { adminForm.password }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, password: e.target.value })
                                }
                                required /
                                >
                                <
                                input type = "password"
                                placeholder = "Confirm Password"
                                value = { adminForm.confirmPassword }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, confirmPassword: e.target.value })
                                }
                                required /
                                >
                                <
                                />
                            )
                        } {
                            editingAdmin && ( <
                                >
                                <
                                input type = "password"
                                placeholder = "New Password (leave empty to keep current)"
                                value = { adminForm.password }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, password: e.target.value })
                                }
                                /> <
                                input type = "password"
                                placeholder = "Confirm New Password"
                                value = { adminForm.confirmPassword }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, confirmPassword: e.target.value })
                                }
                                /> < / >
                            )
                        } <
                        input type = "text"
                    placeholder = "Region Name (Must be unique)"
                    value = { adminForm.region_name }
                    onChange = {
                        (e) => setAdminForm({...adminForm, region_name: e.target.value })
                    }
                    required /
                        >
                        <
                        div className = "modal-actions" >
                        <
                        button type = "submit" > { editingAdmin ? 'Update' : 'Register' } < /button> <
                    button type = "button"
                    onClick = {
                            () => {
                                setShowAdminForm(false);
                                setEditingAdmin(null);
                                setAdminError('');
                                setAdminForm({
                                    fullName: '',
                                    phoneNumber: '',
                                    password: '',
                                    confirmPassword: '',
                                    role: 'Region',
                                    region_name: ''
                                });
                            }
                        } >
                        Cancel <
                        /button> < /
                    div > <
                        /form> < /
                    div > <
                        /div>
                )
            }

            { /* Admins List */ } <
            div className = "admins-list" > {
                    admins.map((admin) => ( <
                        div key = { admin.id }
                        className = "admin-card" >
                        <
                        h3 > { admin.full_name } < /h3> <
                        p > Phone: { admin.phone_number } < /p> <
                        p > Role: { admin.role } < /p> <
                        p > Region: { admin.region_name } < /p> <
                        div className = "admin-actions" >
                        <
                        button onClick = {
                            () => editAdmin(admin)
                        }
                        className = "edit-btn" >
                        Edit <
                        /button> <
                        button onClick = {
                            () => deleteAdmin(admin.id)
                        }
                        className = "delete-btn" >
                        Delete <
                        /button> < /
                        div > <
                        /div>
                    ))
                } <
                /div> < /
            div >
        )
    }

    { /* Requests Tab */ } {
        activeTab === 'requests' && ( <
            div className = "requests-tab" >
            <
            h2 > Request Management < /h2> <
            p > Request management functionality will be implemented here. < /p> < /
            div >
        )
    } <
    /main> < /
    div > <
        /div>
);
};

export default FederalDashboard;
