import React, { useEffect, useMemo, useState } from 'react';
import { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import api from '../api';

export default function MasterData() {
    const [admins, setAdmins] = useState([]);
    const [products, setProducts] = useState([]);
    const [farmerRequests, setFarmerRequests] = useState([]);
    const [loading, setLoading] = useState(false);
    const [modal, setModal] = useState(null); // 'addAdmin' | 'editAdmin' | 'addProduct' | 'editProduct' | 'viewRequest'
    const [adminForm, setAdminForm] = useState({
        id: null,
        fullName: '',
        phoneNumber: '',
        password: '',
        role: '',
        region_name: '',
        zone_name: '',
        woreda_name: '',
        kebele_name: ''
    });
    const [productForm, setProductForm] = useState({
        id: null,
        name: '',
        category: '',
        description: ''
    });
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [error, setError] = useState('');

    // current user saved at login
    const user = useMemo(() => {
        try { return JSON.parse(localStorage.getItem('user') || '{}'); } catch { return {}; }
    }, []);

    // next-lower role (backend rule)
    const lowerRole = useMemo(() => {
        const map = { Federal: 'Region', Region: 'Zone', Zone: 'Woreda', Woreda: 'Kebele', Kebele: 'Farmer' };
        return map[user.role] || '';
    }, [user.role]);

    // which location fields are required for selected role (backend validation)
    const needRegion = ['Region', 'Zone', 'Woreda', 'Kebele', 'Farmer'].includes(adminForm.role);
    const needZone = ['Zone', 'Woreda', 'Kebele', 'Farmer'].includes(adminForm.role);
    const needWoreda = ['Woreda', 'Kebele', 'Farmer'].includes(adminForm.role);
    const needKebele = ['Kebele', 'Farmer'].includes(adminForm.role);

    // label shown based on lower role
    const lowerRoleLabel = lowerRole ? (lowerRole === 'Farmer' ? 'Farmers' : `${lowerRole} Admins`) : 'Lower-level Accounts';

    useEffect(() => {
        refreshAll();
    }, []);

    async function refreshAll() {
        setLoading(true);
        setError('');
        try {
            const [adminsRes, productsRes, requestsRes] = await Promise.all([
                api.get('/admin/list'),
                api.get('/admin/products'),
                api.get('/admin/farmer-requests')
            ]);
            setAdmins(adminsRes.data || []);
            setProducts(productsRes.data || []);
            setFarmerRequests(requestsRes.data || []);
        } catch (e) {
            setError(readErr(e));
        } finally {
            setLoading(false);
        }
    }

    // Helpers
    function readErr(e) {
        if (e.response && e.response.data && e.response.data.message) {
            return e.response.data.message;
        }
        return e.message || 'An error occurred';
    }

    function closeModal() {
        setModal(null);
        setError('');
    }

    // ===== Admins =====
    function openAddAdmin() {
        setAdminForm({
            id: null,
            fullName: '',
            phoneNumber: '',
            password: '',
            role: lowerRole, // fixed as required by backend rule
            region_name: '',
            zone_name: '',
            woreda_name: '',
            kebele_name: ''
        });
        setModal('addAdmin');
    }

    function openEditAdmin(a) {
        setAdminForm({
            id: a.id,
            fullName: a.full_name || '',
            phoneNumber: a.phone_number || '',
            password: '', // not editable here
            role: a.role || lowerRole, // show existing
            region_name: a.region_name || '',
            zone_name: a.zone_name || '',
            woreda_name: a.woreda_name || '',
            kebele_name: a.kebele_name || ''
        });
        setModal('editAdmin');
    }

    async function submitAdmin(e) {
        e.preventDefault();
        setError('');
        try {
            if (modal === 'addAdmin') {
                await api.post('/admin/register', {
                    fullName: adminForm.fullName,
                    phoneNumber: adminForm.phoneNumber,
                    password: adminForm.password,
                    role: adminForm.role,
                    region_name: needRegion ? adminForm.region_name : null,
                    zone_name: needZone ? adminForm.zone_name : null,
                    woreda_name: needWoreda ? adminForm.woreda_name : null,
                    kebele_name: needKebele ? adminForm.kebele_name : null
                });
            } else {
                await api.put(`/admin/edit/${adminForm.id}`, {
                    fullName: adminForm.fullName || undefined,
                    phoneNumber: adminForm.phoneNumber || undefined,
                    region_name: adminForm.region_name || undefined,
                    zone_name: adminForm.zone_name || undefined,
                    woreda_name: adminForm.woreda_name || undefined,
                    kebele_name: adminForm.kebele_name || undefined
                });
            }
            closeModal();
            refreshAll();
        } catch (e2) {
            setError(readErr(e2));
        }
    }

    async function deleteAdmin(id) {
        if (!window.confirm('Are you sure you want to delete this admin?')) return;
        setError('');
        try {
            await api.delete(`/admin/delete/${id}`);
            refreshAll();
        } catch (e) {
            setError(readErr(e));
        }
    }

    // show only the next-lower level in the main grid
    const visibleAdmins = admins
        .filter(r => r.type === 'admin' && (lowerRole ? r.role === lowerRole : true));

    // ===== Products =====
    function openAddProduct() {
        setProductForm({ id: null, name: '', category: '', description: '' });
        setModal('addProduct');
    }

    function openEditProduct(p) {
        setProductForm({
            id: p.id,
            name: p.name || '',
            category: p.category || '',
            description: p.description || ''
        });
        setModal('editProduct');
    }

    async function submitProduct(e) {
        e.preventDefault();
        setError('');
        try {
            if (modal === 'addProduct') {
                await api.post('/admin/products', {
                    name: productForm.name,
                    category: productForm.category,
                    description: productForm.description
                });
            } else {
                await api.put(`/admin/products/${productForm.id}`, {
                    name: productForm.name || undefined,
                    category: productForm.category || undefined,
                    description: productForm.description || undefined
                });
            }
            closeModal();
            refreshAll();
        } catch (e2) {
            setError(readErr(e2));
        }
    }

    async function deleteProduct(id) {
        if (!window.confirm('Are you sure you want to delete this product?')) return;
        setError('');
        try {
            await api.delete(`/admin/products/${id}`);
            refreshAll();
        } catch (e) {
            setError(readErr(e));
        }
    }

    // ===== Farmer Requests =====
    function openRequestView(request) {
        setSelectedRequest(request);
        setModal('viewRequest');
    }

    async function updateRequestStatus(status) {
        try {
            await api.put(`/admin/farmer-requests/${selectedRequest.id}`, { status });
            closeModal();
            refreshAll();
        } catch (e) {
            setError(readErr(e));
        }
    }

    return ( <
            div className = "max-w-7xl mx-auto space-y-10" > { /* header */ } <
            div className = "flex items-center justify-between" >
            <
            h1 className = "text-2xl font-semibold" > Master Data < /h1> <
            div className = "flex items-center gap-2" >
            <
            button onClick = { refreshAll }
            className = "px-3 py-2 bg-gray-200 rounded hover:bg-gray-300" >
            Refresh <
            /button> < /
            div > <
            /div>

            {
                error && ( <
                    div className = "bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded" > { error } < /div>
                )
            }

            { /* Farmer Requests card */ } <
            div className = "bg-white rounded-2xl shadow p-5" >
            <
            div className = "flex items-center justify-between mb-4" >
            <
            div >
            <
            h2 className = "text-lg font-bold" > Farmer Requests < /h2> <
            p className = "text-sm text-gray-500" > Requests from farmers in your scope < /p> < /
            div > <
            /div>

            <
            div className = "overflow-x-auto" >
            <
            table className = "w-full text-sm" >
            <
            thead >
            <
            tr className = "text-left bg-gray-50" >
            <
            th className = "p-3" > Farmer < /th> <
            th className = "p-3" > Product < /th> <
            th className = "p-3" > Quantity < /th> <
            th className = "p-3" > Status < /th> <
            th className = "p-3" > Date < /th> <
            th className = "p-3 text-center" > Actions < /th> < /
            tr > <
            /thead> <
            tbody > {
                farmerRequests.map(r => ( <
                    tr key = { r.id }
                    className = "border-b last:border-0" >
                    <
                    td className = "p-3" > { r.farmer_name || '-' } < /td> <
                    td className = "p-3" > { r.product_name || '-' } < /td> <
                    td className = "p-3" > { r.quantity || '-' } < /td> <
                    td className = "p-3" >
                    <
                    span className = { `px-2 py-1 rounded text-xs ${
                                            r.status === 'accepted' ? 'bg-green-100 text-green-800' :
                                            r.status === 'rejected' ? 'bg-red-100 text-red-800' :
                                            'bg-yellow-100 text-yellow-800'
                                        }` } > { r.status } <
                    /span> < /
                    td > <
                    td className = "p-3" > { new Date(r.created_at).toLocaleDateString() } < /td> <
                    td className = "p-3" >
                    <
                    div className = "flex items-center justify-center gap-2" >
                    <
                    button onClick = {
                        () => openRequestView(r)
                    }
                    className = "px-2 py-1 bg-blue-600 text-white rounded hover:bg-blue-700" >
                    View <
                    /button> < /
                    div > <
                    /td> < /
                    tr >
                ))
            } {
                farmerRequests.length === 0 && ( <
                    tr >
                    <
                    td className = "p-4 text-center text-gray-500"
                    colSpan = "6" >
                    No requests found in your scope <
                    /td> < /
                    tr >
                )
            } <
            /tbody> < /
            table > <
            /div> < /
            div >

            { /* Admins card */ } <
            div className = "bg-white rounded-2xl shadow p-5" >
            <
            div className = "flex items-center justify-between mb-4" >
            <
            div >
            <
            h2 className = "text-lg font-bold" > { lowerRoleLabel } < /h2> <
            p className = "text-sm text-gray-500" >
            Create, edit, or remove { lowerRole ? lowerRole.toLowerCase() : 'lower-level' }
            accounts in your scope <
            /p> < /
            div > {
                lowerRole && ( <
                    button onClick = { openAddAdmin }
                    className = "px-3 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700" >
                    +Add { lowerRole === 'Farmer' ? 'Farmer' : `${lowerRole} Admin` } <
                    /button>
                )
            } <
            /div>

            <
            div className = "overflow-x-auto" >
            <
            table className = "w-full text-sm" >
            <
            thead >
            <
            tr className = "text-left bg-gray-50" >
            <
            th className = "p-3" > Full name < /th> <
            th className = "p-3" > Phone < /th> <
            th className = "p-3" > Role < /th> <
            th className = "p-3" > Region < /th> <
            th className = "p-3" > Zone < /th> <
            th className = "p-3" > Woreda < /th> <
            th className = "p-3" > Kebele < /th> <
            th className = "p-3 text-center" > Actions < /th> < /
            tr > <
            /thead> <
            tbody > {
                visibleAdmins.map(a => ( <
                    tr key = { a.id }
                    className = "border-b last:border-0" >
                    <
                    td className = "p-3" > { a.full_name || '-' } < /td> <
                    td className = "p-3" > { a.phone_number || '-' } < /td> <
                    td className = "p-3" > { a.role || '-' } < /td> <
                    td className = "p-3" > { a.region_name || '-' } < /td> <
                    td className = "p-3" > { a.zone_name || '-' } < /td> <
                    td className = "p-3" > { a.woreda_name || '-' } < /td> <
                    td className = "p-3" > { a.kebele_name || '-' } < /td> <
                    td className = "p-3" >
                    <
                    div className = "flex items-center justify-center gap-2" >
                    <
                    button onClick = {
                        () => openEditAdmin(a)
                    }
                    className = "px-2 py-1 bg-blue-600 text-white rounded hover:bg-blue-700" >
                    Edit <
                    /button> <
                    button onClick = {
                        () => deleteAdmin(a.id)
                    }
                    className = "px-2 py-1 bg-red-600 text-white rounded hover:bg-red-700" >
                    Delete <
                    /button> < /
                    div > <
                    /td> < /
                    tr >
                ))
            } {
                visibleAdmins.length === 0 && ( <
                    tr >
                    <
                    td className = "p-4 text-center text-gray-500"
                    colSpan = "8" >
                    No records found in your scope <
                    /td> < /
                    tr >
                )
            } <
            /tbody> < /
            table > <
            /div> < /
            div >

            { /* Products card */ } <
            div className = "bg-white rounded-2xl shadow p-5" >
            <
            div className = "flex items-center justify-between mb-4" >
            <
            div >
            <
            h2 className = "text-lg font-bold" > Products < /h2> <
            p className = "text-sm text-gray-500" > Products you created < /p> < /
            div > <
            button onClick = { openAddProduct }
            className = "px-3 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700" >
            +Add Product <
            /button> < /
            div >

            <
            div className = "overflow-x-auto" >
            <
            table className = "w-full text-sm" >
            <
            thead >
            <
            tr className = "text-left bg-gray-50" >
            <
            th className = "p-3" > Name < /th> <
            th className = "p-3" > Category < /th> <
            th className = "p-3" > Description < /th> <
            th className = "p-3 text-center" > Actions < /th> < /
            tr > <
            /thead> <
            tbody > {
                products.map(p => ( <
                    tr key = { p.id }
                    className = "border-b last:border-0" >
                    <
                    td className = "p-3" > { p.name } < /td> <
                    td className = "p-3" > { p.category || '-' } < /td> <
                    td className = "p-3" > { p.description || '-' } < /td> <
                    td className = "p-3" >
                    <
                    div className = "flex items-center justify-center gap-2" >
                    <
                    button onClick = {
                        () => openEditProduct(p)
                    }
                    className = "px-2 py-1 bg-blue-600 text-white rounded hover:bg-blue-700" >
                    Edit <
                    /button> <
                    button onClick = {
                        () => deleteProduct(p.id)
                    }
                    className = "px-2 py-1 bg-red-600 text-white rounded hover:bg-red-700" >
                    Delete <
                    /button> < /
                    div > <
                    /td> < /
                    tr >
                ))
            } {
                products.length === 0 && ( <
                    tr >
                    <
                    td className = "p-4 text-center text-gray-500"
                    colSpan = "4" >
                    No products yet <
                    /td> < /
                    tr >
                )
            } <
            /tbody> < /
            table > <
            /div> < /
            div >

            { /* Admin modal */ } {
                modal && (modal === 'addAdmin' || modal === 'editAdmin') && ( <
                        div className = "fixed inset-0 bg-black/40 flex items-center justify-center p-4" >
                        <
                        div className = "bg-white rounded-2xl shadow-lg w-full max-w-lg p-6" >
                        <
                        div className = "flex items-center justify-between mb-4" >
                        <
                        h3 className = "text-lg font-semibold" > { modal === 'addAdmin' ? `Add ${lowerRole === 'Farmer' ? 'Farmer' : `${lowerRole} Admin`}` : 'Edit Admin'}
                            </h3>
                            <button onClick={closeModal} className="px-2 py-1 rounded hover:bg-gray-100"></button>
                        </div>

                        {error && (
                            <div className="bg-red-50 border border-red-200 text-red-700 px-3 py-2 rounded mb-3">
                                {error}
                            </div>
                        )}

                        <form onSubmit={submitAdmin} className="space-y-3">
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                                <label className="flex flex-col text-sm">
                                    <span className="mb-1">Full name</span>
                                    <input
                                        className="border rounded px-3 py-2"
                                        value={adminForm.fullName}
                                        onChange={e => setAdminForm(v => ({ ...v, fullName: e.target.value }))}
                                        required
                                    />
                                </label>

                                <label className="flex flex-col text-sm">
                                    <span className="mb-1">Phone number</span>
                                    <input
                                        className="border rounded px-3 py-2"
                                        value={adminForm.phoneNumber}
                                        onChange={e => setAdminForm(v => ({ ...v, phoneNumber: e.target.value }))}
                                        required
                                    />
                                </label>

                                <label className="flex flex-col text-sm opacity-70">
                                    <span className="mb-1">Role (fixed)</span>
                                    <input className="border rounded px-3 py-2 bg-gray-50" value={adminForm.role} disabled readOnly />
                                </label>

                                {modal === 'addAdmin' && (
                                    <label className="flex flex-col text-sm">
                                        <span className="mb-1">Password</span>
                                        <input
                                            type="password"
                                            className="border rounded px-3 py-2"
                                            value={adminForm.password}
                                            onChange={e => setAdminForm(v => ({ ...v, password: e.target.value }))}
                                            required
                                        />
                                    </label>
                                )}
                            </div>

                            {/* location fields based on role */}
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                                {needRegion && (
                                    <label className="flex flex-col text-sm">
                                        <span className="mb-1">Region</span>
                                        <input
                                            className="border rounded px-3 py-2"
                                            value={adminForm.region_name}
                                            onChange={e => setAdminForm(v => ({ ...v, region_name: e.target.value }))}
                                            required
                                        />
                                    </label>
                                )}
                                {needZone && (
                                    <label className="flex flex-col text-sm">
                                        <span className="mb-1">Zone</span>
                                        <input
                                            className="border rounded px-3 py-2"
                                            value={adminForm.zone_name}
                                            onChange={e => setAdminForm(v => ({ ...v, zone_name: e.target.value }))}
                                            required
                                        />
                                    </label>
                                )}
                                {needWoreda && (
                                    <label className="flex flex-col text-sm">
                                        <span className="mb-1">Woreda</span>
                                        <input
                                            className="border rounded px-3 py-2"
                                            value={adminForm.woreda_name}
                                            onChange={e => setAdminForm(v => ({ ...v, woreda_name: e.target.value }))}
                                            required
                                        />
                                    </label>
                                )}
                                {needKebele && (
                                    <label className="flex flex-col text-sm">
                                        <span className="mb-1">Kebele</span>
                                        <input
                                            className="border rounded px-3 py-2"
                                            value={adminForm.kebele_name}
                                            onChange={e => setAdminForm(v => ({ ...v, kebele_name: e.target.value }))}
                                            required
                                        />
                                    </label>
                                )}
                            </div>

                            <div className="flex items-center justify-end gap-2 pt-2">
                                <button type="button" onClick={closeModal} className="px-3 py-2 rounded bg-gray-100 hover:bg-gray-200">
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    className="px-3 py-2 rounded bg-green-600 text-white hover:bg-green-700"
                                    disabled={loading}
                                >
                                    {modal === 'addAdmin' ? 'Create' : 'Save changes'}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {/* Product modal */}
            {modal && (modal === 'addProduct' || modal === 'editProduct') && (
                <div className="fixed inset-0 bg-black/40 flex items-center justify-center p-4">
                    <div className="bg-white rounded-2xl shadow-lg w-full max-w-lg p-6">
                        <div className="flex items-center justify-between mb-4">
                            <h3 className="text-lg font-semibold">
                                {modal === 'addProduct' ? 'Add Product' : 'Edit Product'}
                            </h3>
                            <button onClick={closeModal} className="px-2 py-1 rounded hover:bg-gray-100"></button>
                        </div>

                        {error && (
                            <div className="bg-red-50 border border-red-200 text-red-700 px-3 py-2 rounded mb-3">
                                {error}
                            </div>
                        )}

                        <form onSubmit={submitProduct} className="space-y-3">
                            <label className="flex flex-col text-sm">
                                <span className="mb-1">Product name</span>
                                <input
                                    className="border rounded px-3 py-2"
                                    value={productForm.name}
                                    onChange={e => setProductForm(v => ({ ...v, name: e.target.value }))}
                                    required
                                />
                            </label>
                            <label className="flex flex-col text-sm">
                                <span className="mb-1">Category</span>
                                <input
                                    className="border rounded px-3 py-2"
                                    value={productForm.category}
                                    onChange={e => setProductForm(v => ({ ...v, category: e.target.value }))}
                                />
                            </label>
                            <label className="flex flex-col text-sm">
                                <span className="mb-1">Description</span>
                                <textarea
                                    className="border rounded px-3 py-2"
                                    rows={3}
                                    value={productForm.description}
                                    onChange={e => setProductForm(v => ({ ...v, description: e.target.value }))}
                                />
                            </label>

                            <div className="flex items-center justify-end gap-2 pt-2">
                                <button type="button" onClick={closeModal} className="px-3 py-2 rounded bg-gray-100 hover:bg-gray-200">
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    className="px-3 py-2 rounded bg-green-600 text-white hover:bg-green-700"
                                    disabled={loading}
                                >
                                    {modal === 'addProduct' ? 'Create' : 'Save changes'}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {/* Request View Modal */}
            {modal === 'viewRequest' && selectedRequest && (
                <div className="fixed inset-0 bg-black/40 flex items-center justify-center p-4">
                    <div className="bg-white rounded-2xl shadow-lg w-full max-w-lg p-6">
                        <div className="flex items-center justify-between mb-4">
                            <h3 className="text-lg font-semibold">Request Details</h3>
                            <button onClick={closeModal} className="px-2 py-1 rounded hover:bg-gray-100"></button>
                        </div>

                        <div className="space-y-4">
                            <div>
                                <h4 className="font-medium text-gray-700">Farmer</h4>
                                <p>{selectedRequest.farmer_name}</p>
                            </div>
                            <div>
                                <h4 className="font-medium text-gray-700">Product</h4>
                                <p>{selectedRequest.product_name}</p>
                            </div>
                            <div>
                                <h4 className="font-medium text-gray-700">Quantity</h4>
                                <p>{selectedRequest.quantity}</p>
                            </div>
                            <div>
                                <h4 className="font-medium text-gray-700">Status</h4>
                                <p className={`inline-block px-2 py-1 rounded text-sm ${
                                    selectedRequest.status === 'accepted' ? 'bg-green-100 text-green-800' :
                                    selectedRequest.status === 'rejected' ? 'bg-red-100 text-red-800' :
                                    'bg-yellow-100 text-yellow-800'
                                }`}>
                                    {selectedRequest.status}
                                </p>
                            </div>
                            <div>
                                <h4 className="font-medium text-gray-700">Date Submitted</h4>
                                <p>{new Date(selectedRequest.created_at).toLocaleString()}</p>
                            </div>

                            {selectedRequest.status === 'pending' && (
                                <div className="flex justify-end gap-2 pt-4">
                                    <button
                                        onClick={() => updateRequestStatus('rejected')}
                                        className="px-3 py-2 bg-red-600 text-white rounded hover:bg-red-700"
                                    >
                                        Reject
                                    </button>
                                    <button
                                        onClick={() => updateRequestStatus('accepted')}
                                        className="px-3 py-2 bg-green-600 text-white rounded hover:bg-green-700"
                                    >
                                        Approve
                                    </button>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            )}

            {loading && (
                <div className="fixed bottom-4 right-4 px-3 py-2 bg-gray-800 text-white rounded shadow">
                    Loading
                </div>
            )}
        </div>
    );
}
