// components/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const [formData, setFormData] = useState({
        phoneNumber: '',
        password: ''
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async(e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            const response = await fetch('http://localhost:5000/api/admins/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    phoneNumber: formData.phoneNumber,
                    password: formData.password
                })
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Login failed');
            }

            // Store token and user data
            localStorage.setItem('token', data.token);
            localStorage.setItem('user', JSON.stringify(data.user));
            localStorage.setItem('userRole', data.user.role);

            // Redirect based on user role
            switch (data.user.role) {
                case 'Federal':
                    navigate('/federal-dashboard');
                    break;
                case 'Region':
                    navigate('/region-dashboard');
                    break;
                case 'Zone':
                    navigate('/zone-dashboard');
                    break;
                case 'Woreda':
                    navigate('/woreda-dashboard');
                    break;
                case 'Kebele':
                    navigate('/kebele-dashboard');
                    break;
                default:
                    navigate('/login');
            }

        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return ( <
        div className = "min-h-screen flex items-center justify-center bg-gray-100" >
        <
        div className = "bg-white p-8 rounded-lg shadow-md w-96" >
        <
        h2 className = "text-2xl font-bold text-center mb-6 text-gray-800" >
        Federal Admin Login <
        /h2>

        {
            error && ( <
                div className = "bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4" > { error } <
                /div>
            )
        }

        <
        form onSubmit = { handleSubmit }
        className = "space-y-4" >
        <
        div >
        <
        label className = "block text-sm font-medium text-gray-700 mb-1" >
        Phone Number <
        /label> <
        input type = "tel"
        value = { formData.phoneNumber }
        onChange = {
            (e) => setFormData({...formData, phoneNumber: e.target.value }) }
        className = "w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500"
        placeholder = "+251911234567"
        required /
        >
        <
        /div>

        <
        div >
        <
        label className = "block text-sm font-medium text-gray-700 mb-1" >
        Password <
        /label> <
        input type = "password"
        value = { formData.password }
        onChange = {
            (e) => setFormData({...formData, password: e.target.value }) }
        className = "w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500"
        placeholder = "Enter your password"
        required /
        >
        <
        /div>

        <
        button type = "submit"
        disabled = { loading }
        className = "w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed" >
        { loading ? 'Logging in...' : 'Login' } <
        /button> <
        /form>

        <
        div className = "mt-6 p-4 bg-gray-50 rounded" >
        <
        h3 className = "font-semibold text-gray-700 mb-2" > Note: < /h3> <
        p className = "text-sm text-gray-600" >
        Only authorized Federal administrators can access this system. <
        /p> <
        /div> <
        /div> <
        /div>
    );
};

export default Login;
