import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const RegionDashboard = () => {
    const [activeTab, setActiveTab] = useState('overview');
    const [user, setUser] = useState(null);
    const [regionInfo, setRegionInfo] = useState(null);
    const [darkMode, setDarkMode] = useState(false);
    const navigate = useNavigate();

    // Request Management State
    const [requests, setRequests] = useState([]);
    const [filteredRequests, setFilteredRequests] = useState([]);
    const [statusFilter, setStatusFilter] = useState('all');
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [showRequestModal, setShowRequestModal] = useState(false);
    const [requestStatus, setRequestStatus] = useState('');
    const [decisionReason, setDecisionReason] = useState('');

    // Product Management State
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [productFilter, setProductFilter] = useState('all'); // all, own, others
    const [showProductForm, setShowProductForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        amount: '',
        price: '',
        description: ''
    });
    const [productError, setProductError] = useState('');

    // Woreda Management State
    const [woredas, setWoredas] = useState([]);
    const [showWoredaForm, setShowWoredaForm] = useState(false);
    const [editingWoreda, setEditingWoreda] = useState(null);
    const [woredaForm, setWoredaForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        woredaName: ''
    });
    const [woredaError, setWoredaError] = useState('');

    // Farmer Management State
    const [farmers, setFarmers] = useState([]);
    const [showFarmerForm, setShowFarmerForm] = useState(false);
    const [editingFarmer, setEditingFarmer] = useState(null);
    const [farmerForm, setFarmerForm] = useState({
        fullName: '',
        phoneNumber: '',
        location: '',
        kebele: ''
    });
    const [farmerError, setFarmerError] = useState('');

    // Stats State
    const [stats, setStats] = useState({
        totalRequests: 0,
        pendingRequests: 0,
        approvedRequests: 0,
        rejectedRequests: 0,
        acceptedRequests: 0,
        totalProducts: 0,
        totalWoredas: 0,
        totalFarmers: 0
    });

    // Apply dark mode to the entire document
    useEffect(() => {
        if (darkMode) {
            document.documentElement.classList.add('dark');
        } else {
            document.documentElement.classList.remove('dark');
        }
    }, [darkMode]);

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Region' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        setRegionInfo({
            name: userData.region_name,
            code: userData.region_name.substring(0, 3).toUpperCase()
        });
        fetchDashboardData();
    }, [navigate]);

    useEffect(() => {
        if (requests.length > 0) {
            filterRequests();
        }
    }, [statusFilter, requests]);

    useEffect(() => {
        if (products.length > 0) {
            filterProducts();
        }
    }, [productFilter, products]);

    const filterRequests = () => {
        let filtered = [...requests];

        // Filter by status
        if (statusFilter !== 'all') {
            filtered = filtered.filter(req => req.status === statusFilter);
        }

        setFilteredRequests(filtered);
    };

    const filterProducts = () => {
        let filtered = [...products];

        // Filter by product ownership
        if (productFilter === 'own') {
            filtered = filtered.filter(product => product.created_by_admin_id === user.id);
        } else if (productFilter === 'others') {
            filtered = filtered.filter(product => product.created_by_admin_id !== user.id);
        }

        setFilteredProducts(filtered);
    };

    const fetchDashboardData = async() => {
        try {
            const token = localStorage.getItem('token');

            // Fetch requests with detailed status
            const requestsResponse = await fetch('http://localhost:5000/api/admins/requests/status', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                setRequests(requestsData);
                filterRequests();

                // Calculate stats from requests data
                const totalRequests = requestsData.length;
                const pendingRequests = requestsData.filter(req => req.status === 'Pending').length;
                const approvedRequests = requestsData.filter(req => req.status === 'Approved').length;
                const acceptedRequests = requestsData.filter(req => req.status === 'Accepted').length;
                const rejectedRequests = requestsData.filter(req => req.status === 'Rejected').length;

                setStats(prev => ({
                    ...prev,
                    totalRequests,
                    pendingRequests,
                    approvedRequests,
                    acceptedRequests,
                    rejectedRequests
                }));
            }

            // Fetch products
            const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
                filterProducts();
                setStats(prev => ({...prev, totalProducts: productsData.length }));
            }

            // Fetch woredas in this region
            const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (adminsResponse.ok) {
                const adminsData = await adminsResponse.json();
                // Filter to only show woredas in this region
                const regionWoredas = adminsData.filter(admin =>
                    admin.role === 'Woreda' && admin.region_name === user.region_name
                );
                setWoredas(regionWoredas);
                setStats(prev => ({...prev, totalWoredas: regionWoredas.length }));
            }

            // Fetch farmers in this region
            const farmersResponse = await fetch('http://localhost:5000/api/admins/farmers', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (farmersResponse.ok) {
                const farmersData = await farmersResponse.json();
                setFarmers(farmersData);
                setStats(prev => ({...prev, totalFarmers: farmersData.length }));
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        navigate('/login');
    };

    // Check if Region admin can act on this request
    const canRegionAdminAct = (request) => {
        // Region admin can only act on requests that have been approved by Woreda admin
        return request.woreda_status === 'Approved' && request.region_status === 'Pending';
    };

    // Check if admin can delete this request (only rejected ones)
    const canDeleteRequest = (request) => {
        return request.status === 'Rejected' && request.region_status === 'Rejected';
    };

    // Update request status at region level
    const updateRequestStatus = async(requestId, status, reason = '') => {
        try {
            const token = localStorage.getItem('token');

            const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    status: status,
                    feedback: reason
                })
            });

            const responseData = await response.json();

            if (response.ok) {
                setShowRequestModal(false);
                setSelectedRequest(null);
                setRequestStatus('');
                setDecisionReason('');
                fetchDashboardData();
                alert(`Request ${status} successfully`);
            } else {
                alert(responseData.message || 'Error updating request');
            }
        } catch (error) {
            console.error('Error updating request:', error);
            alert('Error updating request');
        }
    };

    const handleStatusUpdate = (request, status) => {
        setSelectedRequest(request);
        setRequestStatus(status);
        setShowRequestModal(true);
    };

    const confirmStatusUpdate = () => {
        if (selectedRequest && requestStatus) {
            updateRequestStatus(selectedRequest.id, requestStatus, decisionReason);
        }
    };

    const handleDeleteRequest = async(requestId) => {
        if (window.confirm('Are you sure you want to delete this request?')) {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/deleterequest/${requestId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                    alert('Request deleted successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error deleting request');
                }
            } catch (error) {
                console.error('Error deleting request:', error);
                alert('Error deleting request');
            }
        }
    };

    const viewRequestDetails = async(requestId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                const requestDetails = await response.json();
                setSelectedRequest(requestDetails);
                setShowRequestModal(true);
            } else {
                alert('Error fetching request details');
            }
        } catch (error) {
            console.error('Error fetching request details:', error);
            alert('Error fetching request details');
        }
    };

    const getStatusBadge = (status) => {
        const statusClasses = {
            'Pending': 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300',
            'Approved': 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300',
            'Accepted': 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300',
            'Rejected': 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300'
        };

        return ( <
            span className = { `px-2 py-1 rounded-full text-xs font-medium ${statusClasses[status] || ''}` } > { status } <
            /span>
        );
    };

    const getLevelStatus = (level, request) => {
        const status = request[`${level}_status`];
        const admin = request[`${level}_admin_name`];
        const feedback = request[`${level}_feedback`];
        const date = request[`${level}_approved_at`];

        const formatLevel = level.charAt(0).toUpperCase() + level.slice(1);

        return ( <
            div className = "mb-4 p-3 bg-gray-50 dark:bg-gray-700 rounded" >
            <
            h4 className = "font-semibold dark:text-white" > { formatLevel }
            Level < /h4> <
            p className = "dark:text-gray-300" > < strong > Status: < /strong> {status || 'Pending'}</p > { admin && < p className = "dark:text-gray-300" > < strong > Admin: < /strong> {admin}</p > } { feedback && < p className = "dark:text-gray-300" > < strong > Feedback: < /strong> {feedback}</p > } { date && < p className = "dark:text-gray-300" > < strong > Date: < /strong> {new Date(date).toLocaleString()}</p > } <
            /div>
        );
    };

    // Product Management Functions
    const handleProductSubmit = async(e) => {
        e.preventDefault();
        setProductError('');

        // Validate product amount and price
        if (parseInt(productForm.amount) < 1) {
            setProductError('Product amount must be at least 1');
            return;
        }

        if (parseFloat(productForm.price) <= 0) {
            setProductError('Product price must be greater than 0');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const url = editingProduct ?
                `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                'http://localhost:5000/api/admins/addproduct';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(productForm)
            });

            if (response.ok) {
                setShowProductForm(false);
                setEditingProduct(null);
                setProductForm({ name: '', category: '', amount: '', price: '', description: '' });
                fetchDashboardData();
                alert(editingProduct ? 'Product updated successfully' : 'Product added successfully');
            } else {
                const errorData = await response.json();
                setProductError(errorData.message || 'Error saving product');
            }
        } catch (error) {
            console.error('Error saving product:', error);
            setProductError('Error saving product');
        }
    };

    const deleteProduct = async(productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
                alert('Product deleted successfully');
            } else {
                const errorData = await response.json();
                alert(errorData.message || 'Error deleting product');
            }
        } catch (error) {
            console.error('Error deleting product:', error);
            alert('Error deleting product');
        }
    };

    const editProduct = (product) => {
        // Check if product belongs to current admin
        if (product.created_by_admin_id !== user.id) {
            alert('You can only edit your own products');
            return;
        }

        setEditingProduct(product);
        setProductForm({
            name: product.name || '',
            category: product.category || '',
            amount: product.amount || '',
            price: product.price || '',
            description: product.description || ''
        });
        setShowProductForm(true);
    };

    // Woreda Management Functions
    const handleWoredaSubmit = async(e) => {
        e.preventDefault();
        setWoredaError('');

        if (woredaForm.password !== woredaForm.confirmPassword && !editingWoreda) {
            setWoredaError('Passwords do not match');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            let url, method;

            if (editingWoreda) {
                url = `http://localhost:5000/api/admins/edit/${editingWoreda.id}`;
                method = 'PUT';
            } else {
                url = 'http://localhost:5000/api/admins/register';
                method = 'POST';
            }

            // Prepare data for submission
            const submitData = {
                fullName: woredaForm.fullName,
                phoneNumber: woredaForm.phoneNumber,
                password: woredaForm.password,
                role: 'Woreda',
                region_name: user.region_name,
                woreda_name: woredaForm.woredaName
            };

            // Don't send password if editing and password is empty
            if (editingWoreda && !submitData.password) {
                delete submitData.password;
            }

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(submitData)
            });

            const data = await response.json();

            if (response.ok) {
                setShowWoredaForm(false);
                setEditingWoreda(null);
                setWoredaForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: '',
                    woredaName: ''
                });
                fetchDashboardData();
                alert(editingWoreda ? 'Woreda admin updated successfully' : 'Woreda admin registered successfully');
            } else {
                setWoredaError(data.message || 'Error processing woreda');
            }
        } catch (error) {
            console.error('Error processing woreda:', error);
            setWoredaError('Server error occurred');
        }
    };

    const deleteWoreda = async(woredaId) => {
        if (!window.confirm('Are you sure you want to delete this woreda admin?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/delete/${woredaId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
                alert('Woreda admin deleted successfully');
            } else {
                const errorData = await response.json();
                alert(errorData.message || 'Error deleting woreda admin');
            }
        } catch (error) {
            console.error('Error deleting woreda:', error);
            alert('Error deleting woreda admin');
        }
    };

    const editWoreda = (woreda) => {
        setEditingWoreda(woreda);
        setWoredaForm({
            fullName: woreda.full_name || '',
            phoneNumber: woreda.phone_number || '',
            password: '',
            confirmPassword: '',
            woredaName: woreda.woreda_name || ''
        });
        setShowWoredaForm(true);
    };

    // Farmer Management Functions
    const handleFarmerSubmit = async(e) => {
        e.preventDefault();
        setFarmerError('');

        try {
            const token = localStorage.getItem('token');
            let url, method;

            if (editingFarmer) {
                url = `http://localhost:5000/api/admins/farmers/${editingFarmer.id}`;
                method = 'PUT';
            } else {
                url = 'http://localhost:5000/api/admins/farmers';
                method = 'POST';
            }

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(farmerForm)
            });

            const data = await response.json();

            if (response.ok) {
                setShowFarmerForm(false);
                setEditingFarmer(null);
                setFarmerForm({
                    fullName: '',
                    phoneNumber: '',
                    location: '',
                    kebele: ''
                });
                fetchDashboardData();
                alert(editingFarmer ? 'Farmer updated successfully' : 'Farmer registered successfully');
            } else {
                setFarmerError(data.message || 'Error processing farmer');
            }
        } catch (error) {
            console.error('Error processing farmer:', error);
            setFarmerError('Server error occurred');
        }
    };

    const deleteFarmer = async(farmerId) => {
        if (!window.confirm('Are you sure you want to delete this farmer?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/farmers/${farmerId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
                alert('Farmer deleted successfully');
            } else {
                const errorData = await response.json();
                alert(errorData.message || 'Error deleting farmer');
            }
        } catch (error) {
            console.error('Error deleting farmer:', error);
            alert('Error deleting farmer');
        }
    };

    const editFarmer = (farmer) => {
        setEditingFarmer(farmer);
        setFarmerForm({
            fullName: farmer.full_name || '',
            phoneNumber: farmer.phone_number || '',
            location: farmer.location || '',
            kebele: farmer.kebele || ''
        });
        setShowFarmerForm(true);
    };

    if (!user || !regionInfo) {
        return <div className = "flex justify-center items-center h-screen dark:bg-gray-900" > Loading... < /div>;
    }

    return ( <
        div className = { `flex h-screen ${darkMode ? 'dark bg-gray-900' : 'bg-gray-100'}` } > { /* Sidebar */ } <
        div className = "w-64 bg-blue-800 dark:bg-gray-800 text-white" >
        <
        div className = "p-4" >
        <
        h2 className = "text-xl font-bold" > Region Dashboard < /h2> <
        div className = "region-info mt-4" >
        <
        h3 className = "text-lg font-semibold" > { regionInfo.name } < /h3> <
        p className = "text-sm" > Region Code: { regionInfo.code } < /p> <
        /div> <
        div className = "mt-4 flex items-center" >
        <
        span className = "mr-2" > Dark Mode < /span> <
        button onClick = {
            () => setDarkMode(!darkMode) }
        className = { `relative inline-flex h-6 w-11 items-center rounded-full ${darkMode ? 'bg-blue-600' : 'bg-gray-200'}` } >
        <
        span className = { `inline-block h-4 w-4 transform rounded-full bg-white transition ${darkMode ? 'translate-x-6' : 'translate-x-1'}` }
        /> <
        /button> <
        /div> <
        /div> <
        nav className = "mt-6" >
        <
        button className = { `w-full text-left px-4 py-2 ${activeTab === 'overview' ? 'bg-blue-700 dark:bg-gray-700' : 'hover:bg-blue-700 dark:hover:bg-gray-700'}` }
        onClick = {
            () => setActiveTab('overview') } >
        📊Overview <
        /button> <
        button className = { `w-full text-left px-4 py-2 ${activeTab === 'requests' ? 'bg-blue-700 dark:bg-gray-700' : 'hover:bg-blue-700 dark:hover:bg-gray-700'}` }
        onClick = {
            () => setActiveTab('requests') } >
        📋Requests <
        /button> <
        button className = { `w-full text-left px-4 py-2 ${activeTab === 'products' ? 'bg-blue-700 dark:bg-gray-700' : 'hover:bg-blue-700 dark:hover:bg-gray-700'}` }
        onClick = {
            () => setActiveTab('products') } >
        📦Products <
        /button> <
        button className = { `w-full text-left px-4 py-2 ${activeTab === 'woredas' ? 'bg-blue-700 dark:bg-gray-700' : 'hover:bg-blue-700 dark:hover:bg-gray-700'}` }
        onClick = {
            () => setActiveTab('woredas') } >
        🏘️Woredas <
        /button> <
        button className = { `w-full text-left px-4 py-2 ${activeTab === 'farmers' ? 'bg-blue-700 dark:bg-gray-700' : 'hover:bg-blue-700 dark:hover:bg-gray-700'}` }
        onClick = {
            () => setActiveTab('farmers') } >
        👨‍🌾Farmers <
        /button> <
        /nav> <
        /div>

        { /* Main Content */ } <
        div className = "flex-1 overflow-auto" >
        <
        header className = "bg-white dark:bg-gray-800 shadow p-4 flex justify-between items-center" >
        <
        div >
        <
        h1 className = "text-xl font-bold dark:text-white" > Welcome, { user.fullName }({ regionInfo.name }
            Admin) < /h1> <
        p className = "text-gray-600 dark:text-gray-300" > Region: { user.region_name } < /p> <
        /div> <
        button onClick = { handleLogout }
        className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded dark:bg-red-600 dark:hover:bg-red-700" >
        Logout <
        /button> <
        /header>

        <
        main className = "p-4" > { /* Overview Tab */ } {
            activeTab === 'overview' && ( <
                div className = "overview-tab" >
                <
                h2 className = "text-2xl font-bold mb-4 dark:text-white" > Region Overview < /h2> <
                div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6" >
                <
                div className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                <
                h3 className = "text-2xl font-bold dark:text-white" > { stats.totalRequests } < /h3> <
                p className = "dark:text-gray-300" > Total Requests < /p> <
                /div> <
                div className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                <
                h3 className = "text-2xl font-bold dark:text-white" > { stats.pendingRequests } < /h3> <
                p className = "dark:text-gray-300" > Pending Requests < /p> <
                /div> <
