                div className = "bg-white dark:bg-gray-800 p-6 rounded-lg w-full max-w-md" >
                <
                h3 className = "text-xl font-bold mb-4 dark:text-white" > { editingWoreda ? 'Edit Woreda Admin' : 'Register Woreda Admin' } <
                /h3> {
                    woredaError && ( <
                        div className = "bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4 dark:bg-red-900 dark:text-red-200 dark:border-red-700" > { woredaError } <
                        /div>
                    )
                } <
                form onSubmit = { handleWoredaSubmit } >
                <
                div className = "mb-4" >
                <
                label className = "block mb-2 font-medium dark:text-white" > Full Name: < /label> <
                input type = "text"
                value = { woredaForm.fullName }
                onChange = {
                    (e) => setWoredaForm({...woredaForm, fullName: e.target.value }) }
                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block mb-2 font-medium dark:text-white" > Phone Number: < /label> <
                input type = "tel"
                value = { woredaForm.phoneNumber }
                onChange = {
                    (e) => setWoredaForm({...woredaForm, phoneNumber: e.target.value }) }
                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block mb-2 font-medium dark:text-white" > Woreda Name: < /label> <
                input type = "text"
                value = { woredaForm.woredaName }
                onChange = {
                    (e) => setWoredaForm({...woredaForm, woredaName: e.target.value }) }
                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block mb-2 font-medium dark:text-white" > { editingWoreda ? 'New Password (leave blank to keep current):' : 'Password:' } <
                /label> <
                input type = "password"
                value = { woredaForm.password }
                onChange = {
                    (e) => setWoredaForm({...woredaForm, password: e.target.value }) }
                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                minLength = { 6 }
                required = {!editingWoreda }
                /> <
                /div> {
                    !editingWoreda && ( <
                        div className = "mb-4" >
                        <
                        label className = "block mb-2 font-medium dark:text-white" > Confirm Password: < /label> <
                        input type = "password"
                        value = { woredaForm.confirmPassword }
                        onChange = {
                            (e) => setWoredaForm({...woredaForm, confirmPassword: e.target.value }) }
                        className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                        minLength = { 6 }
                        required /
                        >
                        <
                        /div>
                    )
                } <
                div className = "flex justify-end space-x-2" >
                <
                button type = "button"
                onClick = {
                    () => {
                        setShowWoredaForm(false);
                        setEditingWoreda(null);
                        setWoredaForm({
                            fullName: '',
                            phoneNumber: '',
                            password: '',
                            confirmPassword: '',
                            woredaName: ''
                        });
                        setWoredaError('');
                    }
                }
                className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded dark:bg-gray-600 dark:hover:bg-gray-700" >
                Cancel <
                /button> <
                button type = "submit"
                className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded dark:bg-blue-600 dark:hover:bg-blue-700" >
                { editingWoreda ? 'Update Admin' : 'Register Admin' } <
                /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        }

        { /* Farmer Form Modal */ } {
            showFarmerForm && ( <
                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" >
                <
                div className = "bg-white dark:bg-gray-800 p-6 rounded-lg w-full max-w-md" >
                <
                h3 className = "text-xl font-bold mb-4 dark:text-white" > { editingFarmer ? 'Edit Farmer' : 'Register Farmer' } <
                /h3> {
                    farmerError && ( <
                        div className = "bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4 dark:bg-red-900 dark:text-red-200 dark:border-red-700" > { farmerError } <
                        /div>
                    )
                } <
                form onSubmit = { handleFarmerSubmit } >
                <
                div className = "mb-4" >
                <
                label className = "block mb-2 font-medium dark:text-white" > Full Name: < /label> <
                input type = "text"
                value = { farmerForm.fullName }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, fullName: e.target.value }) }
                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block mb-2 font-medium dark:text-white" > Phone Number: < /label> <
                input type = "tel"
                value = { farmerForm.phoneNumber }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, phoneNumber: e.target.value }) }
                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block mb-2 font-medium dark:text-white" > Location: < /label> <
                input type = "text"
                value = { farmerForm.location }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, location: e.target.value }) }
                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block mb-2 font-medium dark:text-white" > Kebele: < /label> <
                input type = "text"
                value = { farmerForm.kebele }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, kebele: e.target.value }) }
                className = "w-full p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600"
                required /
                >
                <
                /div> <
                div className = "flex justify-end space-x-2" >
                <
                button type = "button"
                onClick = {
                    () => {
                        setShowFarmerForm(false);
                        setEditingFarmer(null);
                        setFarmerForm({
                            fullName: '',
                            phoneNumber: '',
                            location: '',
                            kebele: ''
                        });
                        setFarmerError('');
                    }
                }
                className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded dark:bg-gray-600 dark:hover:bg-gray-700" >
                Cancel <
                /button> <
                button type = "submit"
                className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded dark:bg-blue-600 dark:hover:bg-blue-700" >
                { editingFarmer ? 'Update Farmer' : 'Register Farmer' } <
                /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        } <
        /div>
    );
};

export default RegionDashboard;
