import React, { useState } from 'react';
import { adminRoles } from '../constants'; // Assuming you have a constants file with adminRoles

const LoginPage = ({ onLogin }) => {
    const [selectedRole, setSelectedRole] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleRoleChange = (e) => {
        setSelectedRole(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onLogin(username, password, selectedRole);
    };

    return ( <
        div className = "flex items-center justify-center min-h-screen bg-gray-100 p-4" >
        <
        div className = "w-full max-w-sm bg-white p-8 rounded-xl shadow-lg border border-gray-200" >
        <
        h2 className = "text-3xl font-bold text-center text-gray-800 mb-6" > Log In < /h2> <
        form onSubmit = { handleSubmit }
        className = "space-y-6" > {!selectedRole ? (
                // Role Selection Form
                <
                div >
                <
                label htmlFor = "role"
                className = "block text-sm font-medium text-gray-700 mb-1" >
                Select Role <
                /label> <
                select id = "role"
                value = { selectedRole }
                onChange = { handleRoleChange }
                className = "w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-150 ease-in-out"
                required >
                <
                option value = ""
                disabled > Choose a role < /option> <
                option value = { adminRoles.FEDERAL } > Federal < /option> <
                option value = { adminRoles.REGIONAL } > Regional < /option> <
                /select> <
                /div>
            ) : (
                // Username and Password Form
                <
                >
                <
                div >
                <
                div className = "flex justify-between items-center mb-2" >
                <
                label htmlFor = "username"
                className = "text-sm font-medium text-gray-700" >
                Username <
                /label> <
                span className = "text-sm text-indigo-600 cursor-pointer hover:underline"
                onClick = {
                    () => setSelectedRole('') } >
                Change Role <
                /span> <
                /div> <
                input type = "text"
                id = "username"
                value = { username }
                onChange = {
                    (e) => setUsername(e.target.value) }
                className = "w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-150 ease-in-out"
                placeholder = "Enter your username"
                required /
                >
                <
                /div> <
                div >
                <
                label htmlFor = "password"
                className = "block text-sm font-medium text-gray-700 mb-1" >
                Password <
                /label> <
                input type = "password"
                id = "password"
                value = { password }
                onChange = {
                    (e) => setPassword(e.target.value) }
                className = "w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-150 ease-in-out"
                placeholder = "Enter your password"
                required /
                >
                <
                /div> <
                button type = "submit"
                className = "w-full bg-indigo-600 text-white py-2 px-4 rounded-lg font-semibold hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 transition duration-150 ease-in-out" >
                Sign In <
                /button> <
                />
            )
        } <
        /form> <
        /div> <
        /div>
    );
};

export default LoginPage;
