import React from 'react';
import { Lightbulb, Moon, Menu } from 'lucide-react';

const Header = ({ user, onLogout, isSidebarOpen, toggleSidebar, isDarkMode, toggleDarkMode }) => {
        return ( <
                header className = "bg-white dark:bg-gray-800 shadow-sm py-4 px-8 flex justify-between items-center sticky top-0 z-50 transition-colors duration-300" >
                <
                div className = "flex items-center space-x-4" > { /* Sidebar toggle button */ } <
                button onClick = { toggleSidebar }
                className = "p-2 rounded-lg text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors duration-200" >
                <
                Menu className = "h-6 w-6" / >
                <
                /button> <
                h1 className = "text-2xl font-bold text-gray-800 dark:text-gray-100" > Product Request Management System < /h1> {
                user && ( <
                    span className = "bg-indigo-100 text-indigo-800 text-xs font-semibold px-2.5 py-0.5 rounded-full dark:bg-indigo-900 dark:text-indigo-200" > { user.role } <
                    /span>
                )
            } <
            /div> {
        user && ( <
            div className = "flex items-center space-x-4" > { /* Dark mode toggle button */ } <
            button onClick = { toggleDarkMode }
            className = "p-2 rounded-lg text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors duration-200" > { isDarkMode ? < Lightbulb className = "h-6 w-6" / > : < Moon className = "h-6 w-6" / > } <
            /button> <
            span className = "text-gray-600 dark:text-gray-300" > Hello, { user.name } < /span> <
            button onClick = { onLogout }
            className = "bg-red-500 text-white px-4 py-2 rounded-lg text-sm font-semibold hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 transition-colors duration-200" >
            Logout <
            /button> < /
            div >
        )
    } <
    /header>
);
};

export default Header;
