                            p className = "text-sm text-gray-600 dark:text-gray-300" >
                            Reported by: { report.reporter_name } | Type: { report.report_type } <
                            /p> < /
                            div > <
                            div className = "flex flex-col items-end" > { getStatusBadge(report.status) } { getPriorityBadge(report.priority) } <
                            /div> < /
                            div > <
                            p className = "text-gray-700 dark:text-gray-300 mb-4" > { report.description } < /p> {
                            report.evidence && ( <
                                p className = "text-sm text-gray-600 dark:text-gray-400 mb-4" >
                                <
                                strong > Evidence: < /strong> {report.evidence} < /
                                p >
                            )
