import React from 'react';

const ApprovalsTable = ({ approvals }) => {
    return ( <
        div className = "approvals-table" >
        <
        table >
        <
        thead >
        <
        tr >
        <
        th > Approval ID < /th> <
        th > Request ID < /th> <
        th > Approver < /th> <
        th > Date Approved < /th> <
        th > Status < /th> <
        th > Comments < /th> <
        /tr> <
        /thead> <
        tbody > {
            approvals.map(approval => ( <
                tr key = { approval.id } >
                <
                td > { approval.id } < /td> <
                td > { approval.requestId } < /td> <
                td > { approval.approver } < /td> <
                td > { approval.dateApproved } < /td> <
                td >
                <
                span className = { `status-badge ${approval.status.toLowerCase()}` } > { approval.status } <
                /span> <
                /td> <
                td > { approval.comments } < /td> <
                /tr>
            ))
        } <
        /tbody> <
        /table> <
        style jsx > { `
        .approvals-table {
          width: 100%;
          overflow-x: auto;
        }
        table {
          width: 100%;
          border-collapse: collapse;
        }
        th, td {
          padding: 12px 15px;
          text-align: left;
          border-bottom: 1px solid #ddd;
        }
        th {
          background-color: #f8f9fa;
          font-weight: 600;
        }
        .status-badge {
          padding: 4px 8px;
          border-radius: 12px;
          font-size: 0.8em;
          font-weight: 500;
        }
        .status-badge.approved {
          background-color: #d4edda;
          color: #155724;
        }
        .status-badge.rejected {
          background-color: #f8d7da;
          color: #721c24;
        }
      ` } < /style> <
        /div>
    );
};

export default ApprovalsTable;
