import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import ProfileTab from './components/ProfileTab';
import { Package, Plus, Edit, Trash2, Eye, Filter, Users, CheckCircle, XCircle, AlertCircle } from 'lucide-react';

const KebeleDashboard = () => {
    const [activeTab, setActiveTab] = useState('requests');
    const [user, setUser] = useState(null);
    const [darkMode, setDarkMode] = useState(false);
    const navigate = useNavigate();

    // State management
    const [requests, setRequests] = useState([]);
    const [farmers, setFarmers] = useState([]);
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Filter states
    const [requestFilter, setRequestFilter] = useState('all');
    const [farmerFilter, setFarmerFilter] = useState('all');

    // Modal states
    const [showProductModal, setShowProductModal] = useState(false);
    const [showRequestModal, setShowRequestModal] = useState(false);
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [editingProduct, setEditingProduct] = useState(null);

    // Form states
    const [productForm, setProductForm] = useState({
        name: '',
        description: '',
        category: '',
        amount: '',
        price: ''
    });

    const categories = [
        'Seeds',
        'Fertilizers',
        'Pesticides',
        'Tools',
        'Machinery',
        'Irrigation',
        'Livestock',
        'Feed',
        'Other'
    ];

    useEffect(() => {
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Kebele' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        fetchDashboardData();
    }, [navigate]);

    const fetchDashboardData = async() => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');

            // Fetch requests
            const requestsResponse = await fetch('http://localhost:5000/api/kebele/requests', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                setRequests(requestsData);
            }

            // Fetch farmers
            const farmersResponse = await fetch('http://localhost:5000/api/kebele/farmers', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            if (farmersResponse.ok) {
                const farmersData = await farmersResponse.json();
                setFarmers(farmersData);
            }

            // Fetch products
            const productsResponse = await fetch('http://localhost:5000/api/kebele/products', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
            setError('Error loading dashboard data');
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        navigate('/login');
    };

    const handleProductSubmit = async(e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');

            if (!productForm.name || !productForm.category || !productForm.amount || !productForm.price) {
                alert('All fields except description are required');
                return;
            }

            const url = editingProduct ?
                `http://localhost:5000/api/kebele/product/${editingProduct.id}` :
                'http://localhost:5000/api/kebele/product';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    name: productForm.name,
                    description: productForm.description,
                    category: productForm.category,
                    amount: parseInt(productForm.amount),
                    price: parseFloat(productForm.price)
                })
            });

            if (response.ok) {
                setShowProductModal(false);
                setEditingProduct(null);
                setProductForm({ name: '', description: '', category: '', amount: '', price: '' });
                fetchDashboardData();
                alert(editingProduct ? 'Product updated successfully' : 'Product added successfully');
            } else {
                const errorData = await response.json();
                alert(errorData.message || 'Error processing product');
            }
        } catch (error) {
            console.error('Error processing product:', error);
            alert('Error processing product');
        }
    };

    const handleRequestAction = async(requestId, action, feedback = '') => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/kebele/request/${requestId}/${action}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ feedback })
            });

            if (response.ok) {
                fetchDashboardData();
                setShowRequestModal(false);
                alert(`Request ${action}ed successfully`);
            } else {
                const errorData = await response.json();
                alert(errorData.message || `Error ${action}ing request`);
            }
        } catch (error) {
            console.error(`Error ${action}ing request:`, error);
            alert(`Error ${action}ing request`);
        }
    };

    const getStatusBadge = (status) => {
        const statusConfig = {
            'Pending': { color: 'bg-yellow-100 text-yellow-800 border-yellow-200', icon: AlertCircle },
            'Accepted': { color: 'bg-green-100 text-green-800 border-green-200', icon: CheckCircle },
            'Rejected': { color: 'bg-red-100 text-red-800 border-red-200', icon: XCircle },
            'Approved': { color: 'bg-blue-100 text-blue-800 border-blue-200', icon: CheckCircle }
        };

        const config = statusConfig[status] || statusConfig['Pending'];
        const Icon = config.icon;
        const formatStatus = (status) => status.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());

        return ( <
            span className = { `inline-flex items-center px-3 py-1 rounded-full text-sm font-medium border ${config.color}` } >
            <
            Icon className = "w-4 h-4 mr-1" / > { formatStatus(status) } <
            /span>
        );
    };

    const filteredRequests = requests.filter(request => {
        if (requestFilter === 'all') return true;
        if (requestFilter === 'pending') return request.kebele_status === 'Pending' || !request.kebele_status;
        if (requestFilter === 'accepted') return request.kebele_status === 'Accepted';
        if (requestFilter === 'approved') return request.kebele_status === 'Approved';
        if (requestFilter === 'rejected') return request.kebele_status === 'Rejected';
        return true;
    });

    const filteredFarmers = farmers.filter(farmer => {
        if (farmerFilter === 'all') return true;
        return farmer.status === farmerFilter;
    });

    if (loading) {
        return ( <
            div className = { `min-h-screen flex items-center justify-center ${
                darkMode ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'
            }` } >
            <
            div className = "text-center" >
            <
            div className = "animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4" > < /div> <
            p > Loading... < /p> <
            /div> <
            /div>
        );
    }

    return ( <
        div className = { `min-h-screen transition-colors duration-200 ${
            darkMode ? 'bg-gray-900' : 'bg-gray-50'
        }` } >
        <
        Sidebar activeTab = { activeTab }
        setActiveTab = { setActiveTab }
        userType = "Kebele"
        user = { user }
        darkMode = { darkMode }
        setDarkMode = { setDarkMode }
        onLogout = { handleLogout }
        />

        { /* Main Content */ } <
        div className = "ml-64 p-8" > {
            activeTab === 'profile' && ( <
                ProfileTab user = { user }
                userType = "Kebele Admin"
                darkMode = { darkMode }
                />
            )
        }

        {
            activeTab === 'requests' && ( <
                div >
                <
                div className = "flex justify-between items-center mb-8" >
                <
                div >
                <
                h1 className = { `text-3xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}` } >
                Farmer Requests <
                /h1> <
                p className = { `mt-2 ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                Review and manage farmer requests <
                /p> <
                /div> <
                div className = "flex items-center gap-4" >
                <
                Filter className = { `w-5 h-5 ${darkMode ? 'text-gray-400' : 'text-gray-600'}` }
                /> <
                select value = { requestFilter }
                onChange = {
                    (e) => setRequestFilter(e.target.value) }
                className = { `px-3 py-2 border rounded-lg ${
                                        darkMode 
                                            ? 'bg-gray-800 border-gray-600 text-white' 
                                            : 'bg-white border-gray-300 text-gray-900'
                                    }` } >
                <
                option value = "all" > All Requests < /option> <
                option value = "pending" > Pending < /option> <
                option value = "accepted" > Accepted < /option> <
                option value = "approved" > Approved < /option> <
                option value = "rejected" > Rejected < /option> <
                /select> <
                /div> <
                /div>

                <
                div className = "grid gap-6" > {
                    filteredRequests.map((request) => ( <
                        div key = { request.id }
                        className = { `rounded-lg shadow-lg p-6 ${
                                    darkMode ? 'bg-gray-800' : 'bg-white'
                                }` } >
                        <
                        div className = "flex justify-between items-start mb-4" >
                        <
                        div >
                        <
                        h3 className = { `text-xl font-semibold ${
                                                darkMode ? 'text-white' : 'text-gray-900'
                                            }` } > { request.product_name } <
                        /h3> <
                        p className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                        Requested by: { request.farmer_name } <
                        /p> <
                        /div> { getStatusBadge(request.kebele_status || 'Pending') } <
                        /div>

                        <
                        div className = "grid grid-cols-2 md:grid-cols-5 gap-4 mb-4" >
                        <
                        div >
                        <
                        p className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                        Category <
                        /p> <
                        p className = { `font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}` } > { request.category } <
                        /p> <
                        /div> <
                        div >
                        <
                        p className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                        Price per unit <
                        /p> <
                        p className = { `font-semibold ${darkMode ? 'text-green-400' : 'text-green-600'}` } >
                        $ { request.price } <
                        /p> <
                        /div> <
                        div >
                        <
                        p className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                        Quantity <
                        /p> <
                        p className = { `font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}` } > { request.quantity }
                        units <
                        /p> <
                        /div> <
                        div >
                        <
                        p className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                        Total Cost <
                        /p> <
                        p className = { `font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}` } >
                        $ {
                            (request.price * request.quantity).toFixed(2) } <
                        /p> <
                        /div> <
                        div >
                        <
                        p className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                        Date <
                        /p> <
                        p className = { `font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}` } > { new Date(request.created_at).toLocaleDateString() } <
                        /p> <
                        /div> <
                        /div>

                        {
                            request.note && ( <
                                div className = "mb-4" >
                                <
                                p className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                                Note <
                                /p> <
                                p className = { `${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > { request.note } <
                                /p> <
                                /div>
                            )
                        }

                        <
                        div className = "flex gap-3" >
                        <
                        button onClick = {
                            () => {
                                setSelectedRequest(request);
                                setShowRequestModal(true);
                            }
                        }
                        className = { `flex items-center px-4 py-2 rounded-lg transition-colors duration-200 ${
                                                darkMode 
                                                    ? 'bg-blue-600 hover:bg-blue-700 text-white' 
                                                    : 'bg-blue-50 hover:bg-blue-100 text-blue-700'
                                            }` } >
                        <
                        Eye className = "w-4 h-4 mr-2" / >
                        Review <
                        /button>

                        {
                            (!request.kebele_status || request.kebele_status === 'Pending') && ( <
                                >
                                <
                                button onClick = {
                                    () => handleRequestAction(request.id, 'accept') }
                                className = "flex items-center px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg transition-colors duration-200" >
                                <
                                CheckCircle className = "w-4 h-4 mr-2" / >
                                Accept <
                                /button> <
                                button onClick = {
                                    () => handleRequestAction(request.id, 'reject') }
                                className = "flex items-center px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg transition-colors duration-200" >
                                <
                                XCircle className = "w-4 h-4 mr-2" / >
                                Reject <
                                /button> <
                                />
                            )
                        } <
                        /div> <
                        /div>
                    ))
                }

                {
                    filteredRequests.length === 0 && ( <
                        div className = { `text-center py-12 ${
                                    darkMode ? 'text-gray-400' : 'text-gray-600'
                                }` } >
                        <
                        Package className = "w-16 h-16 mx-auto mb-4 opacity-50" / >
                        <
                        p > No requests found
                        for the selected filter. < /p> <
                        /div>
                    )
                } <
                /div> <
                /div>
            )
        }

        {
            activeTab === 'products' && ( <
                div >
                <
                div className = "flex justify-between items-center mb-8" >
                <
                div >
                <
                h1 className = { `text-3xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}` } >
                Manage Products <
                /h1> <
                p className = { `mt-2 ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                Add and manage agricultural products <
                /p> <
                /div> <
                button onClick = {
                    () => setShowProductModal(true) }
                className = "bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg flex items-center transition-colors duration-200" >
                <
                Plus className = "w-5 h-5 mr-2" / >
                Add Product <
                /button> <
                /div>

                <
                div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" > {
                    products.map((product) => ( <
                        div key = { product.id }
                        className = { `rounded-lg shadow-lg p-6 ${
                                    darkMode ? 'bg-gray-800' : 'bg-white'
                                }` } >
                        <
                        h3 className = { `text-xl font-semibold mb-2 ${
                                        darkMode ? 'text-white' : 'text-gray-900'
                                    }` } > { product.name } <
                        /h3> <
                        p className = { `mb-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > { product.description } <
                        /p>

                        <
                        div className = "space-y-2 mb-4" >
                        <
                        div className = "flex justify-between" >
                        <
                        span className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                        Category:
                        <
                        /span> <
                        span className = { `font-medium ${darkMode ? 'text-white' : 'text-gray-900'}` } > { product.category } <
                        /span> <
                        /div> <
                        div className = "flex justify-between" >
                        <
                        span className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                        Available:
                        <
                        /span> <
                        span className = { `font-medium ${darkMode ? 'text-white' : 'text-gray-900'}` } > { product.amount }
                        units <
                        /span> <
                        /div> <
                        div className = "flex justify-between" >
                        <
                        span className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                        Price per unit:
                        <
                        /span> <
                        span className = { `font-bold text-lg ${darkMode ? 'text-green-400' : 'text-green-600'}` } >
                        $ { product.price } <
                        /span> <
                        /div> <
                        /div>

                        <
                        div className = "flex gap-2" >
                        <
                        button onClick = {
                            () => {
                                setEditingProduct(product);
                                setProductForm({
                                    name: product.name,
                                    description: product.description,
                                    category: product.category,
                                    amount: product.amount.toString(),
                                    price: product.price.toString()
                                });
                                setShowProductModal(true);
                            }
                        }
                        className = { `flex-1 flex items-center justify-center px-3 py-2 rounded-lg transition-colors duration-200 ${
                                                darkMode 
                                                    ? 'bg-gray-700 hover:bg-gray-600 text-white' 
                                                    : 'bg-gray-100 hover:bg-gray-200 text-gray-700'
                                            }` } >
                        <
                        Edit className = "w-4 h-4 mr-1" / >
                        Edit <
                        /button> <
                        /div> <
                        /div>
                    ))
                } <
                /div> <
                /div>
            )
        }

        {
            activeTab === 'farmers' && ( <
                div >
                <
                div className = "flex justify-between items-center mb-8" >
                <
                div >
                <
                h1 className = { `text-3xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}` } >
                Farmers <
                /h1> <
                p className = { `mt-2 ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } >
                Manage farmer profiles in your kebele <
                /p> <
                /div> <
                div className = "flex items-center gap-4" >
                <
                Filter className = { `w-5 h-5 ${darkMode ? 'text-gray-400' : 'text-gray-600'}` }
                /> <
                select value = { farmerFilter }
                onChange = {
                    (e) => setFarmerFilter(e.target.value) }
                className = { `px-3 py-2 border rounded-lg ${
                                        darkMode 
                                            ? 'bg-gray-800 border-gray-600 text-white' 
                                            : 'bg-white border-gray-300 text-gray-900'
                                    }` } >
                <
                option value = "all" > All Farmers < /option> <
                option value = "active" > Active < /option> <
                option value = "inactive" > Inactive < /option> <
                /select> <
                /div> <
                /div>

                <
                div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" > {
                    filteredFarmers.map((farmer) => ( <
                        div key = { farmer.id }
                        className = { `rounded-lg shadow-lg p-6 ${
                                    darkMode ? 'bg-gray-800' : 'bg-white'
                                }` } >
                        <
                        div className = "flex items-center mb-4" >
                        <
                        div className = { `w-12 h-12 rounded-full flex items-center justify-center ${
                                            darkMode ? 'bg-gray-700' : 'bg-gray-100'
                                        }` } >
                        <
                        Users className = { `w-6 h-6 ${darkMode ? 'text-gray-400' : 'text-gray-600'}` }
                        /> <
                        /div> <
                        div className = "ml-4" >
                        <
                        h3 className = { `font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}` } > { farmer.fullName } <
                        /h3> <
                        p className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } > { farmer.email } <
                        /p> <
                        /div> <
                        /div>

                        <
                        div className = "space-y-2 text-sm" >
                        <
                        div className = "flex justify-between" >
                        <
                        span className = { darkMode ? 'text-gray-400' : 'text-gray-600' } > Phone: < /span> <
                        span className = { darkMode ? 'text-white' : 'text-gray-900' } > { farmer.phone } <
                        /span> <
                        /div> <
                        div className = "flex justify-between" >
                        <
                        span className = { darkMode ? 'text-gray-400' : 'text-gray-600' } > Address: < /span> <
                        span className = { darkMode ? 'text-white' : 'text-gray-900' } > { farmer.address } <
                        /span> <
                        /div> <
                        div className = "flex justify-between" >
                        <
                        span className = { darkMode ? 'text-gray-400' : 'text-gray-600' } > Joined: < /span> <
                        span className = { darkMode ? 'text-white' : 'text-gray-900' } > { new Date(farmer.created_at).toLocaleDateString() } <
                        /span> <
                        /div> <
                        /div> <
                        /div>
                    ))
                }

                {
                    filteredFarmers.length === 0 && ( <
                        div className = { `col-span-full text-center py-12 ${
                                    darkMode ? 'text-gray-400' : 'text-gray-600'
                                }` } >
                        <
                        Users className = "w-16 h-16 mx-auto mb-4 opacity-50" / >
                        <
                        p > No farmers found
                        for the selected filter. < /p> <
                        /div>
                    )
                } <
                /div> <
                /div>
            )
        } <
        /div>

        { /* Product Modal */ } {
            showProductModal && ( <
                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" >
                <
                div className = { `rounded-lg shadow-xl max-w-md w-full mx-4 ${
                        darkMode ? 'bg-gray-800' : 'bg-white'
                    }` } >
                <
                div className = { `px-6 py-4 border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'}` } >
                <
                h3 className = { `text-lg font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}` } > { editingProduct ? 'Edit Product' : 'Add New Product' } <
                /h3> <
                /div>

                <
                form onSubmit = { handleProductSubmit }
                className = "p-6" >
                <
                div className = "mb-4" >
                <
                label className = { `block text-sm font-medium mb-2 ${
                                    darkMode ? 'text-gray-300' : 'text-gray-700'
                                }` } >
                Product Name *
                <
                /label> <
                input type = "text"
                value = { productForm.name }
                onChange = {
                    (e) => setProductForm({...productForm, name: e.target.value }) }
                required className = { `w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white' 
                                            : 'bg-white border-gray-300 text-gray-900'
                                    }` }
                /> <
                /div>

                <
                div className = "mb-4" >
                <
                label className = { `block text-sm font-medium mb-2 ${
                                    darkMode ? 'text-gray-300' : 'text-gray-700'
                                }` } >
                Category *
                <
                /label> <
                select value = { productForm.category }
                onChange = {
                    (e) => setProductForm({...productForm, category: e.target.value }) }
                required className = { `w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white' 
                                            : 'bg-white border-gray-300 text-gray-900'
                                    }` } >
                <
                option value = "" > Select a category < /option> {
                    categories.map((category) => ( <
                        option key = { category }
                        value = { category } > { category } <
                        /option>
                    ))
                } <
                /select> <
                /div>

                <
                div className = "mb-4" >
                <
                label className = { `block text-sm font-medium mb-2 ${
                                    darkMode ? 'text-gray-300' : 'text-gray-700'
                                }` } >
                Description <
                /label> <
                textarea value = { productForm.description }
                onChange = {
                    (e) => setProductForm({...productForm, description: e.target.value }) }
                rows = "3"
                className = { `w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                                            : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500'
                                    }` }
                /> <
                /div>

                <
                div className = "grid grid-cols-2 gap-4 mb-6" >
                <
                div >
                <
                label className = { `block text-sm font-medium mb-2 ${
                                        darkMode ? 'text-gray-300' : 'text-gray-700'
                                    }` } >
                Amount(units) *
                <
                /label> <
                input type = "number"
                min = "0"
                value = { productForm.amount }
                onChange = {
                    (e) => setProductForm({...productForm, amount: e.target.value }) }
                required className = { `w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
                                            darkMode 
                                                ? 'bg-gray-700 border-gray-600 text-white' 
                                                : 'bg-white border-gray-300 text-gray-900'
                                        }` }
                /> <
                /div> <
                div >
                <
                label className = { `block text-sm font-medium mb-2 ${
                                        darkMode ? 'text-gray-300' : 'text-gray-700'
                                    }` } >
                Price per unit($) *
                <
                /label> <
                input type = "number"
                min = "0"
                step = "0.01"
                value = { productForm.price }
                onChange = {
                    (e) => setProductForm({...productForm, price: e.target.value }) }
                required className = { `w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
                                            darkMode 
                                                ? 'bg-gray-700 border-gray-600 text-white' 
                                                : 'bg-white border-gray-300 text-gray-900'
                                        }` }
                /> <
                /div> <
                /div>

                <
                div className = "flex gap-3" >
                <
                button type = "submit"
                className = "flex-1 bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg transition-colors duration-200" >
                { editingProduct ? 'Update Product' : 'Add Product' } <
                /button> <
                button type = "button"
                onClick = {
                    () => {
                        setShowProductModal(false);
                        setEditingProduct(null);
                        setProductForm({ name: '', description: '', category: '', amount: '', price: '' });
                    }
                }
                className = { `flex-1 py-2 px-4 rounded-lg transition-colors duration-200 ${
                                        darkMode 
                                            ? 'bg-gray-700 hover:bg-gray-600 text-white' 
                                            : 'bg-gray-200 hover:bg-gray-300 text-gray-700'
                                    }` } >
                Cancel <
                /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        }

        { /* Request Review Modal */ } {
            showRequestModal && selectedRequest && ( <
                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" >
                <
                div className = { `rounded-lg shadow-xl max-w-2xl w-full mx-4 ${
                        darkMode ? 'bg-gray-800' : 'bg-white'
                    }` } >
                <
                div className = { `px-6 py-4 border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'}` } >
                <
                h3 className = { `text-lg font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}` } >
                Review Request <
                /h3> <
                /div>

                <
                div className = "p-6" >
                <
                div className = { `p-4 rounded-lg mb-6 ${darkMode ? 'bg-gray-700' : 'bg-gray-50'}` } >
                <
                h4 className = { `font-semibold mb-4 ${darkMode ? 'text-white' : 'text-gray-800'}` } >
                Request Details <
                /h4> <
                div className = "grid grid-cols-2 gap-4 text-sm" >
                <
                div >
                <
                span className = { darkMode ? 'text-gray-400' : 'text-gray-600' } > Farmer: < /span> <
                p className = { `font-medium ${darkMode ? 'text-white' : 'text-gray-900'}` } > { selectedRequest.farmer_name } <
                /p> <
                /div> <
                div >
                <
                span className = { darkMode ? 'text-gray-400' : 'text-gray-600' } > Product: < /span> <
                p className = { `font-medium ${darkMode ? 'text-white' : 'text-gray-900'}` } > { selectedRequest.product_name } <
                /p> <
                /div> <
                div >
                <
                span className = { darkMode ? 'text-gray-400' : 'text-gray-600' } > Quantity: < /span> <
                p className = { `font-medium ${darkMode ? 'text-white' : 'text-gray-900'}` } > { selectedRequest.quantity }
                units <
                /p> <
                /div> <
                div >
                <
                span className = { darkMode ? 'text-gray-400' : 'text-gray-600' } > Price per unit: < /span> <
                p className = { `font-medium ${darkMode ? 'text-green-400' : 'text-green-600'}` } >
                $ { selectedRequest.price } <
                /p> <
                /div> <
                /div> {
                    selectedRequest.note && ( <
                        div className = "mt-4" >
                        <
                        span className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } > Note: < /span> <
                        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > { selectedRequest.note } <
                        /p> <
                        /div>
                    )
                } <
                /div>

                <
                div className = "flex gap-3" > {
                    (!selectedRequest.kebele_status || selectedRequest.kebele_status === 'Pending') && ( <
                        >
                        <
                        button onClick = {
                            () => handleRequestAction(selectedRequest.id, 'accept') }
                        className = "flex-1 bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-lg transition-colors duration-200" >
                        Accept Request <
                        /button> <
                        button onClick = {
                            () => handleRequestAction(selectedRequest.id, 'reject') }
                        className = "flex-1 bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded-lg transition-colors duration-200" >
                        Reject Request <
                        /button> <
                        />
                    )
                } <
                button onClick = {
                    () => setShowRequestModal(false) }
                className = { `flex-1 py-2 px-4 rounded-lg transition-colors duration-200 ${
                                        darkMode 
                                            ? 'bg-gray-700 hover:bg-gray-600 text-white' 
                                            : 'bg-gray-200 hover:bg-gray-300 text-gray-700'
                                    }` } >
                Close <
                /button> <
                /div> <
                /div> <
                /div> <
                /div>
            )
        } <
        /div>
    );
};

export default KebeleDashboard;
