        className = { `w-full px-3 py-2 rounded-lg border ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                                            : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500'
                                    }` }
        /> < /
        div >

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Category <
        /label> <
        select value = { productForm.category }
        onChange = {
            (e) => setProductForm({...productForm, category: e.target.value })
        }
        disabled = { modalMode === 'view' }
        className = { `w-full px-3 py-2 rounded-lg border ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white' 
                                            : 'bg-white border-gray-300 text-gray-900'
                                    }` } >
        <
        option value = "" > Select Category < /option> <
        option value = "Seed" > Seed < /option> <
        option value = "Fertilizer" > Fertilizer < /option> <
        option value = "Pesticide" > Pesticide < /option> <
        option value = "Equipment" > Equipment < /option> <
        option value = "Other" > Other < /option> < /
        select > <
        /div>

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Sub Category <
        /label> <
        input type = "text"
        value = { productForm.sub_category }
        onChange = {
            (e) => setProductForm({...productForm, sub_category: e.target.value })
        }
        disabled = { modalMode === 'view' }
        className = { `w-full px-3 py-2 rounded-lg border ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                                            : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500'
                                    }` }
        /> < /
        div >

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Price <
        /label> <
        input type = "number"
        step = "0.01"
        value = { productForm.price }
        onChange = {
            (e) => setProductForm({...productForm, price: e.target.value })
        }
        disabled = { modalMode === 'view' }
        className = { `w-full px-3 py-2 rounded-lg border ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                                            : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500'
                                    }` }
        /> < /
        div >

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Stock Amount <
        /label> <
        input type = "number"
        value = { productForm.amount }
        onChange = {
            (e) => setProductForm({...productForm, amount: e.target.value })
        }
        disabled = { modalMode === 'view' }
        className = { `w-full px-3 py-2 rounded-lg border ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                                            : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500'
                                    }` }
        /> < /
        div >

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Unit <
        /label> <
        input type = "text"
        value = { productForm.unit }
        onChange = {
            (e) => setProductForm({...productForm, unit: e.target.value })
        }
        disabled = { modalMode === 'view' }
        className = { `w-full px-3 py-2 rounded-lg border ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                                            : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500'
                                    }` }
        /> < /
        div >

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Description <
        /label> <
        textarea value = { productForm.description }
        onChange = {
            (e) => setProductForm({...productForm, description: e.target.value })
        }
        disabled = { modalMode === 'view' }
        rows = { 3 }
        className = { `w-full px-3 py-2 rounded-lg border ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                                            : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500'
                                    }` }
        /> < /
        div >

        <
        div className = "flex gap-3 pt-4" > {
            modalMode !== 'view' && ( <
                button onClick = { modalMode === 'create' ? createProduct : updateProduct }
                disabled = { loading }
                className = "flex-1 bg-green-500 hover:bg-green-600 text-white py-2 rounded-lg disabled:opacity-50" > { loading ? 'Processing...' : modalMode === 'create' ? 'Create Product' : 'Update Product' } <
                /button>
            )
        } <
        button onClick = {
            () => setShowProductModal(false)
        }
        className = { `flex-1 py-2 rounded-lg ${
                                        darkMode ? 'bg-gray-600 text-white' : 'bg-gray-200 text-gray-700'
                                    }` } > { modalMode === 'view' ? 'Close' : 'Cancel' } <
        /button> < /
        div > <
        /div> < /
        div > <
        /div>
    )
}

{ /* Report Modal */ } {
    showReportModal && ( <
            div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
            <
            div className = { `w-full max-w-md p-6 rounded-lg shadow-lg ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
            <
            h3 className = { `text-lg font-semibold mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}` } > { modalMode === 'create' ? 'Submit Report' : modalMode === 'edit' ? 'Edit Report' : 'Report Details' } <
            /h3>

            <
            div className = "space-y-4" >
            <
            div >
            <
            label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
            Reported Admin <
            /label> <
            select value = { reportForm.reported_admin_id }
            onChange = {
                (e) => setReportForm({...reportForm, reported_admin_id: e.target.value })
            }
            disabled = { modalMode === 'view' }
            className = { `w-full px-3 py-2 rounded-lg border ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white' 
                                            : 'bg-white border-gray-300 text-gray-900'
                                    }` } >
            <
            option value = "" > Select Admin < /option> {
            admins.filter(admin => admin.id !== parseInt(localStorage.getItem('userId'))).map(admin => ( <
                option key = { admin.id }
                value = { admin.id } > { admin.full_name }({ admin.role }) <
                /option>
            ))
        } <
        /select> < /
    div >

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Report Type <
        /label> <
    select value = { reportForm.report_type }
    onChange = {
        (e) => setReportForm({...reportForm, report_type: e.target.value })
    }
    disabled = { modalMode === 'view' }
    className = { `w-full px-3 py-2 rounded-lg border ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white' 
                                            : 'bg-white border-gray-300 text-gray-900'
                                    }` } >
        <
        option value = "Misconduct" > Misconduct < /option> <
    option value = "Incompetence" > Incompetence < /option> <
    option value = "Abuse of Power" > Abuse of Power < /option> <
    option value = "Corruption" > Corruption < /option> <
    option value = "Other" > Other < /option> < /
    select > <
        /div>

    <
    div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Title <
        /label> <
    input type = "text"
    value = { reportForm.title }
    onChange = {
        (e) => setReportForm({...reportForm, title: e.target.value })
    }
    disabled = { modalMode === 'view' }
    className = { `w-full px-3 py-2 rounded-lg border ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                                            : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500'
                                    }` }
    /> < /
    div >

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Description <
        /label> <
    textarea value = { reportForm.description }
    onChange = {
        (e) => setReportForm({...reportForm, description: e.target.value })
    }
    disabled = { modalMode === 'view' }
    rows = { 4 }
    className = { `w-full px-3 py-2 rounded-lg border ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                                            : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500'
                                    }` }
    /> < /
    div >

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Priority <
        /label> <
    select value = { reportForm.priority }
    onChange = {
        (e) => setReportForm({...reportForm, priority: e.target.value })
    }
    disabled = { modalMode === 'view' }
    className = { `w-full px-3 py-2 rounded-lg border ${
                                        darkMode 
                                            ? 'bg-gray-700 border-gray-600 text-white' 
                                            : 'bg-white border-gray-300 text-gray-900'
                                    }` } >
        <
        option value = "Low" > Low < /option> <
    option value = "Medium" > Medium < /option> <
    option value = "High" > High < /option> <
    option value = "Critical" > Critical < /option> < /
    select > <
        /div>

    <
    div className = "flex gap-3 pt-4" > {
            modalMode !== 'view' && ( <
                button onClick = { createReport }
                disabled = { loading }
                className = "flex-1 bg-red-500 hover:bg-red-600 text-white py-2 rounded-lg disabled:opacity-50" > { loading ? 'Processing...' : 'Submit Report' } <
                /button>
            )
        } <
        button onClick = {
            () => setShowReportModal(false)
        }
    className = { `flex-1 py-2 rounded-lg ${
                                        darkMode ? 'bg-gray-600 text-white' : 'bg-gray-200 text-gray-700'
                                    }` } > { modalMode === 'view' ? 'Close' : 'Cancel' } <
        /button> < /
    div > <
        /div> < /
    div > <
        /div>
)
}

{ /* Farmer Modal (View Only) */ } {
    showFarmerModal && ( <
        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
        <
        div className = { `w-full max-w-2xl p-6 rounded-lg shadow-lg ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
        <
        h3 className = { `text-lg font-semibold mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}` } >
        Farmer Profile Details <
        /h3>

        {
            selectedItem && ( <
                div className = "grid grid-cols-1 md:grid-cols-2 gap-4" >
                <
                div >
                <
                h4 className = { `font-medium mb-2 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Personal Information < /h4> <
                div className = "space-y-2" >
                <
                p > < span className = "font-medium" > Name: < /span> {selectedItem.full_name}</p >
                <
                p > < span className = "font-medium" > Phone: < /span> {selectedItem.phone_number}</p >
                <
                p > < span className = "font-medium" > Region: < /span> {selectedItem.region_name}</p >
                <
                p > < span className = "font-medium" > Zone: < /span> {selectedItem.zone_name}</p >
                <
                p > < span className = "font-medium" > Woreda: < /span> {selectedItem.woreda_name}</p >
                <
                p > < span className = "font-medium" > Kebele: < /span> {selectedItem.kebele_name}</p >
                <
                /div> < /
                div >

                <
                div >
                <
                h4 className = { `font-medium mb-2 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Agricultural Profile < /h4> <
                div className = "space-y-2" > { selectedItem.land_size_hectares && < p > < span className = "font-medium" > Land Size: < /span> {selectedItem.land_size_hectares} ha</p > } { selectedItem.primary_crops && < p > < span className = "font-medium" > Primary Crops: < /span> {selectedItem.primary_crops}</p > } { selectedItem.secondary_crops && < p > < span className = "font-medium" > Secondary Crops: < /span> {selectedItem.secondary_crops}</p > } { selectedItem.land_type && < p > < span className = "font-medium" > Land Type: < /span> {selectedItem.land_type}</p > } { selectedItem.farming_experience && < p > < span className = "font-medium" > Experience: < /span> {selectedItem.farming_experience}</p > } { selectedItem.irrigation_type && < p > < span className = "font-medium" > Irrigation: < /span> {selectedItem.irrigation_type}</p > } { selectedItem.farming_method && < p > < span className = "font-medium" > Farming Method: < /span> {selectedItem.farming_method}</p > } { selectedItem.soil_type && < p > < span className = "font-medium" > Soil Type: < /span> {selectedItem.soil_type}</p > } <
                p > < span className = "font-medium" > Has Livestock: < /span> {selectedItem.has_livestock ? 'Yes' : 'No'}</p > { selectedItem.livestock_types && < p > < span className = "font-medium" > Livestock Types: < /span> {selectedItem.livestock_types}</p > } { selectedItem.annual_income && < p > < span className = "font-medium" > Annual Income: < /span> ${selectedItem.annual_income}</p > } { selectedItem.education_level && < p > < span className = "font-medium" > Education: < /span> {selectedItem.education_level}</p > } <
                /div> < /
                div > <
                /div>
            )
        }

        <
        div className = "flex justify-end pt-6" >
        <
        button onClick = {
            () => setShowFarmerModal(false)
        }
        className = { `px-4 py-2 rounded-lg ${
                                    darkMode ? 'bg-gray-600 text-white' : 'bg-gray-200 text-gray-700'
                                }` } >
        Close <
        /button> < /
        div > <
        /div> < /
        div >
    )
} <
/div>
);
};

export default RegionDashboard;
