// src/data/mockData.js

/**
 * Mock user object to represent the current user.
 */
export const MOCK_USER = {
    id: 'user_1',
    name: 'You',
    profilePic: 'https://placehold.co/40x40/FF5733/FFFFFF?text=U', // Placeholder image with the letter 'U'
};

/**
 * An array of mock chat messages for a conversation.
 * This data is useful for populating a chat interface for development and testing.
 */
export const MOCK_CHAT_MESSAGES = [{
        id: 'msg_1',
        sender: 'Gemini',
        content: 'Hi there! How can I help you today?',
        timestamp: '10:00 AM',
    },
    {
        id: 'msg_2',
        sender: MOCK_USER.name,
        content: 'Can you write a short poem about the ocean?',
        timestamp: '10:01 AM',
    },
    {
        id: 'msg_3',
        sender: 'Gemini',
        content: 'Sure! Here is a poem for you:\n\nA vast expanse of blue and green,\nA world of wonders, yet unseen.\nWaves that crash upon the shore,\nA timeless, deep, and endless roar.',
        timestamp: '10:02 AM',
    },
    {
        id: 'msg_4',
        sender: MOCK_USER.name,
        content: 'That\'s great, thanks!',
        timestamp: '10:03 AM',
    },
];
