import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const FederalDashboard = () => {
    const [activeTab, setActiveTab] = useState('overview');
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    // Product Management State
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [productFilter, setProductFilter] = useState('all'); // all, own, others
    const [showProductForm, setShowProductForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        amount: '',
        price: '',
        description: ''
    });
    const [productError, setProductError] = useState('');

    // Admin Management State
    const [admins, setAdmins] = useState([]);
    const [showAdminForm, setShowAdminForm] = useState(false);
    const [editingAdmin, setEditingAdmin] = useState(null);
    const [adminForm, setAdminForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        role: 'Region',
        region_name: ''
    });
    const [adminError, setAdminError] = useState('');

    // Request Management State
    const [requests, setRequests] = useState([]);
    const [filteredRequests, setFilteredRequests] = useState([]);
    const [statusFilter, setStatusFilter] = useState('all');
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [showRequestModal, setShowRequestModal] = useState(false);
    const [requestStatus, setRequestStatus] = useState('');
    const [decisionReason, setDecisionReason] = useState('');

    // Stats State
    const [stats, setStats] = useState({
        totalRequests: 0,
        pendingRequests: 0,
        approvedRequests: 0,
        rejectedRequests: 0,
        acceptedRequests: 0,
        totalAdmins: 0,
        totalProducts: 0
    });

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Federal' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        fetchDashboardData();
    }, [navigate]);

    useEffect(() => {
        if (products.length > 0) {
            filterProducts();
        }
    }, [productFilter, products]);

    useEffect(() => {
        if (requests.length > 0) {
            filterRequests();
        }
    }, [statusFilter, requests]);

    const filterProducts = () => {
        let filtered = [...products];

        // Filter by product ownership
        if (productFilter === 'own') {
            filtered = filtered.filter(product => product.created_by_admin_id === user.id);
        } else if (productFilter === 'others') {
            filtered = filtered.filter(product => product.created_by_admin_id !== user.id);
        }

        setFilteredProducts(filtered);
    };

    const filterRequests = () => {
        let filtered = [...requests];

        // Filter by status
        if (statusFilter !== 'all') {
            filtered = filtered.filter(req => req.status === statusFilter);
        }

        setFilteredRequests(filtered);
    };

    const fetchDashboardData = async() => {
        try {
            const token = localStorage.getItem('token');

            // Fetch products
            const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
                filterProducts();
                setStats(prev => ({...prev, totalProducts: productsData.length }));
            }

            // Fetch admins in scope
            const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (adminsResponse.ok) {
                const adminsData = await adminsResponse.json();
                // Filter to only show Region admins (Federal admin can only create Region admins)
                const regionAdmins = adminsData.filter(admin => admin.role === 'Region');
                setAdmins(regionAdmins);
                setStats(prev => ({...prev, totalAdmins: regionAdmins.length }));
            }

            // Fetch requests with detailed status
            const requestsResponse = await fetch('http://localhost:5000/api/admins/requests/status', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                setRequests(requestsData);
                filterRequests();

                // Calculate stats from requests data
                const totalRequests = requestsData.length;
                const pendingRequests = requestsData.filter(req => req.status === 'Pending').length;
                const approvedRequests = requestsData.filter(req => req.status === 'Approved').length;
                const acceptedRequests = requestsData.filter(req => req.status === 'Accepted').length;
                const rejectedRequests = requestsData.filter(req => req.status === 'Rejected').length;

                setStats(prev => ({
                    ...prev,
                    totalRequests,
                    pendingRequests,
                    approvedRequests,
                    acceptedRequests,
                    rejectedRequests
                }));
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        navigate('/login');
    };

    // Check if Federal admin can act on this request
    const canFederalAdminAct = (request) => {
        // Federal admin can only act on requests that have been approved by Region admin
        return request.region_status === 'Approved' && request.federal_status === 'Pending';
    };

    // Check if admin can delete this request (only rejected ones)
    const canDeleteRequest = (request) => {
        return request.status === 'Rejected' && request.federal_status === 'Rejected';
    };

    // Update request status at federal level
    const updateRequestStatus = async(requestId, status, reason = '') => {
        try {
            const token = localStorage.getItem('token');

            const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    status: status,
                    feedback: reason
                })
            });

            const responseData = await response.json();

            if (response.ok) {
                setShowRequestModal(false);
                setSelectedRequest(null);
                setRequestStatus('');
                setDecisionReason('');
                fetchDashboardData();
                alert(`Request ${status} successfully`);
            } else {
                alert(responseData.message || 'Error updating request');
            }
        } catch (error) {
            console.error('Error updating request:', error);
            alert('Error updating request');
        }
    };

    const handleStatusUpdate = (request, status) => {
        setSelectedRequest(request);
        setRequestStatus(status);
        setShowRequestModal(true);
    };

    const confirmStatusUpdate = () => {
        if (selectedRequest && requestStatus) {
            updateRequestStatus(selectedRequest.id, requestStatus, decisionReason);
        }
    };

    const handleDeleteRequest = async(requestId) => {
        if (window.confirm('Are you sure you want to delete this request?')) {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/deleterequest/${requestId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                    alert('Request deleted successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error deleting request');
                }
            } catch (error) {
                console.error('Error deleting request:', error);
                alert('Error deleting request');
            }
        }
    };

    const viewRequestDetails = async(requestId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                const requestDetails = await response.json();
                setSelectedRequest(requestDetails);
                setShowRequestModal(true);
            } else {
                alert('Error fetching request details');
            }
        } catch (error) {
            console.error('Error fetching request details:', error);
            alert('Error fetching request details');
        }
    };

    const getStatusBadge = (status) => {
        const statusClasses = {
            'Pending': 'bg-yellow-100 text-yellow-800',
            'Approved': 'bg-blue-100 text-blue-800',
            'Accepted': 'bg-green-100 text-green-800',
            'Rejected': 'bg-red-100 text-red-800'
        };

        return ( <
            span className = { `px-2 py-1 rounded-full text-xs font-medium ${statusClasses[status] || ''}` } > { status } <
            /span>
        );
    };

    const getLevelStatus = (level, request) => {
        const status = request[`${level}_status`];
        const admin = request[`${level}_admin_name`];
        const feedback = request[`${level}_feedback`];
        const date = request[`${level}_approved_at`];

        const formatLevel = level.charAt(0).toUpperCase() + level.slice(1);

        return ( <
            div className = "mb-4 p-3 bg-gray-50 rounded" >
            <
            h4 className = "font-semibold" > { formatLevel }
            Level < /h4> <
            p > < strong > Status: < /strong> {status || 'Pending'}</p > { admin && < p > < strong > Admin: < /strong> {admin}</p > } { feedback && < p > < strong > Feedback: < /strong> {feedback}</p > } { date && < p > < strong > Date: < /strong> {new Date(date).toLocaleString()}</p > } <
            /div>
        );
    };

    // Product Management Functions
    const handleProductSubmit = async(e) => {
        e.preventDefault();
        setProductError('');

        // Validate product amount and price
        if (parseInt(productForm.amount) < 1) {
            setProductError('Product amount must be at least 1');
            return;
        }

        if (parseFloat(productForm.price) <= 0) {
            setProductError('Product price must be greater than 0');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const url = editingProduct ?
                `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                'http://localhost:5000/api/admins/addproduct';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(productForm)
            });

            if (response.ok) {
                setShowProductForm(false);
                setEditingProduct(null);
                setProductForm({ name: '', category: '', amount: '', price: '', description: '' });
                fetchDashboardData();
                alert(editingProduct ? 'Product updated successfully' : 'Product added successfully');
            } else {
                const errorData = await response.json();
                setProductError(errorData.message || 'Error saving product');
            }
        } catch (error) {
            console.error('Error saving product:', error);
            setProductError('Error saving product');
        }
    };

    const deleteProduct = async(productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
                alert('Product deleted successfully');
            } else {
                const errorData = await response.json();
                alert(errorData.message || 'Error deleting product');
            }
        } catch (error) {
            console.error('Error deleting product:', error);
            alert('Error deleting product');
        }
    };

    const editProduct = (product) => {
        // Check if product belongs to current admin
        if (product.created_by_admin_id !== user.id) {
            alert('You can only edit your own products');
            return;
        }

        setEditingProduct(product);
        setProductForm({
            name: product.name || '',
            category: product.category || '',
            amount: product.amount || '',
            price: product.price || '',
            description: product.description || ''
        });
        setShowProductForm(true);
    };

    // Admin Management Functions
    const handleAdminSubmit = async(e) => {
        e.preventDefault();
        setAdminError('');

        // Password validation for new admins
        if (!editingAdmin && adminForm.password !== adminForm.confirmPassword) {
            setAdminError('Passwords do not match');
            return;
        }

        // Password validation for editing admins (if password is provided)
        if (editingAdmin && adminForm.password && adminForm.password !== adminForm.confirmPassword) {
            setAdminError('Passwords do not match');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            let url, method;

            if (editingAdmin) {
                url = `http://localhost:5000/api/admins/edit/${editingAdmin.id}`;
                method = 'PUT';
            } else {
                url = 'http://localhost:5000/api/admins/register';
                method = 'POST';
            }

            // Prepare data for submission (don't send password if editing and password is empty)
            const submitData = {...adminForm };
            if (editingAdmin && !submitData.password) {
                delete submitData.password;
                delete submitData.confirmPassword;
            }

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(submitData)
            });

            const data = await response.json();

            if (response.ok) {
                setShowAdminForm(false);
                setEditingAdmin(null);
                setAdminForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: '',
                    role: 'Region',
                    region_name: ''
                });
                fetchDashboardData();
                alert(editingAdmin ? 'Admin updated successfully' : 'Admin registered successfully');
            } else {
                setAdminError(data.message || 'Error processing admin');
            }
        } catch (error) {
            console.error('Error processing admin:', error);
            setAdminError('Server error occurred');
        }
    };

    const deleteAdmin = async(adminId) => {
        if (!window.confirm('Are you sure you want to delete this admin?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/delete/${adminId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
                alert('Admin deleted successfully');
            } else {
                const errorData = await response.json();
                alert(errorData.message || 'Error deleting admin');
            }
        } catch (error) {
            console.error('Error deleting admin:', error);
            alert('Error deleting admin');
        }
    };

    const editAdmin = (admin) => {
        setEditingAdmin(admin);
        setAdminForm({
            fullName: admin.full_name || '',
            phoneNumber: admin.phone_number || '',
            password: '',
            confirmPassword: '',
            role: admin.role || 'Region',
            region_name: admin.region_name || ''
        });
        setShowAdminForm(true);
    };

    if (!user) {
        return <div className = "flex justify-center items-center h-screen" > Loading... < /div>;
    }

    return ( <
        div className = "flex h-screen bg-gray-100" > { /* Sidebar */ } <
        div className = "w-64 bg-blue-800 text-white" >
        <
        div className = "p-4" >
        <
        h2 className = "text-xl font-bold" > Federal Dashboard < /h2> < /
        div > <
        nav className = "mt-6" >
        <
        button className = { `w-full text-left px-4 py-2 ${activeTab === 'overview' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
        onClick = {
            () => setActiveTab('overview')
        } > 📊Overview <
        /button> <
        button className = { `w-full text-left px-4 py-2 ${activeTab === 'requests' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
        onClick = {
            () => setActiveTab('requests')
        } > 📋Requests <
        /button> <
        button className = { `w-full text-left px-4 py-2 ${activeTab === 'products' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
        onClick = {
            () => setActiveTab('products')
        } > 📦Products <
        /button> <
        button className = { `w-full text-left px-4 py-2 ${activeTab === 'admins' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
        onClick = {
            () => setActiveTab('admins')
        } > 👥Admins <
        /button> < /
        nav > <
        /div>

        { /* Main Content */ } <
        div className = "flex-1 overflow-auto" >
        <
        header className = "bg-white shadow p-4 flex justify-between items-center" >
        <
        div >
        <
        h1 className = "text-xl font-bold" > Welcome, { user.fullName }(Federal Admin) < /h1> < /
        div > <
        button onClick = { handleLogout }
        className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded" >
        Logout <
        /button> < /
        header >

        <
        main className = "p-4" > { /* Overview Tab */ } {
            activeTab === 'overview' && ( <
                div className = "overview-tab" >
                <
                h2 className = "text-2xl font-bold mb-4" > System Overview < /h2> <
                div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6" >
                <
                div className = "bg-white p-4 rounded shadow" >
                <
                h3 className = "text-2xl font-bold" > { stats.totalRequests } < /h3> <
                p > Total Requests < /p> < /
                div > <
                div className = "bg-white p-4 rounded shadow" >
                <
                h3 className = "text-2xl font-bold" > { stats.pendingRequests } < /h3> <
                p > Pending Requests < /p> < /
                div > <
                div className = "bg-white p-4 rounded shadow" >
                <
                h3 className = "text-2xl font-bold" > { stats.approvedRequests } < /h3> <
                p > Approved Requests < /p> < /
                div > <
                div className = "bg-white p-4 rounded shadow" >
                <
                h3 className = "text-2xl font-bold" > { stats.acceptedRequests } < /h3> <
                p > Accepted Requests < /p> < /
                div > <
                div className = "bg-white p-4 rounded shadow" >
                <
                h3 className = "text-2xl font-bold" > { stats.rejectedRequests } < /h3> <
                p > Rejected Requests < /p> < /
                div > <
                div className = "bg-white p-4 rounded shadow" >
                <
                h3 className = "text-2xl font-bold" > { stats.totalAdmins } < /h3> <
                p > Region Admins < /p> < /
                div > <
                div className = "bg-white p-4 rounded shadow" >
                <
                h3 className = "text-2xl font-bold" > { stats.totalProducts } < /h3> <
                p > Products < /p> < /
                div > <
                /div>

                <
                div className = "quick-actions bg-white p-4 rounded shadow" >
                <
                h2 className = "text-xl font-bold mb-4" > Quick Actions < /h2> <
                div className = "grid grid-cols-1 md:grid-cols-2 gap-4" >
                <
                button onClick = {
                    () => setActiveTab('admins')
                }
                className = "bg-blue-500 hover:bg-blue-600 text-white p-4 rounded text-center" > 👥Create Region Admin <
                /button> <
                button onClick = {
                    () => setActiveTab('products')
                }
                className = "bg-green-500 hover:bg-green-600 text-white p-4 rounded text-center" > 📦Add Product <
                /button> <
                button className = "bg-purple-500 hover:bg-purple-600 text-white p-4 rounded text-center" > 📊Generate Reports <
                /button> <
                button className = "bg-gray-500 hover:bg-gray-600 text-white p-4 rounded text-center" > ⚙️System Settings <
                /button> < /
                div > <
                /div> < /
                div >
            )
        }

        { /* Requests Tab */ } {
            activeTab === 'requests' && ( <
                    div className = "requests-tab" >
                    <
                    div className = "mb-4" >
                    <
                    h2 className = "text-2xl font-bold mb-2" > Request Management < /h2> <
                    p className = "text-gray-600" > Manage all requests across the system < /p>

                    <
                    div className = "flex items-center space-x-4 mt-4" >
                    <
                    div className = "flex items-center space-x-2" >
                    <
                    label className = "font-medium" > Filter by Status: < /label> <
                    select value = { statusFilter }
                    onChange = {
                        (e) => setStatusFilter(e.target.value)
                    }
                    className = "border rounded p-1" >
                    <
                    option value = "all" > All Requests < /option> <
                    option value = "Pending" > Pending < /option> <
                    option value = "Approved" > Approved < /option> <
                    option value = "Accepted" > Accepted < /option> <
                    option value = "Rejected" > Rejected < /option> < /
                    select > <
                    /div> < /
                    div > <
                    /div>

                    <
                    div className = "grid grid-cols-1 gap-4" > {
                        filteredRequests.map((request) => ( <
                                div key = { request.id }
                                className = "bg-white p-4 rounded shadow" >
                                <
                                div className = "flex justify-between items-start" >
                                <
                                div className = "flex-1" >
                                <
                                h3 className = "text-lg font-semibold" > { request.product_name } < /h3> <
                                p className = "text-gray-600" > 👨‍🌾Farmer: { request.farmer_name } < /p> <
                                p className = "text-gray-600" > 📍Location: { request.region_name }
                                / {request.zone_name} / { request.woreda_name }
                                / {request.kebele_name} < /
                                p > <
                                p className = "text-gray-600" > 📅Requested: { new Date(request.created_at).toLocaleDateString() } <
                                /p> <
                                p className = "text-gray-600" > 💰Amount: { request.amount } < /p> <
                                p className = "text-gray-600" > 📝Reason: { request.reason } < /p> < /
                                div > <
                                div className = "flex flex-col items-end" >
                                <
                                div className = "mb-2" > { getStatusBadge(request.status) } <
                                /div> <
                                div className = "flex space-x-2" >
                                <
                                button onClick = {
                                    () => viewRequestDetails(request.id)
                                }
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded text-sm" > 👁️View Details <
                                /button> {
                                canFederalAdminAct(request) && ( <
                                    >
                                    <
                                    button onClick = {
                                        () => handleStatusUpdate(request, 'Approved')
                                    }
                                    className = "bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded text-sm" > ✅Approve <
                                    /button> <
                                    button onClick = {
                                        () => handleStatusUpdate(request, 'Rejected')
                                    }
                                    className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded text-sm" > ❌Reject <
                                    /button> < /
                                    >
                                )
                            } {
                                canDeleteRequest(request) && ( <
                                    button onClick = {
                                        () => handleDeleteRequest(request.id)
                                    }
                                    className = "bg-gray-500 hover:bg-gray-600 text-white px-3 py-1 rounded text-sm" > 🗑️Delete <
                                    /button>
                                )
                            } <
                            /div> < /
                            div > <
                            /div> < /
                            div >
                        ))
                } {
                    filteredRequests.length === 0 && ( <
                        p className = "text-gray-600" > No requests found with the selected filters. < /p>
                    )
                } <
                /div> < /
                div >
        )
    }

    { /* Products Tab */ } {
        activeTab === 'products' && ( <
            div className = "products-tab" >
            <
            div className = "flex justify-between items-center mb-4" >
            <
            h2 className = "text-2xl font-bold" > Product Management < /h2> <
            button onClick = {
                () => setShowProductForm(true)
            }
            className = "bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded" >
            Add Product <
            /button> < /
            div >

            { /* Product Filter */ } <
            div className = "mb-4 flex items-center space-x-4" >
            <
            label className = "font-medium" > Filter Products: < /label> <
            select value = { productFilter }
            onChange = {
                (e) => setProductFilter(e.target.value)
            }
            className = "border rounded p-2" >
            <
            option value = "all" > All Products < /option> <
            option value = "own" > My Products < /option> <
            option value = "others" > Other Admins ' Products</option> < /
            select > <
            /div>

            <
            div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                filteredProducts.map((product) => ( <
                    div key = { product.id }
                    className = "bg-white p-4 rounded shadow" >
                    <
                    h3 className = "text-lg font-semibold" > { product.name } < /h3> <
                    p className = "text-gray-600" > { product.description } < /p> <
                    p className = "text-gray-600" > 📦Category: { product.category } < /p> <
                    p className = "text-gray-600" > 💰Amount: { product.amount } < /p> <
                    p className = "text-gray-600" > 💵Price: { product.price } < /p> <
                    p className = "text-gray-600" > 📅Created: { new Date(product.created_at).toLocaleDateString() } <
                    /p> <
                    p className = "text-gray-600" > 👤Added by: { product.created_by_name || 'Unknown' } < /p> <
                    div className = "mt-4 flex space-x-2" > {
                        product.created_by_admin_id === user.id && ( <
                            >
                            <
                            button onClick = {
                                () => editProduct(product)
                            }
                            className = "bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded" > ✏️Edit <
                            /button> <
                            button onClick = {
                                () => deleteProduct(product.id)
                            }
                            className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded" > 🗑️Delete <
                            /button> < /
                            >
                        )
                    } <
                    /div> < /
                    div >
                ))
            } {
                filteredProducts.length === 0 && ( <
                    p className = "text-gray-600" > No products found. < /p>
                )
            } <
            /div> < /
            div >
        )
    }

    { /* Admins Tab */ } {
        activeTab === 'admins' && ( <
            div className = "admins-tab" >
            <
            div className = "flex justify-between items-center mb-4" >
            <
            h2 className = "text-2xl font-bold" > Region Admin Management < /h2> <
            button onClick = {
                () => {
                    setEditingAdmin(null);
                    setAdminForm({
                        fullName: '',
                        phoneNumber: '',
                        password: '',
                        confirmPassword: '',
                        role: 'Region',
                        region_name: ''
                    });
                    setShowAdminForm(true);
                }
            }
            className = "bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded" >
            Register Region Admin <
            /button> < /
            div >

            <
            div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                admins.map((admin) => ( <
                    div key = { admin.id }
                    className = "bg-white p-4 rounded shadow" >
                    <
                    h3 className = "text-lg font-semibold" > { admin.full_name } < /h3> <
                    p className = "text-gray-600" > 📞Phone: { admin.phone_number } < /p> <
                    p className = "text-gray-600" > 📍Region: { admin.region_name } < /p> <
                    p className = "text-gray-600" > 📅Registered: { new Date(admin.created_at).toLocaleDateString() } <
                    /p> <
                    div className = "mt-4 flex space-x-2" >
                    <
                    button onClick = {
                        () => editAdmin(admin)
                    }
                    className = "bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded" > ✏️Edit <
                    /button> <
                    button onClick = {
                        () => deleteAdmin(admin.id)
                    }
                    className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded" > 🗑️Delete <
                    /button> < /
                    div > <
                    /div>
                ))
            } {
                admins.length === 0 && ( <
                    p className = "text-gray-600" > No region admins registered yet. < /p>
                )
            } <
            /div> < /
            div >
        )
    } <
    /main> < /
    div >

        { /* Product Form Modal */ } {
            showProductForm && ( <
                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                <
                div className = "bg-white p-6 rounded shadow-lg w-full max-w-md" >
                <
                h2 className = "text-xl font-bold mb-4" > { editingProduct ? 'Edit Product' : 'Add Product' } < /h2> {
                productError && < div className = "text-red-500 mb-4" > { productError } < /div>} <
                form onSubmit = { handleProductSubmit } >
                <
                div className = "mb-4" >
                <
                label className = "block text-gray-700" > Product Name < /label> <
                input type = "text"
                value = { productForm.name }
                onChange = {
                    (e) => setProductForm({...productForm, name: e.target.value })
                }
                className = "w-full border rounded p-2"
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block text-gray-700" > Category < /label> <
                input type = "text"
                value = { productForm.category }
                onChange = {
                    (e) => setProductForm({...productForm, category: e.target.value })
                }
                className = "w-full border rounded p-2"
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block text-gray-700" > Amount < /label> <
                input type = "number"
                min = "1"
                value = { productForm.amount }
                onChange = {
                    (e) => setProductForm({...productForm, amount: e.target.value })
                }
                className = "w-full border rounded p-2"
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block text-gray-700" > Price < /label> <
                input type = "number"
                step = "0.01"
                min = "0.01"
                value = { productForm.price }
                onChange = {
                    (e) => setProductForm({...productForm, price: e.target.value })
                }
                className = "w-full border rounded p-2"
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block text-gray-700" > Description < /label> <
                textarea value = { productForm.description }
                onChange = {
                    (e) => setProductForm({...productForm, description: e.target.value })
                }
                className = "w-full border rounded p-2"
                rows = "3"
                required /
                >
                <
                /div> <
                div className = "flex justify-end space-x-2" >
                <
                button type = "button"
                onClick = {
                    () => {
                        setShowProductForm(false);
                        setEditingProduct(null);
                        setProductForm({ name: '', category: '', amount: '', price: '', description: '' });
                        setProductError('');
                    }
                }
                className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
                Cancel <
                /button> <
                button type = "submit"
                className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" > { editingProduct ? 'Update' : 'Add' } <
                /button> < /
                div > <
                /form> < /
                div > <
                /div>
            )
        }

    { /* Admin Form Modal */ } {
        showAdminForm && ( <
                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                <
                div className = "bg-white p-6 rounded shadow-lg w-full max-w-md" >
                <
                h2 className = "text-xl font-bold mb-4" > { editingAdmin ? 'Edit Region Admin' : 'Register Region Admin' } < /h2> {
                adminError && < div className = "text-red-500 mb-4" > { adminError } < /div>} <
                form onSubmit = { handleAdminSubmit } >
                <
                div className = "mb-4" >
                <
                label className = "block text-gray-700" > Full Name < /label> <
                input type = "text"
                value = { adminForm.fullName }
                onChange = {
                    (e) => setAdminForm({...adminForm, fullName: e.target.value })
                }
                className = "w-full border rounded p-2"
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block text-gray-700" > Phone Number < /label> <
                input type = "tel"
                value = { adminForm.phoneNumber }
                onChange = {
                    (e) => setAdminForm({...adminForm, phoneNumber: e.target.value })
                }
                className = "w-full border rounded p-2"
                required /
                >
                <
                /div> {!editingAdmin && ( <
                    >
                    <
                    div className = "mb-4" >
                    <
                    label className = "block text-gray-700" > Password < /label> <
                    input type = "password"
                    value = { adminForm.password }
                    onChange = {
                        (e) => setAdminForm({...adminForm, password: e.target.value })
                    }
                    className = "w-full border rounded p-2"
                    required /
                    >
                    <
                    /div> <
                    div className = "mb-4" >
                    <
                    label className = "block text-gray-700" > Confirm Password < /label> <
                    input type = "password"
                    value = { adminForm.confirmPassword }
                    onChange = {
                        (e) => setAdminForm({...adminForm, confirmPassword: e.target.value })
                    }
                    className = "w-full border rounded p-2"
                    required /
                    >
                    <
                    /div> < /
                    >
                )
            } {
                editingAdmin && ( <
                    >
                    <
                    div className = "mb-4" >
                    <
                    label className = "block text-gray-700" > New Password(leave empty to keep current) < /label> <
                    input type = "password"
                    value = { adminForm.password }
                    onChange = {
                        (e) => setAdminForm({...adminForm, password: e.target.value })
                    }
                    className = "w-full border rounded p-2" /
                    >
                    <
                    /div> <
                    div className = "mb-4" >
                    <
                    label className = "block text-gray-700" > Confirm New Password < /label> <
                    input type = "password"
                    value = { adminForm.confirmPassword }
                    onChange = {
                        (e) => setAdminForm({...adminForm, confirmPassword: e.target.value })
                    }
                    className = "w-full border rounded p-2" /
                    >
                    <
                    /div> < /
                    >
                )
            } <
            div className = "mb-4" >
            <
            label className = "block text-gray-700" > Region Name(Must be unique) < /label> <
        input
        type = "text"
        value = { adminForm.region_name }
        onChange = {
            (e) => setAdminForm({...adminForm, region_name: e.target.value })
        }
        className = "w-full border rounded p-2"
        required
            /
            >
            <
            /div> <
        div className = "flex justify-end space-x-2" >
            <
            button
        type = "button"
        onClick = {
            () => {
                setShowAdminForm(false);
                setEditingAdmin(null);
                setAdminForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: '',
                    role: 'Region',
                    region_name: ''
                });
                setAdminError('');
            }
        }
        className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
            Cancel <
            /button> <
        button
        type = "submit"
        className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" > { editingAdmin ? 'Update' : 'Register' } <
            /button> < /
            div > <
            /form> < /
            div > <
            /div>
    )
}

{ /* Request Modal */ } {
    showRequestModal && ( <
        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
        <
        div className = "bg-white p-6 rounded shadow-lg w-full max-w-2xl" >
        <
        h2 className = "text-xl font-bold mb-4" > Request Details < /h2>

        {
            selectedRequest && ( <
                div >
                <
                div className = "mb-4" >
                <
                h3 className = "text-lg font-semibold" > { selectedRequest.product_name } < /h3> <
                p className = "text-gray-600" > 👨‍🌾Farmer: { selectedRequest.farmer_name } < /p> <
                p className = "text-gray-600" > 📍Location: { selectedRequest.region_name }
                / {selectedRequest.zone_name} / { selectedRequest.woreda_name }
                / {selectedRequest.kebele_name} < /
                p > <
                p className = "text-gray-600" > 📅Requested: { new Date(selectedRequest.created_at).toLocaleDateString() } <
                /p> <
                p className = "text-gray-600" > 💰Amount: { selectedRequest.amount } < /p> <
                p className = "text-gray-600" > 📝Reason: { selectedRequest.reason } < /p> < /
                div >

                { /* Approval Workflow */ } <
                div className = "border-t pt-4" >
                <
                h4 className = "font-semibold mb-2" > Approval Workflow < /h4> { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) } { getLevelStatus('federal', selectedRequest) } < /
                div >

                { /* Decision Form (if applicable) */ } {
                    canFederalAdminAct(selectedRequest) && requestStatus && ( <
                        div className = "mt-4" >
                        <
                        h4 className = "font-semibold mb-2" > { requestStatus === 'Approved' ? 'Approve' : 'Reject' }
                        Request <
                        /h4> <
                        div className = "mb-4" >
                        <
                        label className = "block text-gray-700" > Reason / Feedback(Optional) < /label> <
                        textarea value = { decisionReason }
                        onChange = {
                            (e) => setDecisionReason(e.target.value)
                        }
                        className = "w-full border rounded p-2"
                        rows = "3"
                        placeholder = "Enter reason for your decision" /
                        >
                        <
                        /div> <
                        div className = "flex justify-end space-x-2" >
                        <
                        button onClick = {
                            () => {
                                setShowRequestModal(false);
                                setSelectedRequest(null);
                                setRequestStatus('');
                                setDecisionReason('');
                            }
                        }
                        className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
                        Cancel <
                        /button> <
                        button onClick = { confirmStatusUpdate }
                        className = { `${requestStatus === 'Approved' ? 'bg-green-500 hover:bg-green-600' : 'bg-red-500 hover:bg-red-600'} text-white px-4 py-2 rounded` } >
                        Confirm { requestStatus } <
                        /button> < /
                        div > <
                        /div>
                    )
                }

                {
                    !requestStatus && ( <
                        div className = "flex justify-end" >
                        <
                        button onClick = {
                            () => {
                                setShowRequestModal(false);
                                setSelectedRequest(null);
                            }
                        }
                        className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
                        Close <
                        /button> < /
                        div >
                    )
                } <
                /div>
            )
        } <
        /div> < /
        div >
    )
} <
/div>
);
};

export default FederalDashboard;
