import React, { useState } from 'react';
import { api } from './api';

function App() {
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');
    const [currentUser, setCurrentUser] = useState(null);
    const [currentPage, setCurrentPage] = useState('login');

    const handleLogin = async(e) => {
        e.preventDefault();
        try {
            const user = await api.login(phoneNumber, password);
            setCurrentUser(user);
            setCurrentPage('dashboard');
        } catch (err) {
            alert('Login failed: ' + (err.response && err.response.data && err.response.data.message));
        }
    };

    if (currentPage === 'login') {
        return ( <
            div className = "min-h-screen flex items-center justify-center bg-gray-100" >
            <
            form onSubmit = { handleLogin }
            className = "bg-white p-8 rounded shadow-md w-96" >
            <
            h2 className = "text-2xl font-bold mb-4 text-center" > Login < /h2> <
            input type = "text"
            placeholder = "Phone Number"
            value = { phoneNumber }
            onChange = {
                (e) => setPhoneNumber(e.target.value) }
            className = "w-full p-2 border rounded mb-4" /
            >
            <
            input type = "password"
            placeholder = "Password"
            value = { password }
            onChange = {
                (e) => setPassword(e.target.value) }
            className = "w-full p-2 border rounded mb-4" /
            >
            <
            button type = "submit"
            className = "w-full bg-blue-600 text-white p-2 rounded" >
            Login <
            /button> <
            /form> <
            /div>
        );
    }

    if (currentPage === 'dashboard') {
        return ( <
            div className = "p-4" >
            <
            h1 className = "text-xl font-bold" > Welcome, { currentUser.name } < /h1> { /* Render dashboard based on user role */ } <
            p > Your role: { currentUser.role } < /p> <
            /div>
        );
    }

    return null;
}

export default App;
